cptr do_cmd_class_power_aux_ran(int num, bool only_info)
{
	int dir,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int typ;
			if(only_info) return format("");

			if(one_in_(3)) typ = SUMMON_DEMON;
			else if(one_in_(2)) typ = SUMMON_UNDEAD;
			else typ = SUMMON_KWAI;

			if(summon_specific(-1, py, px, plev, typ, (PM_FORCE_PET)))
				msg_format(_("何かが招かれてきた！", "You have summoned someone!"));
			else
				msg_format(_("何も現れなかった・・", "Nobody appeared..."));

			break;
		}
	case 1:
		{
			int rad = 1 + plev / 16;
			int num = (plev+5) / 6;
			int damage = (10 + plev  + chr_adj) / 2 ;
			int flg = (PROJECT_STOP | PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL);

			if(only_info) return format(_("損傷:最大%d*%d", "dam: up to %d*%d"),damage,num);
			msg_format(_("卍の印が高速回転した！", "You rotate a sigil around yourself at high speed!"));
			for(;num>0;num--) project(0,rad,py,px,damage,GF_LITE,flg,-1);

			break;
		}
	case 2:
		{
			int i;
			bool flag = FALSE;
			monster_type *m_ptr;
			u32b mode = PM_EPHEMERA;

			if(only_info) return "";
			if(plev > 29) mode |= PM_HASTE;

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_CHEN) ) flag = TRUE;
			}
			if(flag)
			{
				msg_print(_("橙はすでにこのフロアにいる。", "Chen already is on this floor."));
				return NULL;
			}
			if(summon_named_creature(0, py, px, MON_CHEN, PM_EPHEMERA))
				msg_print(_("橙を呼び出した。", "You summon Chen."));
			else
				msg_print(_("式の召喚に失敗した。", "You failed to summon your shikigami."));

			break;

		}
	case 3:
		{
			int time;
			base = 25;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			set_fast(time,FALSE);
			set_tim_levitation(time,FALSE);
			if(plev > 34) set_tim_speedster(time,FALSE);

		}
		break;
	case 4:
		{
			int power = 50 + plev * 2 + chr_adj * 5;
			if(power < 120) power = 120;

			if(only_info) return format(_str_eff_power,power);
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay())
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			msg_print(_("あなたは敵を支配しようと試みた・・",
                        "You attempt to take control of the enemy..."));
			fire_ball_hide(GF_CHARM,dir,power,0);
			break;
		}
	case 5:
		{
			int x, y;
			int dist = plev / 2;
			if(only_info) return format(_str_eff_area,dist);
			if (!tgt_pt(&x, &y)) return NULL;

			if(!cave[y][x].m_idx || !m_list[cave[y][x].m_idx].ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}

			else if (distance(y, x, py, px) > dist)
			{
				teleport_player(30, 0L);
				msg_print(_("瞬間移動に失敗した。", "You failed to teleport."));
			}
			else
			{
				teleport_player_to(y, x, 0L);
				if(distance(py,px,y,x) > 1)
				{
					msg_print(_("うまく標的の場所に行けなかった。", "You couldn't appear close enough to the target."));
				}
				else
				{
					msg_print(_("あなたは一瞬で標的の隣に現れた！", "You appear next to your target in an instant!"));
					py_attack(y,x,0L);
				}
			}
			break;
		}
	case 6:
		{
			int dice = 5 + p_ptr->lev / 2;
			int sides = 10 + chr_adj / 5;
			if(dice < 20) dice = 20;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if(use_itemcard)
				msg_format(_("カードから大量の札が噴き出して周囲を埋め尽くした!",
                            "Lots of ofuda fly out of the card, spreading everywhere around you!"));
			else
				msg_format(_("大量の札で周囲を埋め尽くした！",
                            "You spread lots of ofuda everywhere!"));
			project_hack2(GF_HOLY_FIRE,dice,sides,0);
			break;
		}
		break;
	case 7:
		{
			int percentage;
			int base = 7;
			int time;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("伝説の超人の力が身に宿った気がする！",
                        "You feel the power of a legendary superhuman reside in your body!"));
			percentage = p_ptr->chp * 100 / p_ptr->mhp;
			time = base + randint1(base);
			set_tim_addstat(A_STR,110,time,FALSE);
			set_tim_addstat(A_DEX,110,time,FALSE);
			set_tim_addstat(A_CON,110,time,FALSE);
			set_fast(time, FALSE);
			set_hero(time,FALSE);
			set_tim_speedster(time, FALSE);
			p_ptr->chp = p_ptr->mhp * percentage / 100;
			p_ptr->redraw |= (PR_HP );
			redraw_stuff();


			break;
		}

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


