/*:::諏訪子用特技*/
cptr do_cmd_class_power_aux_suwako(int num, bool only_info)
{
	int dir,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int damage = 20 + plev + chr_adj;
			int dist = 2 + plev / 10;
			if(only_info) return  format(_str_eff_range_dam,dist,damage);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("透き通った蛙が飛びかかった。", "A transparent frog leaps forth."));
			fire_ball(GF_WATER, dir, damage,1);
			break;
		}
	case 1://洩矢の鉄の輪
		//v1.1.44 ルーチン見直し
		{
			int dam = 10 + plev/2 + chr_adj/2 ;
			int rad = 0;

			if(only_info) return format(_("損傷:%d * 不定", "dam: %d*undef"),dam);

			if (!get_aim_dir(&dir)) return NULL;

			msg_format(_("鉄輪を投擲した！", "You throw an iron ring!"));
			execute_restartable_project(0, dir, 8, 0, 0, dam, GF_ARROW, rad);

		}
		break;
	case 2:
		{
			int dam = p_ptr->chp / 4;
			if(dam<1) dam = 1;
			if(dam > 800) dam=800;
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから濁流が噴き出した！", "Muddy water pours out of the card!"));
			else
				msg_print(_("あなたは水流を吐いた！", "You breathe a stream of water!"));

			fire_ball(GF_WATER, dir, dam, -2);
			break;
		}
	case 3:
		{
			int y,x;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];

			if (!cave_naked_bold(y, x))
			{
				msg_print(_("ここには岩を作れない。", "You can't create the stone here."));
				return NULL;
			}
			msg_print(_("地面から蛙のような岩が出現した！", "A frog-shaped stone rises out from the ground!"));
			cave_set_feat(y, x, f_tag_to_index_in_init("RUBBLE"));

			break;

		}
	case 4:
		{
			int dam = plev + chr_adj/2 ;
			int cnt;

			if(only_info) return format(_("損傷:%d*不定", "dam: %d*undef"),dam);
			msg_format(_("手長足長の力を借りた。", "You borrow the power of Long-Arm and Long-Leg."));
			for(cnt=0;cnt<4;cnt++) fire_random_target(GF_MISSILE,dam,6,0,0);

		}
		break;
	case 5:
		{
			base = 10;
			if(only_info) return format(_str_eff_dur,base);

			if (p_ptr->inside_arena)
			{
				msg_print(_("あなたはすでに夢の中にいる。", "You are already in a dream."));
				return NULL;
			}

			set_stun(0);
			set_cut(0);
			set_poisoned(0);
			set_image(0);
			dispel_player();
			reset_tim_flags();
			set_tim_general(base,FALSE,0,0);
			do_cmd_redraw();

		}
		break;
	case 6:
		{
			int rad = 2 + plev / 24;
			int damage = plev*2  + chr_adj*2 ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("天から多数の水塊が降り注いだ。", "Many globes of water fall from above."));
			cast_meteor(damage, rad, GF_WATER);

			break;
		}
	case 7:
		{
			int base = plev / 2;
			if(base < 10) base = 10;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			set_kabenuke(base + randint1(base), FALSE);

			break;
		}
	case 8:
		{
			int dam = plev * 3 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("大地が黒く染まり、瘴気を噴き出した！",
                        "The earth turns black, and miasma spouts forth!"));
			project_hack2(GF_POLLUTE,0,0,dam);

			break;
		}
	case 9:
		{
			if(only_info) return format("");
			if(summon_named_creature(0, py, px, MON_TATARI, PM_FORCE_PET))
				msg_print(_("祟り神を呼び出した！", "You summon a scourge god!"));
			else
				msg_print(_("祟り神は現れなかった。", "The scourge god didn't appear."));

		}
		break;
	case 10:
		{
			int dam = 5 * (p_ptr->csp+1) + chr_adj * 10;
			dam = dam * (p_ptr->mhp + (p_ptr->mhp - p_ptr->chp) * 2) / p_ptr->mhp;
			if(only_info) return format(_str_eff_dam,dam/2);
			//Hack 画面を黒にするためworld_monsterを使う
			world_monster = -1;
			do_cmd_redraw();
			Term_xtra(TERM_XTRA_DELAY, 1500);
			project(0,1,py,px,dam/5,GF_DEC_ALL, PROJECT_KILL | PROJECT_JUMP | PROJECT_HIDE, -1);
			project(0,1,py,px,dam,GF_DISP_ALL,PROJECT_KILL | PROJECT_JUMP | PROJECT_HIDE,-1);
			world_monster = 0;
			p_ptr->redraw |= (PR_MAP);
			handle_stuff();

			break;
		}

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


