cptr do_cmd_class_power_aux_magic_eater(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			if(only_info) return "";
			gain_magic();
			break;
		}
	case 1:
		{
			int power = 50 + plev * 3;
			if(only_info)return format(_str_eff_power,power);
			if (!recharge(power)) return NULL;
			break;
		}
	case 2:
		{
			int power = (50+plev*2) * (20+adj_general[p_ptr->stat_ind[A_INT]]*3) * (150+randint1(100));
			if(only_info) return "";
			if(!recharge_magic_eater(power)) return NULL;
			break;
		}
	case 3:
		{
			int base = plev*2;
			if(only_info)return format(_("短縮ターン:%d+1d%d", "reduces by %d+1d%d"),base,base);
			if(!do_cmd_recharge_equipment(base+randint1(base))) return NULL;
			break;
		}

	case 4:
		{
			if(only_info) return "";
			if (!do_cmd_magic_eater(FALSE, TRUE)) return NULL;
			break;
		}

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

