//界王拳にtim_general[0,1]を使う
cptr do_cmd_class_power_aux_martial_artist(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int ex_power = (p_ptr->csp > p_ptr->msp)?(p_ptr->csp - p_ptr->msp):0;
	int dir;

	if(p_ptr->tim_general[0]) ex_power += 200;

	switch(num)
	{
	case 0:
		{
			//int max_csp = MAX(p_ptr->msp*3, p_ptr->lev*5+5);
			int max_csp = MIN(p_ptr->msp*4, 255);
			if(only_info) return format(_("最大:%d", "up to %d"),max_csp);

			if (total_friends)
			{
				msg_print(_("今は配下のことに集中していないと。", "You have to stay focused on your followers right now."));
				return NULL;
			}
			if(p_ptr->tim_general[0])
			{
				msg_print(_("気を爆発的に高めた！", "Your Ki greatly rises!"));
				p_ptr->csp += p_ptr->msp;
			}
			else
			{
				msg_print(_("気を高めた！", "Your Ki rises!"));
				p_ptr->csp += p_ptr->msp / 2;
			}
			if (p_ptr->csp >= max_csp)
			{
				p_ptr->csp = max_csp;
				p_ptr->csp_frac = 0;
			}
			p_ptr->redraw |= (PR_MANA);
			p_ptr->update |= (PU_BONUS);

			break;
		}
	case 1:
		{
			int power = plev + ex_power;
			int rad = MIN(10,(3 + power / 20));
			if(only_info) return format("");
			msg_format(_("あなたは突然眩く光った！", "You suddenly emit dazzling light!"));
			lite_area(damroll(2, power / 5), rad);
			if(power >= 50) confuse_monsters(power);
		}
		break;
	case 2:
		{
			int dice = 3 + ex_power / 7;
			int sides = 4 + plev / 8;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			if(dice > 9)
			{
				msg_format(_("気砲を放った！", "You fire a wave of Ki!"));
				fire_beam(GF_FORCE,dir,damroll(dice,sides));
			}
			else
			{
				msg_format(_("気弾を放った！", "You fire a Ki bullet!"));
				fire_bolt(GF_FORCE,dir,damroll(dice,sides));
			}
		}
		break;
	case 3:
		{
			int y,x;
			int rad = plev / 30 + ex_power / 50;
			int damage = 20 + plev + ex_power / 3;

			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			msg_format(_("二重の衝撃を与えた！", "You deliver two severe blows!"));
			project(0,rad,y,x,damage,GF_DISINTEGRATE,PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM,-1);
			break;

		}
	case 4:
		{
			int time = 15 + plev / 2;
			if(only_info) return format(_str_eff_dur,time);

			if(plev > 39)
			{
				set_tim_speedster(time, FALSE);
			}
			else
			{
				msg_format(_("気の力で身体が浮いた！", "You make yourself float with the power of Ki!"));
				set_tim_levitation(time, FALSE);
			}

			break;
		}
	case 5:
		{
			int dist = 2 + ex_power / 60 + plev / 35;
			int damage=(plev + ex_power / 2) * (p_ptr->mhp - p_ptr->chp) / 100;

			if(dist > 7) dist = 7;
			if(p_ptr->hero) damage /= 3;

			if(!damage)
			{
				if(only_info) return  format(_str_eff_dam,damage);
				msg_format(_("気を放とうとしたが気が軽かった。", "You attempt to release your Ki, but it was too light."));

			}
			else if(p_ptr->chp > p_ptr->mhp / 2)
			{
				if(only_info) return  format(_str_eff_range_dam,dist,damage);
				project_length = dist;
				if (!get_aim_dir(&dir)) return NULL;
				msg_format(_("あなたは重い気を放った！", "You release heavy Ki!"));
				fire_beam(GF_FORCE, dir, damage);
			}
			else
			{
				damage *= 4;
				if(only_info) return  format(_("損傷:最大%d", "dam: up to %d"),damage/2);
				msg_format(_("巨大な気が沈み地面に大穴を開けた！", "Your Ki blasts open a large hole in the ground!"));
				project(0,dist,py,px,damage,GF_FORCE,PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM,-1);
			}

			break;
		}
	case 6:
		{
			int rad = 5 + plev / 10 + ex_power / 5;
			if(rad > 50) rad = 50;
			if(only_info) return  format(_str_eff_area,rad);
			msg_format(_("周囲を濃密な念で満たした・・", "You carefully sense your surroundings..."));
			map_area(rad);
			detect_all(rad);
			break;

		}
	case 7: //ドレインタッチ
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage = plev * 2 + ex_power;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("あなたは冷たい手で%sに触れた・・", "You touch %s with your cold hand..."),m_name);
				if(r_ptr->flagsr & RFR_RES_ALL || !monster_living(r_ptr))
				{
					msg_format(_("%sには全く効果がないようだ。", "%s is not affected at all."),m_name);
					break;
				}
				if(r_ptr->flagsr & RFR_RES_NETH)
				{
					msg_format(_("%sはいくらかの耐性を示した。", "%s resists somewhat."),m_name);
					damage /= 2;
				}

				if(m_ptr->hp < damage) damage = m_ptr->hp + 1;

				if(damage>0)
				{
					msg_format(_("%sから体力を吸い取った！", "You drain life force from %s!"),m_name);
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_DISP_ALL,PROJECT_KILL,-1);
					hp_player(damage);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}
	case 8:
		{
			int dam = plev * 3 + ex_power ;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("あなたは激しく回転し始めた！", "You spin intensely!"));
			project_hack2(GF_WINDCUTTER,0,0,dam);

			break;
		}
	case 9:
		{
			int len = plev / 7 + ex_power / 20;
			if(len > 16) len = 16;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}
	case 10://レイ・ガン
		{
			int dam = 50 + plev + ex_power;
			int rad = 1 + plev / 35 + ex_power / 50;
			int dist = MIN(15,5 + ex_power / 20);
			if(rad > 5) rad = 5;
			if(only_info) return format(_str_eff_dam,dam);

			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(is_hurt_holy() > 0)
			{
				msg_print(_("あなたの拳から黒い光が放たれた！", "Black light shoots out of your fist!"));
				fire_rocket(GF_DARK, dir, dam, rad);
			}
			else
			{
				msg_print(_("あなたの拳から眩い光が放たれた！", "Dazzling light shoots out of your fist!"));
				fire_rocket(GF_FORCE, dir, dam, rad);
			}
			break;
		}
	case 11:
		{
			int time = plev / 2;

			if(only_info) return format(_str_eff_dur,time);
			if(p_ptr->chp < 150 && !get_check_strict(_("体力が少なすぎる。本当に使いますか？",
                                                        "You are low on health. Really use this ability?"), CHECK_OKAY_CANCEL)) return NULL;

			msg_print(_("身体の潜在能力を引き出した！", "You draw out your body's potential!"));
			set_tim_addstat(A_STR,105,time,FALSE);
			set_tim_addstat(A_DEX,105,time,FALSE);
			set_fast(time,FALSE);
			take_hit(DAMAGE_USELIFE, 50 + randint1(100), _("八門遁甲の負荷", "backlash from opening the Eight Gates"), -1);

			break;
		}
	case 12: //山吹色の波紋疾走
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage = plev * 4 + ex_power * 2;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				if(r_ptr->flagsr & RFR_RES_ALL)
				{
					msg_format(_("%sには全く効果がないようだ。", "%s is not affected at all."),m_name);
					break;
				}
				if(r_ptr->flags3 & RF3_UNDEAD)
				{
#ifdef JP
					msg_format("「ふるえるぞハート！燃え尽きるほどヒート！刻むぞ血液のビート！」");
#endif
					damage *= 2;
				}
				msg_format(_("%sに波紋エネルギーを叩き込んだ！", "You slam %s with Hamon energy!"),m_name);
				project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_LITE,PROJECT_KILL,-1);

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}
	case 13:
		{
			int heal = p_ptr->csp;
			if(p_ptr->tim_general[0]) heal += 200;
			if(only_info) return format(_str_eff_heal,heal);
			msg_format(_("気を身体に巡らせた・・", "Ki flows through your body..."));
			hp_player(heal);
			set_cut(0);
			break;

		}
	case 14:
		{
			int dice = 10 + ex_power / 3;
			int sides = plev / 3;
			int rad = 1;
			if(p_ptr->tim_general[0]) rad = 2;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("「波ァ――――！」", "'Haa----!'"));
			fire_spark(GF_FORCE,dir,damroll(dice,sides),rad);
		}
		break;
	case 15:
		{
			int rad = 7 + ex_power / 15;
			if(only_info) return format(_str_eff_area,rad);
			msg_print(_("地面に亀裂が入り光が漏れだした・・", "The earth cracks, and light flows out..."));
			destroy_area(py,px,rad,FALSE,FALSE,FALSE);
		}
		break;
	case 16://界王拳　技自体のタイマーと技の再使用禁止タイマーの二つのtim_generalを使う
		{
			int time = p_ptr->csp / 50;
			int percentage = p_ptr->chp * 100 / p_ptr->mhp;
			if(only_info) return format(_str_eff_dur,time);

			msg_print(_("瞬間的に戦闘力を数倍に高めた！",
                        "Your grow several times more powerful in an instant!"));
			set_tim_general(time,FALSE,0,0);
			set_tim_general(100+randint1(200),FALSE,1,0);
			p_ptr->chp = p_ptr->mhp * percentage / 100;
			p_ptr->redraw |= PR_HP;
			p_ptr->update |= PU_BONUS;
			handle_stuff();
		}
		break;


	default:
		if(only_info) return format(_str_unimp);
		//msg_format("ERROR:実装していない特技が呼ばれた num:%d",num);
		return NULL;
	}
	return "";
}



