cptr do_cmd_class_power_aux_yamame(int num, bool only_info)
{
	int dir,damage;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0: //巣を張る
		{
			if(only_info) return format("");

			if(cave_have_flag_bold(py,px,FF_SPIDER_NEST_1))
			{
				msg_print(_("ここには既に巣を張っている。", "You already have woven a web here."));
				return NULL;
			}

			project(0, 0, py, px, 0, GF_MAKE_SPIDER_NEST, PROJECT_GRID, -1);
			if (cave_have_flag_bold(py, px, FF_SPIDER_NEST_1))
			{
				msg_print(_("巣を張った。", "You weave a web."));

				//v1.1.75 処理忘れ追加
				p_ptr->redraw |= (PR_STATE);
				p_ptr->update |= (PU_BONUS);
			}
			else
				msg_print(_("ここには巣を張れない。", "You can't weave a web here."));




			break;
		}
	case 1: //キャプチャーウェブ
		{
			int rad =  1 + (plev-10) / 15;
			if(plev < 25)
			{
				if(only_info) return format("");
				msg_print(_("蜘蛛の糸を網状に広げた。", "You spread spiderwebs around."));
				project(0, rad, py, px, 0, GF_MAKE_SPIDER_NEST, PROJECT_GRID, -1);

			}
			else
			{
				int dam = plev * 2 + chr_adj * 2;
				if(only_info) return format(_str_eff_dam_around,dam/2);
				msg_print(_("光る網を放った！", "You release a glowing net!"));
				project(0, rad, py, px, 0, GF_MAKE_SPIDER_NEST, PROJECT_GRID | PROJECT_HIDE, -1);
				project(0, rad, py, px, dam, GF_INACT, PROJECT_STOP | PROJECT_GRID | PROJECT_KILL, -1);

			}

			break;
		}

	case 2: //原因不明の熱病
		{
			int power = plev * 3;
			if(power < 50) power = 50;
			if(only_info) return format(_str_eff_power,power);
			if(use_itemcard)
				msg_print(_("カードが何かを撒き散らしている...", "Something scatters out of the card..."));
			else
				msg_print(_("あなたは病原菌を撒き散らした。", "You spread pathogens."));
			confuse_monsters(power);
			stun_monsters(power);

			break;
		}
	case 3: //カンダタロープ
	case 7: //v1.1.91 ヴェノムウェブ
		{
			int range;
			int x = 0, y = 0;
			int dam = 0;

			if (num == 3)
			{
				range = plev / 2;
				if (only_info) return format(_("範囲:%d", "rng: %d"), range);
			}
			else
			{
				range = plev / 3;
				dam = plev * 7 + chr_adj * 5;
				if (only_info) return format(_("範囲:%d 損傷:%d", "rng: %d dam: %d"), range,dam);
			}


			if (!tgt_pt(&x, &y)) return NULL;

			if(!cave_have_flag_bold((y), (x), FF_SPIDER_NEST_1))
			{
				msg_print(_("そこには巣がない。", "There's no web there."));
				return NULL;
			}
			else if(distance(py,px,y,x) > range)
			{
				msg_print(_("そこは遠すぎる。", "It's too far away."));
				return NULL;
			}

			if (num == 3)
			{
				if (cave[y][x].m_idx)
				{
					monster_type *m_ptr = &m_list[cave[y][x].m_idx];
					monster_race *r_ptr = &r_info[m_ptr->r_idx];
					char m_name[80];
					monster_desc(m_name, m_ptr, 0);

					if (r_ptr->flags2 & RF2_GIGANTIC)
					{
						msg_format(_("%sは大きすぎて引き寄せられなかった！",
                                    "%s is too large to pull up!"), m_name);
						break;
					}
					else
					{
						msg_format(_("%sに糸を絡め、引っ張り寄せた！",
                                    "You wrap %s in your threads, pulling towards yourself!"), m_name);
						teleport_monster_to(cave[y][x].m_idx, py, px, 100, TELEPORT_PASSIVE);
					}
				}

				else
				{
					msg_format(_("あなたは糸を手繰って飛んだ！", "You fly holding a thread in your hand!"));
					teleport_player_to(y, x, TELEPORT_NONMAGICAL);
				}
			}
			else
			{
				teleport_player_to(y, x, TELEPORT_NONMAGICAL);
				msg_format(_("あなたは%sから急降下して猛毒を撒き散らした！",
                            "You drop down from the %s, splashing venom around!"),(dun_level?_("天井", "ceiling"):_("空", "sky")));
				project(0, 4, y, x, dam, GF_POIS, (PROJECT_JUMP | PROJECT_KILL | PROJECT_GRID), -1);

			}

			break;
		}


	case 4: //フィルドミアズマ
		{
			int dam = plev * 3 + chr_adj * 3;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("あなたは瘴気を撒き散らした。", "You spread miasma."));
			project_hack2(GF_POLLUTE,0,0,dam);

			break;
		}

	case 5: //樺黄小町
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			damage = 10 + plev + chr_adj * 2;
			if(damage < 50) damage = 50;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				if(use_itemcard) msg_format(_("%sに牙を突き立てた！", "%s is stabbed by fangs!"),m_name);
				else msg_format(_("あなたは%sに噛み付いた！", "You bite %s!"),m_name);

				if(r_ptr->flagsr & RFR_RES_ALL)
				{
					msg_format(_("%sには全く効果がないようだ。", "%s is not affected at all."),m_name);
					break;
				}

				if(!(r_ptr->flagsr & RFR_IM_POIS))
				{
					damage *= 3;
					if(!((r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2))
						&& (randint1(randint1(r_ptr->level/7)+3) == 1))
					{
						msg_format(_("%sの全身に毒が回った！", "%s is severely poisoned!"),m_name);
						damage = m_ptr->hp + 1;
					}
					else if(set_monster_timed_status_add(MTIMED2_DEC_ATK, cave[y][x].m_idx, 16+randint1(16)))
						msg_format(_("%sの攻撃力が下がったようだ。", "The attack power of %s is lowered."), m_name);


				}

				if(damage>0)
				{
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_MISSILE,PROJECT_KILL,-1);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}

	case 6: //階段生成
	{
		if (only_info) return format("");

		stair_creation();

		break;
	}

	case 8: //石窟の蜘蛛の巣
		{
			if(only_info) return format("");
			msg_print(_("周囲が光る網に埋め尽くされた！", "Glowing webs spread around you!"));
			project_hack2(GF_MAKE_SPIDER_NEST,0,0,0);

			//v1.1.75 処理忘れ追加
			p_ptr->redraw |= (PR_STATE);
			p_ptr->update |= (PU_BONUS);

			break;
		}


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



