/*:::空専用特技*/
cptr do_cmd_class_power_aux_utsuho(int num, bool only_info)
{
	int dir,dice,sides,damage;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0: //核融合
		{
			if(only_info) return format("");

			if(one_in_(10)) msg_print(_("元気な水素が絶賛融合中だ！", "You're energetically fusing hydrogen!"));
			else if(one_in_(9)) msg_print(_("究極のエネルギーが身体に満ちる！", "Ultimate energy flows through your body!"));
			else msg_print(_("体内で核反応が活発化した！", "Nuclear reactions go off in your body!"));

			p_ptr->csp += p_ptr->msp / 8 + randint1(p_ptr->msp / 8);
			if(p_ptr->csp >= p_ptr->msp)
			{
				p_ptr->csp = p_ptr->msp;
				p_ptr->csp_frac = 0;
			}
			lite_room(py, px);
			p_ptr->redraw |= (PR_MANA);
			p_ptr->window |= (PW_PLAYER);
			break;
		}
	case 1: //レーザー
		{
			int dam = 20 + plev + chr_adj;
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("ビームを撃った。", "You fire a beam."));
			fire_beam(GF_LITE,dir, dam);
			break;
		}

	case 2: //プチフレア
		{
			int range = 5 + plev / 10;
			int dice = 7 + plev / 3;
			int sides = 10 + chr_adj / 6;

			if(only_info) return format(_("射程:%d 損傷:%dd%d", "rng: %d dam: %dd%d"),range,dice,sides);
			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("火球を撃ち出した。", "You launch a fireball."));
			fire_ball(GF_FIRE,dir,damroll(dice,sides),1);

			break;
		}
	case 3: //ハイテンションブレード
		{
			int dam = 30 + plev * 2 + chr_adj * 3;
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("高エネルギー荷電粒子を発生させた！", "You generate high energy charged particles!"));
			fire_beam(GF_PLASMA,dir, dam);
			break;
		}


	case 4: //ロケットダイブ
		{
			int len = 5 + p_ptr->lev / 10;
			damage = plev + adj_general[p_ptr->stat_ind[A_STR]] *5;
			if(only_info) return format(_str_eff_range_dam,len,damage);
			if (!rush_attack3(len,_("あなたは砲弾のように飛んだ！", "You fly forward like a rocket!"),damage)) return NULL;
			break;
		}

	case 5: //メガフレア
		{
			int dice = 15 + plev / 2;
			int sides = 10 + chr_adj / 4;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから巨大な火球が放たれた！", "A huge ball of fire shoots out of the card!"));
			else
				msg_print(_("巨大な火球を放った！", "You shoot a huge fireball!"));
			fire_ball(GF_NUKE,dir,damroll(dice,sides),4);

			break;
		}
	case 6: //核熱バイザー
		{
			int base = 5 + plev / 5 ;
			int v;
			if(only_info) return format(_("期間:%d+1d%dターン", "dur: %d+1d%d"),base,base);
			msg_format(_("エネルギーの障壁を張った！", "You form an energy barrier!"));
			v = base + randint1(base);
			set_oppose_acid(v, FALSE);
			set_oppose_elec(v, FALSE);
			set_oppose_fire(v, FALSE);
			set_oppose_cold(v, FALSE);

			break;
		}


	case 7: //地獄波動砲
		{
			int base = plev*3 + chr_adj * 3;

			if(only_info) return format(_str_eff_dam,base);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("原子分解の球を放った！", "You fire a ball of molecular disruption!"));
			fire_ball(GF_DISINTEGRATE,dir,base,3);

			break;
		}
	case 8: //ニュークリアフュージョン
		{
			int damage = 50 + plev*6 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,damage);
			msg_print(_("閃光と光熱が周囲を蹂躙した！", "Light and heat wash over around you!"));
			project_hack2(GF_NUKE,0,0,damage);
			break;
		}
	case 9: //十凶星
		{
			int rad = 2 + plev / 16;
			int damage = plev*3  + chr_adj*3 ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("天から幾つもの巨大な火球が降り注いだ。", "Huge fireballs rain down from above."));
			cast_meteor(damage, rad, GF_FIRE);

			break;
		}
	case 10: //ヘルズトカマク
		{
			int sides = plev / 5;
			int base = 12;
			if(only_info) return format(_("範囲:%d+1d%d", "rad: %d+1d%d"),base,sides);
			destroy_area(py, px, base + randint1(sides), FALSE,FALSE,FALSE);
			break;
		}

	case 11://フィクストスター
		//v1.1.44 ルーチン見直し
		{

			int dam = plev * 2 + chr_adj * 2;
			int rad = 3;

			if(only_info) return format(_("損傷:%d * 不定", "dam: %d*undef"),dam);

			if (!get_aim_dir(&dir)) return NULL;

			msg_format(_("眩く輝く光球を放った！", "You fire a sphere of dazzling light!"));
			execute_restartable_project(0, dir, 8, 0, 0, dam, GF_LITE, rad);

		}
		break;
	case 12://ギガフレア
		{
			dice = p_ptr->lev;
			sides = 35 + chr_adj / 2;
			if(dice < 30) dice = 30;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("神の火が前方の全てを焼き払った！", "Divine flame scorches everything in front of you!"));
			fire_spark(GF_NUKE,dir, damroll(dice,sides),2);

			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


