cptr do_cmd_class_power_aux_seija(int num, bool only_info)
{
	int dir,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			if(only_info) return "";
			gain_magic();
			break;
		}
	case 1: //魔道具充填
		{
			int power = (50+plev*2) * (10+adj_general[p_ptr->stat_ind[A_INT]]*3) * (100+randint1(200));

			if(only_info) return "";
			if(!recharge_magic_eater(power)) return NULL;

			break;
		}
	case 2:
		{
			if(only_info) return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_HIRARI],p_ptr->magic_num2[SEIJA_ITEM_HIRARI]);
			msg_print(_("この道具は自動発動する", "This tool activates automatically."));
			return NULL;
			break;
		}
	case 3:
		{
			int power = 50 + plev * 4 + chr_adj * 5;
			if(only_info) return format("効力:%d",power);
			msg_format(_("あなたは周囲の敵の視界を狂わせた！", "You disrupt the senses of enemies in your vicinity!"));
			confuse_monsters(power);
			break;
		}
	case 4:
		{
			int x,y;
			int attempt = 500;
			if(only_info)
			{
				if(use_itemcard)
					return "";
				else
					return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_SUKIMA],p_ptr->magic_num2[SEIJA_ITEM_SUKIMA]);
			}

			if (p_ptr->anti_tele)
			{
				msg_format(_("今はスキマによる移動ができない。", "You cannot move through gaps right now."));
				return NULL;
			}


			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];

			if(cave_have_flag_bold(y,x,FF_MOVE) || cave_have_flag_bold(y,x,FF_CAN_FLY))
			{
				msg_format(_("そこには壁がない。", "There's no wall there."));
				return NULL;
			}
			while(1)
			{
				attempt--;
				y += ddy[dir];
				x += ddx[dir];

				if(y >= cur_hgt) y = 1;
				if(y < 1) y = cur_hgt - 1;
				if(x >= cur_wid) x = 1;
				if(x < 1) x = cur_wid - 1;

				if(attempt<0)
				{
					msg_format(_("スキマ移動に失敗した！", "You failed to move through the gap!"));
					teleport_player(200,0L);
					break;
				}
				if(!cave_player_teleportable_bold(y, x, 0L)) continue;
				msg_format(_("あなたはスキマを開いて反対側に抜けた！", "You open the gap and emerge on the opposite side!"));
				teleport_player_to(y,x,0L);
				break;
			}

			if(!use_itemcard) p_ptr->magic_num1[SEIJA_ITEM_SUKIMA] -= 1;

			break;
		}
	case 5:
		{
			if(only_info) return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_DECOY],p_ptr->magic_num2[SEIJA_ITEM_DECOY]);
			if (!(p_ptr->special_defense & NINJA_KAWARIMI))
			{
				msg_print(_("あなたはこっそりと仕込みを済ませた・・", "You finish your secret preparations..."));

				p_ptr->special_defense |= NINJA_KAWARIMI;
				p_ptr->redraw |= (PR_STATUS);
				p_ptr->magic_num1[SEIJA_ITEM_DECOY] -= 1;
			}
			else
			{
				msg_print(_("既にデコイ人形の準備は済んでいる。", "You already have prepared a decoy doll."));
				return NULL;
			}
			break;
		}
	case 6:
		{
			int rad = 3 + plev / 15;
			base = 100 + plev * 3;
			if(only_info) return format(_("使用回数:%d/%d 損傷:%d", "uses: %d/%d dam: %d"),p_ptr->magic_num1[SEIJA_ITEM_BOMB],p_ptr->magic_num2[SEIJA_ITEM_BOMB],base);

			project_length = 4;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("あなたは爆弾に点火し、投げつけた！", "You light a bomb and throw it!"));
			fire_ball(GF_DISINTEGRATE,dir,base,rad);
			p_ptr->magic_num1[SEIJA_ITEM_BOMB] -= 1;

			break;
		}
	case 7:
		{
			bool result;
			if(only_info) return "";

			/* HACK -- No range limit */
			project_length = -1;
			result = get_aim_dir(&dir);
			project_length = 0;
			if (!result) return NULL;
			teleport_swap(dir);
			break;
		}
	case 8:
		{
			if(only_info) return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_CAMERA],p_ptr->magic_num2[SEIJA_ITEM_CAMERA]);
			msg_print(_("この道具は自動発動する", "This tool activates automatically."));
			return NULL;
			break;
		}
	case 9:
		{
			if(only_info) return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_ONMYOU],p_ptr->magic_num2[SEIJA_ITEM_ONMYOU]);
			//if (!tgt_pt(&x, &y)) return NULL;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			if (p_ptr->anti_tele)
			{
				msg_print(_("不思議な力がテレポートを防いだ！", "A mysterious force prevents you from teleporting!"));
			}
			else
			{
				msg_print(_("あなたは離れた場所に一瞬で現れた。", "You instantly appear in a distant location."));
				teleport_player_to(target_row, target_col, 0L);
			}
			p_ptr->magic_num1[SEIJA_ITEM_ONMYOU] -= 1;
			break;
		}
	case 10://小槌レプリカ
		{
			int y, x;
			monster_type *m_ptr;
			base = plev * 6;
			sides = plev * 6;

			if(base < 150) base = 150;
			if(sides < 150) sides = 150;

			if(only_info)
			{
				if(use_itemcard)
					return format(_("損傷:1d%d + %d", "dam: 1d%d + %d"),base,sides);
				else
					return format(_("残り回数:%d/%d 損傷:1d%d + %d", "uses: %d/%d dam: 1d%d+%d"),p_ptr->magic_num1[SEIJA_ITEM_KODUCHI],(int)p_ptr->magic_num2[SEIJA_ITEM_KODUCHI],base,sides);

			}

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];
			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				int flg = PROJECT_STOP | PROJECT_KILL | PROJECT_JUMP;
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("あなたは小槌を振りかぶり、%sに向けて叩き付けた！",
                            "You swing your mallet and slam it into %s!"),m_name);
				project(0, 0, y, x, randint1(sides) + base, GF_DISP_ALL, flg, -1);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			if(!use_itemcard) p_ptr->magic_num1[SEIJA_ITEM_KODUCHI] -= 1;

			break;
		}
	case 11:
		{
			if(only_info) return format(_("残り回数:%d/%d", "uses: %d/%d"),p_ptr->magic_num1[SEIJA_ITEM_JIZO],p_ptr->magic_num2[SEIJA_ITEM_JIZO]);
			msg_print(_("この道具は自動発動する", "This tool activates automatically."));
			return NULL;
			break;
		}
	case 12:
		{
			base = 15;
			if(only_info) return format(_("残り回数:%d/%d 期間:%d + 1d%d", "uses: %d/%d dur: %d+1d%d"),p_ptr->magic_num1[SEIJA_ITEM_CHOCHIN],p_ptr->magic_num2[SEIJA_ITEM_CHOCHIN],base,base);
			set_wraith_form(randint1(base) + base, FALSE);
			p_ptr->magic_num1[SEIJA_ITEM_CHOCHIN] -= 1;
			break;
		}
	case 13:
		{
			int dam = plev * 5 + chr_adj * 5;

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("あなたは天地をひっくり返した！", "You turn heaven and earth around!"));
			project_hack2(GF_GRAVITY,0,0,dam);
			break;
		}
	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


