cptr do_cmd_class_power_aux_nue(int num, bool only_info)
{
	int dir,dice,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			if(only_info) return "";
			msg_format(_("あなたの身体から黒い霧が湧き出した。", "Black mist flows out of your body."));
			unlite_room(py,px);
			break;
		}
	case 1:
		{
			int rad = 2 + plev / 24;
			int damage = 10 + plev  + chr_adj / 2 ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("あなたの周りを火球が飛び回った。", "Balls of fire start flying around you."));
			cast_meteor(damage, rad, GF_FIRE);

			break;
		}

	case 2://正体不明の種
		{
			int y, x;
			monster_type *m_ptr;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

				if (cave[y][x].m_idx && (m_ptr->ml) )
				{
					char m_name[80];
					int chance = 40 + plev * 2 + chr_adj * 2;
					monster_race *r_ptr = &r_info[m_ptr->r_idx];
					monster_desc(m_name, m_ptr, 0);

					if(use_itemcard && chance < 100) chance=100;

					if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) chance /= 2;

					if(r_ptr->flagsr & (RFR_RES_CHAO | RFR_RES_ALL))
					{
						msg_format(_("%sには効果がない！", "%s is unaffected!"),m_name);
					}
					else if(m_ptr->mflag & MFLAG_NUE_UNKNOWN)
					{
						msg_format(_("既に効いている。", "This ability already is in effect."));

					}
					else if( randint1(chance) < r_ptr->level)
					{
						msg_format(_("%sに正体不明の種を仕込むのに失敗した！", "You fail to implant seed of non-identification into %s!"),m_name);
					}
					else
					{

						msg_format(_("%sに正体不明の種を仕込んだ！", "You implanted seed of non-identification into %s!"),m_name);
						m_ptr->mflag |= MFLAG_NUE_UNKNOWN;
						lite_spot(m_ptr->fy,m_ptr->fx);

					}
				}
				else
				{
					msg_format(_str_mess_nobody_here);
					return NULL;
				}

			break;
		}
	case 3://原理不明の妖怪玉
		{
			int rad = 2 + plev / 15;
			dice = 8 + p_ptr->lev / 3;
			sides = 12 + chr_adj / 2 ;
			base = p_ptr->lev*4;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_PLASMA, dir, base + damroll(dice,sides), rad,_("光球の群れが襲いかかった！",
                                                                                "A swarm of spheres of light strikes!"))) return NULL;
			break;
		}
	case 4:
		{
			int dam = plev * 3 + chr_adj * 5;

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("辺りは濃密な闇に包まれた・・", "Thick darkness covers area around you..."));
			project_hack2(GF_DARK,0,0,dam);
			break;
		}
	case 5:
		{
			dice = base = 8;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);
			set_multishadow(base + randint1(dice),FALSE);
			break;
		}
	case 6:
		{
			int base = plev * 5 + chr_adj * 5;

			if(base < 300) base=300;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,base);
			msg_format(_("あなたは異界の正体不明のパワーを呼び出した！",
                        "You call forth unidentified otherworldly power!"));
			project_hack2(0,1,base,base);
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


