cptr do_cmd_class_power_aux_byakuren(int num, bool only_info)
{
	int dir,dice,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int wis_adj = adj_general[p_ptr->stat_ind[A_WIS]];

	switch(num)
	{

	case 0:
		{
			int time = 10 + plev / 5;
			int dec_hp = p_ptr->mhp - p_ptr->chp;
			if(only_info) return format(_str_eff_dur,time);

			if(one_in_(3))msg_print(_("光る文字が空中を高速で流れた。", "Glowing characters fly through the air at high speed."));
			else if(one_in_(2))msg_print(_("エア巻物を起動させた。", "You activate the Air Scroll."));
			else msg_print(_("「いざ、南無三ー！」", "'Now, namusan-!'"));
			if(plev > 29) set_fast(time,FALSE);
			if(plev > 34)
			{
				set_oppose_acid(time, FALSE);
				set_oppose_elec(time, FALSE);
				set_oppose_fire(time, FALSE);
				set_oppose_cold(time, FALSE);
				set_oppose_pois(time, FALSE);
			}
			if(!use_itemcard) set_tim_general(time,FALSE,0,0);
			set_tim_addstat(A_STR,105+plev/10,time,FALSE);
			set_tim_addstat(A_DEX,105+plev/10,time,FALSE);
			set_tim_addstat(A_CON,105+plev/10,time,FALSE);
			p_ptr->chp = p_ptr->mhp - dec_hp;
			p_ptr->redraw |= PR_HP;
			handle_stuff();
			break;
		}

	case 1:
		{
			int rad = 2;
			if(plev > 39) rad = 3;
			dice = 2 + plev / 7;
			sides = 10 + chr_adj / 6;
			base = 20 + plev + chr_adj ;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_ELEC, dir, base + damroll(dice,sides), rad,_("雷が落ちた！", "Lightning strikes!"))) return NULL;
			break;

		}
	case 2:
		{
			if(only_info) return format("");
			msg_print(_("精神を研ぎ澄ませた・・", "You sharpen your mind..."));
			p_ptr->csp += (3 + plev/15);
			if (p_ptr->csp >= p_ptr->msp)
			{
				p_ptr->csp = p_ptr->msp;
				p_ptr->csp_frac = 0;
			}
			p_ptr->redraw |= (PR_MANA);
		}
		break;
	case 3:
		{
			int power = plev * 2;
			if(power < 30) power=30;
			if(only_info) return format(_str_eff_power,power);
			msg_print(_("光が降り注ぎ、周囲の者の動きを縫い止めた。",
                        "Light pours down, restraining movement of those around you."));
			stun_monsters(power);
			stasis_monsters(power);
			break;

		}
	case 4:
		{
			int len = 5 + p_ptr->lev / 7;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}
	case 5:
		{
			int base = 20 + plev + chr_adj;
			int num = 3 + plev / 15 + chr_adj / 20;
			int rad = 2;
			int i;
			if(only_info) return format(_("損傷:最大%d * %d", "dam: up to %d*%d"),base/2,num);
			msg_print(_("あなたの周りに光線が渦を巻いた！", "Rays of light swirl around you!"));
			for (i = 0; i < num; i++)
			{
				project(0, rad, py, px, base, GF_LITE, PROJECT_KILL, -1);
				rad = MIN(7,rad+1);
			}
			break;
		}
	case 6:
		{
			int time = 20;
			if(only_info) return format(_str_eff_dur,time);
			msg_format(_("あなたの周りに無数の護符が浮かんだ。", "Countless protective charms start floating around you."));
			set_dustrobe(time,FALSE);
		}
		break;
	case 7:
		{
			int range = plev;
			if(only_info) return format(_str_eff_area,range);
			map_area(plev);
			detect_all(plev);

		}
		break;
	case 8:
		{
			int damage = p_ptr->lev * 6 + chr_adj * 5 + wis_adj * 5;
			if(damage < 450) damage = 450;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("調伏の光が前方を焼き払った！", "Intense light scorches everything in front of you!"));
			fire_spark(GF_LITE,dir,damage,2);

			break;
		}
	case 9:
		{
			int time = 5;
			if(only_info) return format(_str_eff_dur,time);
			msg_print(_("強化の魔法を極限まで高めた！", "You raise the potency of your physical enhancement magic to its limit!"));
			if(p_ptr->tim_general[0] < time) p_ptr->tim_general[0] = time;
			set_lightspeed(time, FALSE);
			redraw_stuff();
		}
		break;


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


