//にとり特技の必要水妖エネルギー　添字がclass_power_nitoriと一致し、エネルギー(p_ptr->magic_num1[20])がこの値未満の技は使えない
int nitori_energy[] =
{
	0,0,0,0,0,0,
	1,0,50,30,200,
	0,300,400,0

};

cptr do_cmd_class_power_aux_nitori(int skillnum, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(skillnum)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!put_item_into_inven2()) return NULL;
			break;

		}
	case 1:
		{
			if(only_info) return format("");
			(void) use_machine(0);
			return NULL; //見るだけなので行動順消費なし

		}
	case 2:
		{
			if(only_info) return format("");
			if(!takeout_inven2()) return NULL;
			break;
		}
	case 3:
		{
			if(only_info) return format("");
			if( !use_machine(2)) return NULL;

			break;
		}
	case 4: //弾薬補充
		{
			if(only_info) return format("");
			if( !use_machine(1)) return NULL;
			break;
		}
	case 5:
		{
			if(only_info) return format(_("現在エネルギー:%d", "Cur: %d"),p_ptr->magic_num1[20]);
			if(p_ptr->magic_num1[20] == NITORI_ENERGY_MAX)
			{
				msg_print(_("既に十分に水を入れている。", "You already have enough water."));
				return NULL;
			}
			msg_print(_("あなたはリュックに水を汲めるだけ汲んだ！", "You put as much water in your rucksack as you can!"));
			p_ptr->magic_num1[20] = NITORI_ENERGY_MAX;
			break;
		}
	case 6:
		{
			int num = 2 + plev / 5;
			int dice = 1;
			int sides = 3 + plev / 8 + chr_adj / 10;
			if(!use_itemcard && p_ptr->magic_num1[20] < num) num = p_ptr->magic_num1[20];

			if(only_info) return format(_str_eff_dam_dice_sides_mult,dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("水鉄砲を乱射した！", "You start firing your water gun!"));
			fire_blast(GF_WATER,dir,dice,sides,num,3,( PROJECT_STOP | PROJECT_KILL | PROJECT_REFLECTABLE | PROJECT_GRID));
			if(p_ptr->pseikaku == SEIKAKU_TRIGGER) new_class_power_change_energy_need = 80;
			if(!use_itemcard) p_ptr->magic_num1[20] -= num;

			break;
		}
	case 7:
		{
			int base = 50 + plev * 4 + chr_adj * 3;
			int rad = 4 + plev / 12;

			if(only_info) return format(_("損傷:最大%d", "dam: up to %d"),base/2);
			msg_print(_("突然水が盛り上がり噴き出した！", "Water suddenly spouts out!"));

			project(0, rad, py, px, base, GF_WATER, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
		}
		break;
	case 8:
		{
			int time = 20;
			if(only_info) return format(_str_eff_dur,time);
			msg_format(_("リュックからギアが飛び出し回転を始めた。", "Gears fly out of your rucksack and start rotating around you."));
			set_dustrobe(time,FALSE);
			p_ptr->magic_num1[20] -= nitori_energy[skillnum];
		}
		break;
	case 9:
		{
			int sides = plev*2;

			if(only_info) return format("損傷:1d%d",sides);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("リュックの横からスピーカーが飛び出し、音波を放った！",
                        "Speakers fly out of your rucksack and blast a sound wave!"));
			fire_beam(GF_SOUND,dir,randint1(sides));
			p_ptr->magic_num1[20] -= nitori_energy[skillnum];
			if(p_ptr->pseikaku == SEIKAKU_TRIGGER) new_class_power_change_energy_need = 80;
		}
		break;
	case 10:
		{
			int range = 5;
			int dam = 50 + plev * 3;
			int tx, ty;
			int flg = PROJECT_STOP | PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL | PROJECT_COMPRESS;

			if(dam < 120) dam = 120;
			if(only_info) return format(_str_eff_range_dam,range,dam);
			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("外部圧縮機から高圧の水が噴射された！",
                        "Water under high pressure is shot out of external compressor!"));
			tx = px + 99 * ddx[dir];
			ty = py + 99 * ddy[dir];
			if ((dir == 5) && target_okay())
			{
				flg &= ~(PROJECT_STOP);
				tx = target_col;
				ty = target_row;
			}
			project(0, 5, ty, tx, dam, GF_WATER, flg, -1);
			if(!use_itemcard) p_ptr->magic_num1[20] -= nitori_energy[skillnum];
			if(p_ptr->pseikaku == SEIKAKU_TRIGGER) new_class_power_change_energy_need = 80;

			break;
		}
	case 11:
		{
			int dam = 20 + plev + chr_adj;
			if(only_info) return format(_("損傷:%d*不定", "dam: %d*undef"),dam);
			msg_print(_("多量の水塊が滝のように降り注いだ！",
                        "Many globs of water fall down like a waterfall!"));
			cast_meteor(dam, 3,GF_WATER);
		}
		break;
	case 12:
		{
			int base = 20;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("光学迷彩を起動した！", "You activate optical camouflage!"));
			set_tim_superstealth(base + randint1(base),FALSE, SUPERSTEALTH_TYPE_OPTICAL);
			if(!use_itemcard) p_ptr->magic_num1[20] -= nitori_energy[skillnum];
			break;
		}
	case 13:
		{
			int num = 8;
			int dice = 3;
			int sides = plev/2;
			if(only_info) return format(_str_eff_dam_dice_sides_mult,dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("激しく放水した！", "You launch intense bursts of water!"));
			for (i = 0; i < num; i++) fire_rocket(GF_WATER, dir, damroll(dice, sides),3);
			p_ptr->magic_num1[20] -= nitori_energy[skillnum];
			if(p_ptr->pseikaku == SEIKAKU_TRIGGER) new_class_power_change_energy_need = 80;
			break;

		}


	case 14:
		{
			int damage = plev * 5 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,damage);
			msg_format(_("辺りは一瞬にして深い水の底になった。",
                        "Your surroundings instantly become deeply flooded with water."));
			project_hack2(GF_WATER_FLOW,0,0,2);
			project_hack2(GF_WATER,0,0,damage);
			p_ptr->update |= (PU_BONUS);
			handle_stuff();

		}
		break;
	}

	return "";
}


