/*:::影狼特技*/
cptr do_cmd_class_power_aux_kagerou(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//狼変身
	case 0:
		{
			int time = 0;
			if(only_info) return format(_("期間:夜明けまで", "dur: until dawn"));
			time = ((turn / (TURNS_PER_TICK * TOWN_DAWN) + 1) * (TURNS_PER_TICK * TOWN_DAWN) - turn) / TURNS_PER_TICK;
			if(time < 5)
			{
				msg_print(_("変身できない。夜明けが近いようだ。", "You can't transform. It's going to be dawn soon."));
				return NULL;
			}

			if(!set_mimic(time, MIMIC_BEAST, FALSE)) return NULL;

		}
		break;

	case 1: //フルムーンロア
		{
			if(FULL_MOON)
			{
				int power = plev * 3 + 50;
				if(p_ptr->mimic_form == MIMIC_BEAST) power += plev * 2;
				if(only_info) return format(_str_eff_dam,power);
				msg_print(_("あなたは月に向けて大咆哮を放った！",
                            "You howl loudly at the moon!"));
				project_hack2(GF_SOUND,0,0,power);
				turn_monsters(power);
				set_afraid(0);
				set_hero(30 + randint1(30),FALSE);
				aggravate_monsters(0,FALSE);

			}
			else
			{
				int power = plev * 3;
				if(p_ptr->mimic_form == MIMIC_BEAST) power += plev;
				if(only_info) return format(_str_eff_power,power);
				msg_print(_("あなたは恐ろしげに遠吠えした。", "You let out a terrifying howl."));
				turn_monsters(power);
				set_afraid(0);
				set_hero(20 + randint1(10),FALSE);
				aggravate_monsters(0,FALSE);
			}
			break;
		}
	case 2: //ハイスピードバウンス
		{
			int len = p_ptr->lev / 5 + 5;
			if(len < 5) len = 5;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}

	case 3://スターファング rush_attack2()を改変
		{
			int num = plev / 9;
			int cnt;
			int range = plev / 10;
			int dam = plev  + chr_adj * 5 / 3;
			if(p_ptr->mimic_form == MIMIC_BEAST)
			{
				range += 2;
				dam += plev;
			}
			if(only_info) return  format(_("射程:%d 損傷:%d*%d", "rng:%d dam: %d*%d"),range,dam,num);

			msg_print(_("あなたは地を蹴って跳んだ！", "You pounce off the ground!"));
			for(cnt=0;cnt < num;cnt++)
			{
				int i, path_n;
				int tx,ty;
				int target_m_idx = 0;
				int target_num = 0;
				u16b path_g[32];
				bool moved = FALSE;
				for (i = 1; i < m_max; i++)
				{
					monster_type *m_ptr;
					m_ptr = &m_list[i];
					if(!m_ptr->r_idx) continue;
					if(m_ptr->cdis > range) continue;
					if(!m_ptr->ml || !projectable(py, px, m_ptr->fy, m_ptr->fx)) continue;
					if (!is_hostile(m_ptr)) continue;
					target_num++;
					if(!one_in_(target_num)) continue;
 					target_m_idx = i;
				}
				if(!target_m_idx) break;
				tx = m_list[target_m_idx].fx;
				ty = m_list[target_m_idx].fy;
				project_length = 10;
				path_n = project_path(path_g, project_length, py, px, ty, tx, PROJECT_STOP | PROJECT_KILL);
				project_length = 0;
				if (!path_n) break;
				ty = py;
				tx = px;

				/* Project along the path */
				for (i = 0; i < path_n; i++)
				{
					monster_type *m_ptr;
					int ny = GRID_Y(path_g[i]);
					int nx = GRID_X(path_g[i]);

					if (!cave[ny][nx].m_idx && player_can_enter(cave[ny][nx].feat, 0))
					{
						ty = ny;
						tx = nx;
						continue;
					}
					if (!cave[ny][nx].m_idx)
					{
						msg_print(_("壁に激突した。", "You run into a wall."));
						break;
					}

					if (!player_bold(ty, tx)) teleport_player_to(ty, tx, TELEPORT_NONMAGICAL);
					update_mon(cave[ny][nx].m_idx, TRUE);
					msg_format(_("「ワオーン！」", "'Hoowl!'"));

					if (!player_bold(ty, tx)) teleport_player_to(ty, tx, TELEPORT_NONMAGICAL);
					moved = TRUE;
					project(0, 0, ny, nx, dam, GF_ARROW, PROJECT_KILL | PROJECT_JUMP, -1);

					break;
				}
				if (!moved && !player_bold(ty, tx)) teleport_player_to(ty, tx, TELEPORT_NONMAGICAL);

			}
			if(!cnt) msg_print(_("・・しかし周囲に敵がいなかった。", "...However, you couldn't find any enemies."));

			break;
		}
	case 4://血に餓えたウルフファング
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;
			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				int num = 3;
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);
				set_afraid(0);
				set_shero(randint1(20) + 20, FALSE);
				msg_format(_("あなたは%sに飛びかかった！", "You leap at %s!"),m_name);
				for(num=3; (num>1 && cave[y][x].m_idx);num--) py_attack(y, x, 0);
				new_class_power_change_energy_need = 150;
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



