cptr do_cmd_class_power_aux_reimu(int num, bool only_info)
{
	int dir, i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int rank;

	if(use_itemcard) rank = 5;
	else rank = osaisen_rank();

	switch(num)
	{

	case 0:
		{
			int num = 3 + plev / 7;
			int dice = 3 + plev / 20;
			int sides = 3 + chr_adj / 10;

			if(rank > 6) num += rank - 4;
			if(only_info) return format(_str_eff_dam_dice_sides_mult,dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("針を投擲した！", "You throw needles!"));
			for (i = 0; i < num; i++) fire_bolt(GF_MISSILE, dir, damroll(dice, sides));
			break;
		}
	case 1:
		{

			if(only_info) return format("");
			msg_format(_("精神を集中し、大きく手を振った。",
                        "You concentrate your mind and move your hands around."));
			(void) destroy_doors_touch();

			break;
		}
	case 2:
		{
			bool success = FALSE;
			if(only_info) return format("");
			msg_format(_("その場に座り、手を合わせて祝詞を唱えた。",
                        "You sit down, put your hands together and chant a blessing."));
			if(plev < 30)
			{
				if(remove_curse()) success = TRUE;
			}
			else
			{
				if(remove_all_curse())  success = TRUE;
			}
			if(success) msg_print(_("装備品の呪縛が消えた。", "The curses on your equipment have disappeared."));
			else msg_print(_("何も起こらなかった。", "Nothing happens."));

			break;
		}
	case 3:
		{
			if(only_info) return format("");

			msg_print(_("周囲の気配を探った・・", "You look for nearby presences..."));
			(void)detect_monsters_normal(DETECT_RAD_DEFAULT);
			(void)detect_monsters_invis(DETECT_RAD_DEFAULT);
			break;
		}
	case 4:
		{
			int dam = (plev * 3 + chr_adj * 5) / 2;
			int rad =  plev / 20 + rank / 3;
			if(dam < 25) dam = 25;

			if(only_info) return format(_("損傷:%d 半径:%d", "dam: %d rad: %d"),dam,rad);

			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("陰陽玉を取り出し、投げつけた！", "You take out a Ying-Yang orb and throw it!"));
			fire_ball(GF_HOLY_FIRE,dir,dam,rad);

			break;
		}

	case 5:
		{
			int x, y;
			if(only_info) return format("");
			if (!tgt_pt(&x, &y)) return NULL;

			if (p_ptr->anti_tele)
			{
				msg_print(_("不思議な力がテレポートを防いだ！", "A mysterious force prevents you from teleporting!"));
			}
			else if (!cave_player_teleportable_bold(y, x, 0L) ||
			    (distance(y, x, py, px) > MAX_SIGHT ) ||
			    !projectable(py, px, y, x))
			{
				teleport_player(30, 0L);
				msg_print(_("ちょっと移動に失敗した。", "You couldn't move to your desired location."));
			}
			else
			{
				msg_print(_("あなたは離れた場所に一瞬で現れた。", "You instantly appear in a distant location."));
				teleport_player_to(y, x, 0L);
				new_class_power_change_energy_need = 50;
			}
			break;
		}
	case 6:
		{
			int rad = 1 + plev / 40 + rank / 4;
			bool flag = FALSE;
			int num = 3 + plev / 15 + rank / 2;
			int dam = plev + chr_adj;

			if(dam < 30) dam = 30;

			if(only_info) return format(_("損傷:%d * %d", "dam: %d * %d"),dam,num);

			if (!in_bounds2(py + 1,px) || !cave_have_flag_grid( &cave[py + 1][px],FF_PROJECT)
				||  !in_bounds2(py - 1,px) || !cave_have_flag_grid( &cave[py - 1][px],FF_PROJECT)
				||  !in_bounds2(py ,px + 1) || !cave_have_flag_grid( &cave[py ][px + 1],FF_PROJECT)
				||  !in_bounds2(py ,px - 1) || !cave_have_flag_grid( &cave[py ][px - 1],FF_PROJECT))
				{
					msg_print(_("壁が邪魔だ！", "There's a wall in the way!"));
					return NULL;
				}

			msg_format(_("あなたの周りに幾つもの光球が現れた・・",
                        "Several spheres of light appear around you..."));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_DISP_ALL, dam, 3, rad,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_str_mess_cant_find_enemies);
					break;
				}
			}
			break;

		}

	case 7:
		{
			int heal = plev * 5;
			if(only_info) return format(_str_eff_heal,heal);
			msg_format(_("治癒の札を取り出して貼った。", "You take out a healing ofuda and put it on yourself."));
			hp_player(heal);
			set_cut(0);

			break;
		}
	case 8:
		{
			int base = 15 + plev / 5 + rank * 2;
			int v;
			if(only_info) return format(_("期間:%d+1d%dターン", "dur: %d+1d%d turns"),base,base);
			msg_format(_("防壁を張り巡らせた！", "You form a barrier around yourself!"));
			v = base + randint1(base);
			set_oppose_acid(v, FALSE);
			set_oppose_elec(v, FALSE);
			set_oppose_fire(v, FALSE);
			set_oppose_cold(v, FALSE);
			set_oppose_pois(v, FALSE);
			if(plev > 39) set_shield(v,FALSE);

			break;
		}
	case 9:
		{
			if(only_info) return format("");

			if (p_ptr->csp < p_ptr->msp) msg_print(_("少し頭がハッキリした。", "You feel your head clear a bit."));

			p_ptr->csp += (3 + randint1(1 + plev/10));
			if (p_ptr->csp >= p_ptr->msp)
			{
				p_ptr->csp = p_ptr->msp;
				p_ptr->csp_frac = 0;
			}

			p_ptr->redraw |= (PR_MANA);
		}
		break;
	/*::: -Hack- 八方鬼縛陣　magic_num1[0]とtim_general[0]を使用する*/
	//v1.1.95 GF_NO_MOVEに置き換えた
	case 10:
		{
			int y, x;
			int dam = plev * 2 + chr_adj * 10 / 3 + (rank+1) * plev / 5;
			monster_type *m_ptr;

			int base = 7 + p_ptr->lev / 10 + rank * 2;
			if(only_info) return format(_("損傷:%d 期間:%d", "dam: %d dur: %d"),dam, base);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("地面の陣から光が噴き上がった！", "A pillar of light rises up from a circle on the ground!"),m_name);

				project(0,1,m_ptr->fy,m_ptr->fx,dam,GF_HOLY_FIRE,PROJECT_KILL,-1);
				if(!m_ptr->r_idx) break;

				msg_format(_("%sは光の柱に囚われた！", "%s is restrained by the pillar of light!"),m_name);
				project(0, 0, m_ptr->fy, m_ptr->fx, base, GF_NO_MOVE, PROJECT_KILL|PROJECT_JUMP|PROJECT_HIDE, -1);

				//p_ptr->magic_num1[0] = cave[y][x].m_idx;
				//set_tim_general(base,FALSE,0,0);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}

	/*::: -Hack - グローバル変数oonusa_dam=0にダメージ値を設定し、project()中でこれが0でなければそれをダメージ値として再射撃する*/
	case 11: //よくばり大幣
		//v1.1.44 ルーチン見直し
		{

			int tx,ty;
			int dam = 0;
			int restart_chance = osaisen_rank();

			if (restart_chance < 7) restart_chance = 7; //テスト用の再発動率最低値

			if(inventory[INVEN_RARM].tval == TV_STICK && inventory[INVEN_RARM].sval == SV_WEAPON_OONUSA )
				dam += calc_weapon_dam(0) / p_ptr->num_blow[0];
			if(inventory[INVEN_LARM].tval == TV_STICK && inventory[INVEN_LARM].sval == SV_WEAPON_OONUSA )
				dam += calc_weapon_dam(1) / p_ptr->num_blow[1];
			if(only_info) return format(_("損傷:%d * 不定", "dam: %d * undef"),dam);

			if (dam <= 0)
			{
				msg_print(_("こんなものを投げても無駄だ。", "No use throwing something like this around."));
				return NULL;

			}

			msg_format(_("大幣が手から離れて飛んで行った！",
                        "Your oonusa flies out of your hand!"));
			execute_restartable_project(0, 0, restart_chance, 0, 0, dam, GF_ARROW, 0);
			msg_format(_("大幣は飛んで帰ってきて再び手の中に納まった。",
                        "Your oonusa returns to your hand."));

		}
		break;
	case 12:
		{
			int rad = 2 + rank / 3;
			int dice = p_ptr->lev / 4 + chr_adj / 3;
			int sides = p_ptr->lev * rank / 10;
			int base = p_ptr->lev * (1 + rank / 3);

			if(dice < 10) dice = 10;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("巨大な陰陽玉が唸りを上げて飛んでいった！", "You hurl a gigantic Ying-Yang orb!"));
			fire_rocket(GF_HOLY_FIRE, dir, base + damroll(dice,sides), rad);
			break;
		}
	case 13: //博麗幻影
		{
			int i,cnt=0;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(only_info) return "";
			for (i = 1; i < m_max; i++)
			{
				int dist_tmp;
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_REIMU) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt)
			{
				msg_format(_("すでに術を実行している。", "This technique is already in effect."),num);
				return NULL;
			}

			if(summon_named_creature(0, py, px, MON_REIMU, PM_EPHEMERA))
			{
				if(use_itemcard)
					msg_print(_("博麗の巫女の幻影が出現した！", "A phantom of the Hakurei shrine maiden appears!"));
				else
					msg_print(_("自分の幻影が出現した！", "Your phantom appears!"));

			}
			else
				msg_print(_("術に失敗した。", "The technique failed."));


			break;
		}
	case 14:
		{
			if(only_info) return "";

			msg_print(_("あなたは全てのものから解き放たれた！", "You become free from all things!"));
			p_ptr->special_defense |= MUSOU_TENSEI;
			p_ptr->redraw |= (PR_MAP | PR_STATUS);
			p_ptr->update |= (PU_MONSTERS | PU_BONUS);
			p_ptr->window |= (PW_OVERHEAD | PW_DUNGEON);

			break;
		}




	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


