cptr do_cmd_class_power_aux_yugi(int num, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int str_adj = adj_general[p_ptr->stat_ind[A_STR]];
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int rad = 5 + plev / 10;
			if(only_info) return format(_str_eff_area,rad);
			if(use_itemcard)
				msg_print(_("カードが地面を激しく揺らした！", "The card makes the ground shake violently!"));
			else
				msg_print(_("地面を踏み鳴らした。", "You stomp on the ground."));
			earthquake(py, px, rad);

			break;
		}
	case 1:
		{
			int dam = plev * 3 + str_adj * 5;
			int rad = 3 + plev / 16 + str_adj / 20;
			if(only_info) return format(_str_eff_dam_around,dam/2);
			msg_format(_("大きく息を吸い込み、大音声を放った！", "You take a deep breath, and let out a loud shout!"));
			project(0, rad, py, px, dam, GF_SOUND, PROJECT_KILL|PROJECT_GRID , -1);
			aggravate_monsters(0,FALSE);
			break;

		}
	case 2:
		{
			int base = 10;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			set_nennbaku(randint1(base) + base, FALSE);
			break;
		}
	case 3:
		{
			int sides = plev * 3 + str_adj * 5;

			if(only_info) return format(_("損傷:1d%d", "dam: 1d%d"),sides);
			msg_format(_("冷たい風が吹き荒れた！", "Intense freezing wind starts blowing!"));
			project_hack2(GF_TORNADO,1,sides,0);
			break;
		}
	case 4:
		{
			int base = 25;
			int v;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			v = base + randint1(base);
			set_shero(v, FALSE);
			hp_player(30);
			set_afraid(0);
			set_fast(v, FALSE);
			break;
		}
	case 5:
		{
			int rad = plev / 5 + str_adj / 5;
			if(only_info) return format(_str_eff_area,rad);
			if(!destroy_area(py, px, rad, FALSE,FALSE,FALSE)) msg_print(_("ここでは使えない。", "You can't use it here."));

			break;
		}

	case 6:
		{
			int base = plev * 3 + str_adj * 5;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base/2,base/2);
			msg_format(_("掌から閃光と熱風が吹き荒れた！", "Light and scorching wind bursts out from your palm!"));
			project_hack2(GF_NUKE,1,base/2,base/2);
			break;
		}
	case 7:
		{
			int dam = 0;

			if(!p_ptr->concent) dam = plev * 3 + str_adj * 3;
			else if(p_ptr->concent == 1) dam = plev * 5 + str_adj * 5;
			else dam = plev * 10 + str_adj * 10 + chr_adj * 10;

			if(only_info)
			{
				if(p_ptr->concent < 2)
					return format(_str_eff_dam_around,dam/2);
				else
					return format(_str_eff_dam,dam);
			}
			if(p_ptr->concent == 0)
			{
				do_cmd_concentrate(0);
				project(0, 3, py, px, dam, GF_DISP_ALL, PROJECT_KILL|PROJECT_GRID , -1);

			}
			else if(p_ptr->concent == 1)
			{
				do_cmd_concentrate(0);
				project(0, 6, py, px, dam, GF_FORCE, PROJECT_KILL|PROJECT_GRID , -1);
			}
			else
			{
				msg_format(_("あなたが力強く三歩目を踏み出すと、周囲の全てのものが吹き飛んだ！",
                            "You make a powerful third step, and everything around you gets blown away!"));
				project_hack2(GF_DISINTEGRATE,0,0,dam);
			}


			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



