/*:::慧音特技*/
cptr do_cmd_class_power_aux_keine(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	bool hakutaku = FALSE;

	if(use_itemcard)
	{
		if(num != 5) hakutaku = TRUE;

	}
	else if(p_ptr->magic_num1[0]) hakutaku = TRUE;

	switch(num)
	{
	case 0:
		{
			int dice = 5 + plev / 5;
			int sides = 6 + chr_adj / 6;
			bool hyper = FALSE;
			if(buki_motteruka(INVEN_RARM) && inventory[INVEN_RARM].name1 == ART_KUSANAGI
				|| buki_motteruka(INVEN_LARM) && inventory[INVEN_LARM].name1 == ART_KUSANAGI)
				hyper = TRUE;

			if(hyper) dice *= 3;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			if(hyper)
			{
				msg_print(_("神剣が鋭く煌めいた！", "The sacred sword glitters sharply!"));
				fire_beam(GF_PSY_SPEAR, dir,damroll(dice,sides));
			}
			else
			{
				msg_print(_("剣型の弾を放った。", "You fire a sword-shaped bullet."));
				fire_bolt(GF_MISSILE,dir,damroll(dice,sides));
			}
			break;
		}
	case 1:
		{
			int dam = 10 + plev + chr_adj * 2;
			int rad = 2;
			bool hyper = FALSE;

			if(inventory[INVEN_NECK].name1 == ART_MAGATAMA)	hyper = TRUE;

			if(hyper)
			{
				dam *= 2;
				rad = 5;
			}
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			if(hyper)
			{
				msg_print(_("神宝から光が溢れだした！", "Light pours forth out of the sacred magatama!"));
				fire_ball(GF_HOLY_FIRE, dir, dam, rad);
			}
			else
			{
				msg_print(_("光弾を放った。", "You fire a bullet of light."));
				fire_ball(GF_LITE, dir, dam, rad);
			}
			break;

		}
	case 2:
		{
			int base = 10;
			int sides = 10;
			int v;
			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			v = randint1(sides) + base;

			if(inventory[INVEN_RARM].name1 == ART_KAGAMI || inventory[INVEN_LARM].name1 == ART_KAGAMI )
			{
				msg_format(_("神鏡が眩く光った！", "The sacred mirror glows in dazzling light!"));
				set_oppose_acid(v, FALSE);
				set_oppose_elec(v, FALSE);
				set_oppose_fire(v, FALSE);
				set_oppose_cold(v, FALSE);
				set_oppose_pois(v, FALSE);
				set_ultimate_res(v, FALSE);
			}
			else
			{
				msg_format(_("目の前に光る鏡が現れた。", "A glowing mirror appears in front of you."));
				set_tim_reflect(v, FALSE);
			}

			break;
		}
	case 3://歴史隠蔽1、オールドヒストリー
		if(hakutaku)
		{
			if(only_info) return format("");
			msg_print(_("秘された歴史が頭の中に流れ込んでくる・・",
                        "The concealed history flows through your mind..."));
			wiz_lite(FALSE);
			probing();

		}
		else
		{
			int heal = (plev + 20 + chr_adj) / 2;
			if(only_info) return format(_("治癒:%d", "heal: %d"),heal);

			msg_print(_("傷を受けた歴史を隠した。", "You conceal the history of receiving wounds."));
			hp_player(heal);
			set_cut(0);
			set_poisoned(0);

		}
		break;
	case 4:
		if(hakutaku)
		{
			if(only_info) return format("");
			msg_print(_("自分史の再構築を開始した・・",
                        "You start reconstructing your own history..."));
			hp_player(5000);
			set_poisoned(0);
			set_stun(0);
			set_cut(0);
			set_image(0);
			do_res_stat(A_STR);
			do_res_stat(A_INT);
			do_res_stat(A_WIS);
			do_res_stat(A_DEX);
			do_res_stat(A_CON);
			do_res_stat(A_CHR);
			set_alcohol(0);
			restore_level();
			dispel_player();
			set_asthma(0);

			if (p_ptr->muta1 || p_ptr->muta2 || p_ptr->muta3 || p_ptr->muta4)
			{
				if(muta_erasable_count()) msg_print(_("全ての突然変異が治った。", "All of your mutations have been cured."));
				p_ptr->muta1 = p_ptr->muta2 = p_ptr->muta3 = p_ptr->muta4 = 0;
				p_ptr->muta1 = p_ptr->muta1_perma;
				p_ptr->muta2 = p_ptr->muta2_perma;
				p_ptr->muta3 = p_ptr->muta3_perma;
				p_ptr->muta4 = p_ptr->muta4_perma;
				p_ptr->update |= PU_BONUS;
				p_ptr->bydo = 0;
				handle_stuff();
				mutant_regenerate_mod = calc_mutant_regenerate_mod();
			}
			msg_print(_("再構築が完了した。", "Reconstruction complete."));

		}
		else
		{
			int rad = 2 + plev / 10;
			if(only_info) return format(_str_eff_area,rad);
			msg_print(_("周囲の罠をなかったことにした！", "You make nearby traps never exist!"));
			//v1.1.70 箱の罠が対象から抜けていたので修正
			project(0, rad, py, px, 0, GF_KILL_TRAP, (PROJECT_GRID|PROJECT_ITEM), -1);

		}
		break;

	case 5:
		if(hakutaku)
		{
				if(only_info) return format("");
				if (!get_check(_("このフロアの歴史の再構築を試みますか？", "Attempt to reconstruct history of this level?"))) return NULL;

				if (p_ptr->inside_arena || ironman_downward)
				{
					msg_print(_("何も起こらなかった。", "Nothing happens."));
				}
				else
				{
					/*:::0ではまずいはず*/
					p_ptr->alter_reality = 1;
					p_ptr->redraw |= (PR_STATUS);
				}

		}
		else
		{
			int power = plev * 4;
			if(power < 120) power = 120;
			if(only_info) return format(_str_eff_power,power);
			symbol_genocide(power, TRUE,0);

		}
		break;


	case 6:
		if(hakutaku)
		{
			int dam = plev * plev;
			if(dam < 1000) dam = 1000;
			if(only_info) return format(_str_eff_dam_around,dam/2);
			msg_print(_("因果律の修正が発動した！", "You initiate correction of causality!"));
			project(0, 4, py, px, dam, GF_N_E_P, PROJECT_KILL, -1);

		}
		else
		{
			int base = 10;
			int sides = 5;
			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);

			set_tim_superstealth(randint1(sides) + base, FALSE, SUPERSTEALTH_TYPE_NORMAL);

		}
		break;

	case 7:
		{
			int dice = 5 + p_ptr->lev / 2;
			int sides = 15 + chr_adj / 2;
			if(sides < 1) sides = 1;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("あなたは太陽のように輝き始めた！", "You start shining bright like the sun!"));
			project_hack2(GF_LITE,dice,sides,0);
			break;
		}
		break;

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}








