cptr do_cmd_class_power_aux_ninja(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			int rad = DETECT_RAD_DEFAULT;
			if(only_info) return format(_str_eff_area,rad);
			detect_monsters_mind(rad);
			break;
		}
	case 1:
		{
			object_type forge, *q_ptr = &forge;
			if(only_info) return _("食料を生成", "create food");
			msg_print(_("食料を調達した。", "You prepare some food."));
			object_prep(q_ptr, lookup_kind(TV_FOOD, SV_FOOD_RATION));
			q_ptr->discount = 99;
			drop_near(q_ptr, -1, py, px);
			break;
		}
	case 2:
		{
			int power = plev * 2;
			if(only_info) return format(_str_eff_power,power);

			if(plev < 30)
			{
				if (!get_aim_dir(&dir)) return NULL;
				msg_print(_("あなたは標的を指さし術をかけた。", "You point at the target and use your jutsu."));
				(void)stasis_monster(dir);
				break;
			}
			else
			{
				msg_print(_("必殺の念を放った！", "You emit feeling of certain death!"));
				stasis_monsters(power);
			}
			break;
		}
	case 3:
		{
			if(only_info) return format("");
			brand_weapon(4); //鈴蘭エゴ
			break;
		}
	case 4:
		{
			if(only_info) return format("");
			if(!hit_and_away()) return NULL;
			break;
		}

	case 5:
		{
			int rad = 5 + plev / 2;
			if(only_info) return format(_str_eff_area,rad);
			msg_print(_("周辺を調査した・・", "You investigate the nearby area..."));
			map_area(rad);
			detect_all(rad);
			break;

		}
	case 6:
		{
			int mult = 2 + plev / 15;
			if (only_info) return format(_("倍率：%d", "mult: %d"),mult);
			msg_print(_("あなたは全身を撓めた・・", "You bend your entire body..."));
			if (!do_cmd_throw_aux(mult, FALSE, -1)) return NULL;
			break;
		}
	case 7:
		{
			if(only_info) return format("");
			if (!get_aim_dir(&dir)) return NULL;
			destroy_door(dir);
			break;

		}

	case 8:
		{
			base = 10 + plev / 5;
			if(only_info) return format("");
			fire_ball(GF_FIRE, 0, 50+plev, plev/10+2);
			teleport_player(30, 0L);
			set_oppose_fire(base + randint1(base), FALSE);
			break;

		}
	case 9:
		{
			if(only_info) return format("");
			if (!ident_spell(FALSE)) return NULL;
			break;
		}
	case 10:
		{
			if(only_info) return format("");
			if (!(p_ptr->special_defense & NINJA_KAWARIMI))
			{
				msg_print(_("あなたはこっそりと仕込みを済ませた・・",
                            "You finish your secret preparations..."));

				p_ptr->special_defense |= NINJA_KAWARIMI;
				p_ptr->redraw |= (PR_STATUS);
			}

			break;
		}
	case 11:
		{
			if(only_info) return format("");
			probing();
			break;
		}
	case 12:
		{
			if(only_info) return format("");
			msg_print(_("部屋が煙に包まれた！", "The room is filled with smoke!"));
			unlite_room(py,px);
			break;
		}
	case 13:
		{
			int len = p_ptr->lev / 5;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}
	case 14:
		{
			dice = base = 6;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);
			set_tim_superstealth(0,TRUE,0);
			set_multishadow(base+randint1(dice), FALSE);
			break;
		}
	case 15:
		{
			dice = base = 15;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);
			set_multishadow(0, TRUE);
			msg_format(_("あなたの姿は半ば宙に溶けた・・", "Your form partially dissolves in air..."));
			set_tim_superstealth(base + randint1(dice),FALSE, SUPERSTEALTH_TYPE_NORMAL);
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


