cptr do_cmd_class_power_aux_wriggle(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int rad = 9 + plev / 3;
			if(only_info) return format(_str_eff_area,rad);
			msg_print(_("蟲たちが周りの様子を知らせてくれた。",
                        "Insects inform you about surrounding area."));
			(void)detect_monsters_normal(DETECT_RAD_DEFAULT);
			(void)detect_monsters_invis(DETECT_RAD_DEFAULT);

			break;
		}
	case 1:
		{
			bool flag = FALSE;
			if(only_info) return "";
			for(i=0;i<(2 + p_ptr->lev / 15) ;i++)
			{
				if ((summon_specific(-1, py, px, p_ptr->lev, SUMMON_SPIDER, (PM_ALLOW_GROUP | PM_FORCE_PET))))flag = TRUE;
			}
			if(flag)
				msg_print(_("蟲達を呼び出した！", "You summon insects!"));
			else
				msg_print(_("蟲は現れなかった。", "No insects appear."));
			break;

		}
	case 2:
		{
			bool flag = FALSE;
			int i;
			int dice = 2 + plev / 10;
			int sides = 3 + chr_adj / 10;
			int num = 4 + plev / 8;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);
			msg_print(_("蟲たちを放った！", "You release insects!"));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_ARROW, damroll(dice,sides),1, 0,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_str_mess_cant_find_enemies);
					break;
				}
			}
			break;
		}
	case 3:
		{
			int j;
			int chance = plev * 2 + chr_adj * 5;
			if(only_info) return format("");

			if (p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}


			if(one_in_(4))msg_print(_("あなたは特殊な香りを放った・・", "You release a special pheromone..."));
			else if(one_in_(3))msg_print(_("あなたは蟲の言葉で話しかけた・・", "You start speaking insect language..."));
			else if(one_in_(2))msg_print(_("あなたは妖しい光を放った・・", "You emit a bewitching light..."));
			else msg_print(_("あなたは触覚を誘惑的に動かした・・", "You move alluringly..."));

			for (j = 1; j < m_max; j++)
			{
				char m_name[80];
				monster_type *m_ptr;
				monster_race *r_ptr;
				bool success = FALSE;

				//視界内の虫系の敵が対象
				m_ptr = &m_list[j];
				if(!m_ptr->r_idx) continue;
				if(r_info[m_ptr->r_idx].flags1 & RF1_QUESTOR) continue;
				if(!projectable(py,px,m_ptr->fy,m_ptr->fx)) continue;
				if(is_pet(m_ptr) || is_friendly(m_ptr)) continue;
				if(!m_ptr->ml) continue;

				r_ptr = &r_info[m_ptr->r_idx];
				if(r_ptr->d_char != 'I' && r_ptr->d_char != 'c' && r_ptr->d_char != 'w') continue;

				monster_desc(m_name, m_ptr, 0);
				(void)set_monster_csleep(j, 0);
				if((r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2) || r_ptr->level > plev)
				{
					chance /= 2;
					if(r_ptr->level < randint1(chance))
					{
						msg_format(_("%sと仲良くなった！", "%s is now friendly towards you!"),m_name);
						set_friendly(m_ptr);
						success = TRUE;
					}
				}
				else
				{
					if(r_ptr->level < randint1(chance))
					{
						msg_format(_("%sはあなたに魅せられた！", "%s became charmed by you!"),m_name);
						set_pet(m_ptr);
						success = TRUE;
					}
				}
				if(!success) msg_format(_("%sはあなたに敵対している。", "%s is hostile towards you."),m_name);

			}

			break;
		}
	case 4:
		{
			int rad = 2 + plev / 12;
			base = p_ptr->lev * 3 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam_around,base / 2);
			msg_format(_("あなたは眩い光を放った！", "You emit dazzling light!"));
			project(0, rad, py, px, base, GF_LITE, PROJECT_KILL | PROJECT_ITEM, -1);
			confuse_monsters(plev * 3);
			break;
		}
	case 5:
		{
			bool flag = FALSE;
			int max = p_ptr->lev / 10;
			if(max < 2) max = 2;
			if(only_info) return format(_("最大:%d体", "up to %d"),max);

			for(i=0;i<max;i++) if(summon_named_creature(0, py, px, MON_TUTUGAMUSHI, PM_FORCE_PET)) flag = TRUE;
			if(flag) msg_print(_("ツツガムシを呼び出した！", "You summon tsutsugamushi!"));
			else msg_print(_("蟲は現れなかった。", "No insects appear."));

			break;
		}
	case 6:
		{
			int dice = plev/2 ;
			int sides = 15 + chr_adj / 2;
			int rad = 4;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_LITE,dir,damroll(dice,sides),rad,_("光の群れが標的へと襲い掛かった！",
                                                                    "A glowing swarm flies forth at the target!"))) return NULL;

			break;
		}


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


