cptr do_cmd_class_power_aux_kokoro(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int rad = 10 + plev / 2;
			if(only_info) return format(_str_eff_area,rad);

			detect_monsters_mind(DETECT_RAD_DEFAULT);
			break;
		}

	case 1:
		//面に加える
		{

			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;
			int dir, damage;

			damage= plev * 4;

			if(only_info) return  format(_("損傷:1d%d", "dam: 1d%d"),damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if(dir==5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				int chance;
				int mon_ac;

				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				mon_ac = r_ptr->ac;

				//v1.1.94 モンスター防御力低下中はAC25%カット
				if (MON_DEC_DEF(m_ptr))
				{
					mon_ac = MONSTER_DECREASED_AC(mon_ac);
				}

				chance = (p_ptr->skill_thn + (p_ptr->to_h[0] + p_ptr->lev * ref_skill_exp(SKILL_MARTIALARTS) / 8000)* BTH_PLUS_ADJ);

				msg_format(_("あなたは%sへ飛びかかった！", "You leap at %s!"),m_name);

				if(MON_CSLEEP(m_ptr) || test_hit_norm( chance, mon_ac, m_ptr->ml))
				{
					project(0,0,y,x,randint1(damage),GF_KOKORO,(PROJECT_KILL | PROJECT_HIDE),0);
				}
				else
				{
					msg_format(_("しかし躱された。", "%s avoids your attack."),m_name);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
		}
		break;

	case 2:
		{
			if(only_info) return "";

			if(!cast_monspell_new()) return NULL;
			break;
		}


	case 3: //v1.1.95 効果少し変更
	{
		int power = plev * 5 + chr_adj * 5;
		if (only_info) return format(_("効力:〜%d", "pow: ~%d"), power / 2);

		msg_print(_("絶望のオーラを放った！", "You emit an aura of despair!"));

		project(0, 8, py, px, power, GF_TURN_ALL, PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL, -1);
		if (plev > 19)
			project(0, 8, py, px, power, GF_OLD_CONF, PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL, -1);
		if (plev > 29)
			project(0, 8, py, px, power, GF_STUN, PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL, -1);
		if (plev > 39)
			project(0, 8, py, px, power, GF_DEC_ATK, PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL, -1);

		break;
	}

	case 4: //v1.1.95 性能変更

		{
			monster_type *m_ptr;
			monster_race *r_ptr;
			int m_idx;

			if (only_info) return format(_("効力:不定", "pow: undef"));

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			m_idx = cave[py + ddy[dir]][px + ddx[dir]].m_idx;
			m_ptr = &m_list[m_idx];

			if (m_idx && (m_ptr->ml))
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sに向けて感情の波動を放った！", "You release a wave of emotions at %s!"), m_name);

				//QUESTORには効かなくする。狂戦士化薬を10服くらいガブ飲みしてから使えばボスをあっさりハメ殺せそうなので
				if (r_ptr->flags2 & RF2_EMPTY_MIND || r_ptr->flagsr & RFR_RES_ALL || r_ptr->flags1 & RF1_QUESTOR)
				{
					msg_format(_("%sには効果がなかった！", "%s is unaffected!"), m_name);
					break;
				}
				if (p_ptr->afraid)
				{
					if (set_monster_monfear(m_idx, p_ptr->afraid))
						msg_format(_("%sは恐怖して逃げ出した！", "%s flees in terror!"), m_name);
					set_afraid(0);
				}
				else if (p_ptr->stun)
				{
					if (set_monster_stunned(m_idx, p_ptr->stun))
						msg_format(_("%sは朦朧とした。", "%s is stunned."), m_name);
					set_stun(0);
				}
				else if (p_ptr->shero)
				{
					if (set_monster_timed_status_add(MTIMED2_BERSERK, m_idx, p_ptr->shero))
						msg_format(_("%sは突然暴れ出した！", "%s suddenly starts rampaging!"), m_name);
					set_shero(0, TRUE);
				}
				//つよしスペシャル　攻撃低下+混乱
				else if (p_ptr->tsuyoshi)
				{
					if (set_monster_confused(m_idx, p_ptr->tsuyoshi + p_ptr->image))
					{
						if (is_gen_unique(m_ptr->r_idx) || my_strchr("hknopstuzAFGKLOPSTUVWY", r_ptr->d_char) || r_ptr->flags3 & (RF3_HUMAN | RF3_DEMIHUMAN))
							msg_format(_("%s「オクレ兄さん！！」", "%s - 'Brother OKURE!!'"), m_name);
						else
							msg_format(_("%sは何かキマッちゃってるようだ！", "The mind of %s starts to falter!"), m_name);

					}
					set_monster_timed_status_add(MTIMED2_DEC_ATK, m_idx, p_ptr->tsuyoshi + p_ptr->image);

						//通常の処理を通さずに効果を消去する。副作用の腕耐弱化を無視するため
						p_ptr->tsuyoshi = 0;
					p_ptr->redraw |= (PR_STATUS);
					p_ptr->update |= (PU_BONUS | PU_HP);
					//幻覚も直しておく
					set_image(0);
				}
				//幻覚は混乱扱いにする
				else if (p_ptr->image)
				{
					if (set_monster_confused(m_idx, p_ptr->image))
						msg_format(_("%sは混乱した。", "%s is confused."), m_name);
					set_image(0);
				}
				else
				{
					msg_print(_("しかし押し付けるような感情を抱いていなかった。", "However, you had no emotions to transfer."));
				}

			}
			else
			{
				msg_format(_("そこには何もいない。", "There's nobody here."));
				return NULL;
			}

			break;
		}



	case 5:
		{
			int time;
			int base = 20;
			int choice;
			if(only_info) return format(_str_eff_dur_plus_1d_,base,base);

			if(plev > 44) choice = 6;
			else if(plev > 29) choice = 5;
			else choice = 4;
			time = base + randint1(base);

			switch(randint1(choice))
			{
				case 1:
					msg_print(_("嘆く老婆の面を引いた！",
                                "You pull out the Lamenting Old Woman mask!"));
					set_hero(0,TRUE);
					set_shero(0,TRUE);
					set_blessed(0,TRUE);
					if (!p_ptr->resist_fear) set_afraid(p_ptr->afraid + time);
					break;
				case 2:
					msg_print(_("笑う老爺の面を引いた！",
                                "You pull out the Laughing Old Man mask!"));
					set_hero(time,FALSE);
					set_shero(0,TRUE);
					set_blessed(0,TRUE);
					set_afraid(0);
					break;
				case 3:
					msg_print(_("お多福の面を引いた！",
                                "You pull out the Happiness mask!"));
					set_hero(0,TRUE);
					set_shero(0,TRUE);
					set_blessed(time,FALSE);
					set_afraid(0);
					break;

				case 4:
					msg_print(_("般若の面を引いた！", "You pull out the Hannya mask!"));
					set_hero(0,TRUE);
					set_shero(time,FALSE);
					set_blessed(0,TRUE);
					set_afraid(0);
					break;
				case 5:
					msg_print(_("天狗の面を引いた！", "You pull out the Tengu mask!"));
					set_hero(0,TRUE);
					set_shero(0,TRUE);
					set_blessed(0,TRUE);
					set_afraid(0);
					set_oppose_acid(time, FALSE);
					set_oppose_elec(time, FALSE);
					set_oppose_fire(time, FALSE);
					set_oppose_cold(time, FALSE);
					set_oppose_pois(time, FALSE);
					break;
				case 6:
					msg_print(_("新たな希望の面を引いた！", "You pull out the New Mask of Hope!"));
					hp_player(500);
					set_stun(0);
					set_cut(0);
					set_poisoned(0);
					set_image(0);
					restore_level();
					do_res_stat(A_STR);
					do_res_stat(A_INT);
					do_res_stat(A_WIS);
					do_res_stat(A_DEX);
					do_res_stat(A_CON);
					do_res_stat(A_CHR);
					break;
				default:
					msg_print(_("ERROR:こころルーレットで未定義の数値が出た",
                                "ERROR: Undefined result in Kokoro Roulette"));
					return NULL;
			}



		}
		break;
	case 6:
		{
			int rad = 2 + plev / 24;
			int damage = plev  + chr_adj;
			if(damage < 20) damage = 20;

			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			if(use_itemcard)
				msg_format(_("カードから花火が舞い散った！", "Fireworks shoot out of the card!"));
			else
				msg_format(_("歓喜の舞を舞った！", "You dance a cheerful dance!"));
			cast_meteor(damage, rad, GF_FIRE);

			break;
		}
	case 7:
		{
			int len = plev / 7;
			int damage = 50 + plev * 2;
			if(len < 4) len = 4;

			if(only_info) return format(_str_eff_range_dam,len,damage);
			if (!rush_attack2(len,GF_FORCE,damage,0)) return NULL;
			break;
		}

	case 8:
	{
		int base = 20;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);

		set_tim_general(base + randint1(base), FALSE, 0, 0);

	}
	break;

	case 9:
		{
			dice = p_ptr->lev / 3;
			sides = 10 + chr_adj;
			if(dice < 10) dice = 10;
			if(only_info) return format(_str_eff_dam_base_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("獅子の面の口から熱線が放たれた！",
                        "Intense heat shoots out of the lion mask's mouth!"));
			fire_spark(GF_PLASMA,dir, damroll(dice,sides),1);

			break;
		}


	case 10: //v1.1.95
	{
		int power = plev * 3 + chr_adj * 5;
		if (only_info) return format(_str_eff_power, power);

		msg_print(_("無数の面が周囲に飛んだ...", "Countless masks start flying around..."));

		turn_monsters(power);
		confuse_monsters(power);
		stun_monsters(power);
		project_hack(GF_DEC_ATK, power);

		break;
	}


	case 11:
		{
			base = p_ptr->lev;
			dice = 1;
			sides = chr_adj * 5;
			if(only_info) return format(_("損傷:(%dd%d+%d) * 4", "dam: (%dd%d+%d) * 4"),dice,sides,base);
			msg_format(_("憑かれたように踊り狂った！", "You frenziedly dance, as if you were possessed!"));
			project_hack2(GF_MANA,dice,sides,base);
			project_hack2(GF_DARK,dice,sides,base);
			project_hack2(GF_LITE,dice,sides,base);
			project_hack2(GF_FORCE,dice,sides,base);
		}
		break;

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



