cptr do_cmd_class_power_aux_miko(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int rad = 5 + plev * 2 / 5;
			if(only_info) return format(_str_eff_area,rad);

			msg_format(_("あなたは耳当てをはずした。", "You take off your earmuffs."));
			detect_monsters_normal(rad);
			detect_monsters_invis(rad);
			msg_format(_("耳当てをつけた。", "You put on your earmuffs."));
			break;
		}
	case 1:
		{
			int rad = 2 + plev / 20;
			int damage = plev  + chr_adj ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("多数の光弾を放った！", "You fire multiple bullets of light!"));
			cast_meteor(damage, rad, GF_LITE);

			break;
		}
	case 2:
		{
			if(only_info) return format("");
			msg_format(_("周囲を見渡した・・", "You carefully observe your surroundings..."));
			probing();
			break;
		}
	case 3:
		{
			dice = p_ptr->lev / 3;
			sides = chr_adj / 3 + p_ptr->lev / 10;
			base = p_ptr->lev + chr_adj * 3;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("十七条の光線が放たれた！", "You fire seventeen rays of light!"));
			fire_beam(GF_HOLY_FIRE, dir, base + damroll(dice,sides));
			break;
		}
	case 4:
		{
			int x, y;
			if(only_info) return format("");
			if (!tgt_pt(&x, &y)) return NULL;
			if (!cave_player_teleportable_bold(y, x, 0L) ||
			    (distance(y, x, py, px) > MAX_SIGHT ) ||
			    !projectable(py, px, y, x))
			{
				msg_print(_("そこへは移動できない。", "You cannot move there."));
				return NULL;
			}
			else if (p_ptr->anti_tele)
			{
				msg_print(_("不思議な力がテレポートを防いだ！", "A mysterious force prevents you from teleporting!"));
			}
			else
			{
				msg_print(_("あなたは離れた場所に一瞬で現れた。", "You appear at a distant location in an instant."));
				teleport_player_to(y, x, 0L);
				new_class_power_change_energy_need = 100 - plev;
			}
			break;
		}
	case 5:
		{
			dice = p_ptr->lev / 2;
			sides = 10 + chr_adj / 2;
			if(dice < 15) dice=15;
			if(sides < 1) sides = 1;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("あなたは眩い光を放った！", "You emit dazzling light!"));
			project_hack2(GF_LITE,dice,sides,0);
			break;
		}
	case 6:
		{
			int i;
			bool huto = FALSE, toziko = FALSE;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(only_info) return "";
			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_FUTO) ) huto = TRUE;
				if((m_ptr->r_idx == MON_TOZIKO)) toziko = TRUE;
			}
			if(!huto && summon_named_creature(0, py, px, MON_FUTO, PM_EPHEMERA))
			{
				if(one_in_(3))msg_print(_("「われにおまかせを！」", "'Please let me handle this! '"));
				else if(one_in_(2))msg_print(_("「はなしにもならぬわ！」", "'Your pathetic power isn't worth talking about! '"));
				else msg_print(_("「まだまだぁ！」", "'You haven't won yet! '"));
			}
			if(!toziko && summon_named_creature(0, py, px, MON_TOZIKO, PM_EPHEMERA))
			{
				if(one_in_(3))msg_print(_("「やってやんよ！」", "'Have at it! '"));
				else if(one_in_(2))msg_print(_("「おろかものめが！」", "'You damn fools! '"));
				else msg_print(_("「またですか？」", "'Once again? '"));
			}

			if(huto && toziko) msg_print(_("すでに二人ともこのフロアにいる。", "Those two already are on this level."));
			break;

		}

	case 7:
		{
			int rad = 4;
			int damage = plev * 2 + chr_adj * 2;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("光の爆弾が降り注いだ！", "Bombs of light rain down!"));
			cast_meteor(damage, rad, GF_HOLY_FIRE);

			break;
		}
	case 8:
		{
			dice = p_ptr->lev / 2;
			sides = chr_adj * 3;
			if(dice < 15) dice = 15;
			if(sides < 30) sides = 30;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("巨大な光の剣が大地を切り裂いた！", "A giant blade of light cleaves through the earth!"));
			fire_spark(GF_PSY_SPEAR,dir, damroll(dice,sides),1);

			break;
		}



	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



