cptr do_cmd_class_power_aux_hatate(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int cons = p_ptr->concent;

	switch(num)
	{

	case 0:
		{
			int rad = 9 + plev / 3;
			if(only_info) return format(_str_eff_area,rad);
			if(use_itemcard)
				msg_print(_("カメラに周囲の光景が写った！", "The camera takes a shot of surrounding area!"));
			else
				msg_print(_("あなたはカメラに念を込め、シャッターを押した。",
                            "You concentrate your mind on your camera and press the shutter."));

			map_area(rad);
			detect_objects_normal(rad);
			detect_objects_gold(rad);
			detect_monsters_normal(rad);
			break;
		}
	case 1:
		{
			int rad = 2 + plev / 30;
			int damage = 10 + plev + chr_adj * 2;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_print(_("周囲を無差別に撮影した！", "You indiscriminately photograph everything in your vicinity!"));
			cast_meteor(damage, rad, GF_LITE);

			break;
		}
	case 2:
		{
			if(only_info) return format("");
			if(!hatate_psychography()) return NULL;
			break;

		}
	case 3:
		{
			if(only_info) return format("");

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !cave[target_row][target_col].m_idx || !m_list[cave[target_row][target_col].m_idx].ml)
			{
				msg_print(_("ターゲットを指定しないといけない。", "You have to pick a target."));
				return NULL;
			}

			msg_print(_("あなたは被写体を思い浮かべ、シャッターを押した。",
                        "You think of your photograph subject and press the shutter."));
			project(0, 0, target_row, target_col, 1, GF_PHOTO,  PROJECT_JUMP | PROJECT_KILL, -1);

			break;
		}
	case 4:
		{
			int damage = plev*2 + chr_adj * 2;
			if(only_info) return format(_str_eff_dam,damage);
			msg_print(_("周囲を広角モードで連続撮影した！",
                        "You consecutively take panoramic shots of your surroundings!"));
			project_hack2(GF_LITE,0,0,damage);
			break;
		}

	case 5:
		{
			if(only_info) return format("");
			msg_print(_("全力でカメラに集中した・・", "You fully concentrate on your camera..."));
			wiz_lite(FALSE);
			(void)detect_monsters_normal(255);

			break;

		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


