cptr do_cmd_class_power_aux_kisume(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int cons = p_ptr->concent;

	if(use_itemcard) cons = 3 + plev / 20;

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");

			do_cmd_concentrate(0);
			break;
		}
	case 1:
		{
			int range = 2 + cons * 2;
			int dam = 20 + plev * 2 + chr_adj;

			if(only_info) return format(_str_eff_range_dam,range,dam);
			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("空中から鬼火を落とした。", "A ball of fire drops down from the air."));
			fire_ball(GF_FIRE,dir,dam,2);

			reset_concent = FALSE; //高度を維持する
			break;
		}
	case 2:
		{
			int range = 4 + cons * 2;
			int base = (plev) * (cons+1) + calc_kisume_bonus();
			int tx,ty,dam;

			if(only_info) return format(_("損傷:%d + 1d%d", "dam: %d + 1d%d"),base/2,base/2);

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			dam = base / 2 + randint1(base/2);
			ty = target_row;
			tx = target_col;
			if(!fire_ball_jump(GF_ARROW,dir,dam,0,_("あなたは飛び上がり、標的目掛けて落下した！", "You fly up, and the drop down on the target!"))) return NULL;
			teleport_player_to(ty,tx,TELEPORT_NONMAGICAL);
			//周りがみっしり敵のときどうなるのかチェック要
			break;
		}
	case 3:
		{
			int range = 3 + cons * 2;
			int base;
			int tx,ty,dam;

			if(use_itemcard)
				base = plev * (cons+1) / 2 + 20 * cons;
			else
				base = plev * (cons+1) / 2 + calc_kisume_bonus();

			if(only_info) return format(_("損傷:%d + 1d%d", "dam: %d + 1d%d"),base/2,base/2);

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			dam = base / 2 + randint1(base/2);
			ty = target_row;
			tx = target_col;
			if(!fire_ball_jump(GF_WATER_FLOW,dir,3,3,_("地面にヒビが入り、水が噴き出した！", "Earth cracks, and water gushes forth!"))) return NULL;
			fire_ball_jump(GF_WATER,dir,dam,3,NULL);
			teleport_player_to(ty,tx,TELEPORT_NONMAGICAL);
			set_oppose_fire(randint1(20) + 20, FALSE);

			break;
		}
	case 4:
		{
			int range = 1 + cons;
			int base = plev * (1 + cons*2) + calc_kisume_bonus() * 3;
			int tx,ty,dam;

			if(only_info) return format(_("損傷:%d + 1d%d", "dam: %d + 1d%d"),base/2,base/2);

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			dam = base / 2 + randint1(base/2);
			ty = target_row;
			tx = target_col;
			if(!fire_ball_jump(GF_METEOR,dir,dam,5,_("あなたは隕石のように大地を穿った！", "You strike the earth like a meteor!"))) return NULL;
			teleport_player_to(ty,tx,TELEPORT_NONMAGICAL);
			earthquake(ty, tx, 5);
			aggravate_monsters(0,FALSE);
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


