cptr do_cmd_class_power_aux_toziko(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int num = 1 + plev / 8;

			dice = 3;
			sides = 6 + plev / 15 + chr_adj / 10 ;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);

			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("雷の矢を放った。", "You fire thunder arrows."));
			for (i = 0; i < num; i++) fire_bolt(GF_ELEC, dir, damroll(dice, sides));
			break;
		}
	case 1:
		{
			int rad = 2 + plev / 15;
			base = plev * 8 + chr_adj * 10;
			if(base < 200) base = 200;
			if(only_info) return format(_str_eff_dam_around,base / 2);
			if(use_itemcard)
				msg_format(_("カードが激しく放電した！", "There is an intense electric discharge from the card!"));
			else
				msg_format(_("あなたは激しく放電した。", "You intensily discharge electricity."));
			project(0, rad, py, px, base, GF_ELEC, PROJECT_KILL | PROJECT_ITEM, -1);
			break;
		}
	case 2:
		{
			int xx,yy;
			object_type forge;
			object_type *o_ptr = &forge;

			if(only_info) return format("");

			object_prep(o_ptr, (int)lookup_kind(TV_ARROW, SV_AMMO_LIGHT));
			o_ptr->number = plev / 10 + randint1(plev/10);
			o_ptr->to_d = p_ptr->lev / 5;
			o_ptr->to_h = p_ptr->lev / 5;
			o_ptr->name2 = EGO_ARROW_ELEC;
			object_aware(o_ptr);
			object_known(o_ptr);
			o_ptr->discount = 99;
			msg_format(_("稲妻を矢の形にした。", "You shape thunderbolts into arrows."));
			(void)drop_near(o_ptr, -1, py, px);

			break;
		}
	case 3:
		{
			dice = p_ptr->lev / 2;
			sides = 15 + chr_adj / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("稲妻の嵐が周囲を焼き払った！", "A lightning storm scorches everything around you!"));
			project_hack2(GF_ELEC,dice,sides,0);
			break;
		}

	case 4:
		{
			int dice = plev/2 + chr_adj/2;
			int sides = plev/2;
			int rad ;
			int mult = 100 + 300 * (p_ptr->mhp - p_ptr->chp) / p_ptr->mhp;

			if(dice < 20) dice = 20;
			if(sides < 16) sides = 16;
			sides = sides * mult / 100;
			rad = mult / 50;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_ELEC,dir,damroll(dice,sides),rad,_("雷の鉄槌が下された！", "A hammer of thunder strikes down!"))) return NULL;

			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


