/*:::響子特技*/
/*::: p_ptr->magic_num1[0],[1],p_ptr->magic_num2[0]が歌に使われる*/
/*::: p_ptr->magic_num1[5]がオウム返し候補の呪文番号格納に使われる*/
///mod160305 オウム返し呪文番号をnum1[5]でなくmonspell_yamabikoに格納することにした
cptr do_cmd_class_power_aux_kyouko(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//黙る　行動順消費しない
	case 0:
		{
			if(only_info) return "";
			stop_singing();
			return NULL;

		}
		break;
	case 1: //チャージドクライ
		{
			int rad = 1 + plev / 10;
			int dam = p_ptr->chp * 2 / 3;
			if(dam > 1600) dam=1600;
			if(only_info) return format(_str_eff_dam_around,dam / 2);

			stop_singing();
			if(one_in_(2)) msg_format(_("「おはよーございます！」", "'Good morning!'"));
			else msg_format(_("「YAH○O！」", "'YAHOO!'"));
			project(0, rad, py, px, dam, GF_SOUND, PROJECT_KILL|PROJECT_GRID , -1);

			aggravate_monsters(0,FALSE);
			break;
		}
	case 2://マウンテンエコー
		{
			cptr mname;
			if(monspell_yamabiko) mname = monspell_list2[monspell_yamabiko].name;
			else mname = _("なし", "none");

			if(only_info) return mname;

			if(!monspell_yamabiko)
			{
				msg_format(_("返す言葉がない。", "There's no words to echo."));
				return NULL;
			}

			if(!use_itemcard) stop_singing();
			if(!cast_monspell_new_aux(monspell_yamabiko,FALSE,FALSE,CAST_MONSPELL_EXTRA_KYOUKO_YAMABIKO)) return NULL;

			kyouko_echo(TRUE,0);//一度オウム返ししたら消去
			break;
		}
	case 3://ロングレンジエコー
		{
			int dir;
			int rad = 3;
			int dice = p_ptr->lev / 4 + chr_adj / 3;
			int sides = 5 + p_ptr->lev / 8;
			int base = p_ptr->lev ;

			if(dice < 5) dice = 5;

			if(p_ptr->lev > 39) rad = 4;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_SOUND, dir, base + damroll(dice,sides), rad, _("木霊が一点に収束した！",
                                                                                "Kodama converge at a single point!"))) return NULL;
			break;
		}


	case 4://パワーレゾナンス
		{
			int base = 50 + plev * 2;

			if(only_info) return format(_str_eff_dam,base);
			stop_singing();
			if(dun_level) msg_format(_("あなたの大声がダンジョンに響き渡った！",
                                        "Your loud voice echoes throughout the dungeon!"));
			else msg_format(_("あなたの大声が野山に響き渡った！",
                                "Your loud voice echoes throughout the area!"));
			project_hack2(GF_SOUND,0,0,base);
			aggravate_monsters(0,FALSE);
			break;
		}
	case 5://無限念仏
		{
			int dice = plev/5 +5;
			if(only_info) return  format(_("損傷:%dd%d/ターン", "dam: %dd%d/turn"),dice,dice);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_KYOUKO_NENBUTSU, SPELL_CAST);

		}
		break;
	case 6://プライマルスクリーム
		{
			int dice = plev * 2;
			if(only_info) return  format(_("損傷:1d%d/ターン", "dam: 1d%d/turn"),dice);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_KYOUKO_SCREAM, SPELL_CAST);
		}
		break;
	case 7://チャージドヤッホー
		{
			int dice = p_ptr->lev / 2;
			int sides = 20 + chr_adj / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			stop_singing();
			msg_format(_("全身全霊の叫びを放った！", "You scream with all of your might!"));
			fire_spark(GF_SOUND,dir, damroll(dice,sides),1);
			aggravate_monsters(0,FALSE);

			break;
		}



	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


