cptr do_cmd_class_power_aux_satori(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			if(only_info) return format("");

			satori_reading = TRUE;
			probing();
			satori_reading = FALSE;
			make_magic_list_satori(FALSE);


			break;
		}
	case 1:
		{
			if(only_info) return "";

			if(!cast_monspell_new()) return NULL;
			break;
		}
	case 2:
		{
			int dam = plev * 3 + chr_adj * 5;
			if(dam < 100) dam = 100;
			if(only_info) return format(_str_eff_dam,dam);

			if(plev < 35 && !use_itemcard)
			{
				if (!get_aim_dir(&dir)) return NULL;
				if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
				{
					msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                                "You have to pick a target in sight."));
					return NULL;
				}
				msg_print(_("あなたの第三の眼が妖しい光を放った・・",
                            "Your third eye glows in bewitching light..."));
				fire_ball_hide(GF_SATORI,dir,dam,0);
			}
			else
			{
				if(!use_itemcard)
					msg_print(_("あなたの第三の眼が妖しい光を放った・・",
                            "Your third eye glows in bewitching light..."));
				else
					msg_print(_("カードに描かれた目が妖しい光を放った・・",
                                "The eye drawn on the card glows in bewitching light..."));
				project_hack2(GF_SATORI,0,0,dam);
			}


			break;
		}

	case 3: //v1.1.84 残留思念解読
	{

		if (only_info) return format(" ");

		if (plev < 30)
		{
			if (!psychometry()) return NULL;
		}
		else
		{
			if (!ident_spell(FALSE)) return NULL;
		}

	}
	break;


	case 4://恐怖催眠術(アブソリュートルーザーから少し改変)
	{
		int y, x;
		int i;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (only_info) return format(" ");

		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You cannot use this ability right now."));
			return NULL;
		}

		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			int tim;
			bool flag_success = FALSE;
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);

			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			if (is_pet(m_ptr))
			{
				msg_format(_("%sはすでにあなたの下僕だ。", "%s already is your follower."), m_name);
			}
			else if ((r_ptr->flags1 & RF1_QUESTOR) || (r_ptr->flagsr & RFR_RES_ALL) || r_ptr->flags2 & (RF2_EMPTY_MIND | RF2_WEIRD_MIND))
			{
				msg_format(_("%sには効果がなかった！", "%s is unaffected!"), m_name);
			}
			else if (!MON_MONFEAR(m_ptr))
			{
				msg_format(_("%sの心には隙がない。もっと恐怖を与える必要がある。",
                            "The mind of %s doesn't have a weak spot yet. You'll have to apply terror first."), m_name);
			}
			else
			{
				msg_format(_("第三の目が%sをじっと見つめた...",
                            "Your third eye glares at %s..."), m_name);
				set_pet(m_ptr);

				//ユニークや可変パラメータモンスターはフロアから出ると消えることにしておく
				if (r_ptr->flags7 & RF7_VARIABLE || r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2)
					m_ptr->mflag |= (MFLAG_EPHEMERA);
				msg_format(_("あなたは%sの精神を支配した。", "You gain control of %s."), m_name);
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;

	case 5:
	{
		if (only_info) return format(_("損傷:不定", "dam: undef"));

		msg_print(_("あなたの第三の目が煌々と輝いた。", "Your third eye shines brightly."));
		project_hack2(GF_BRAIN_FINGER_PRINT, 0, 0, 1);

		break;
	}



	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





/*:::宝石(珍品は含まない)*/
bool item_tester_hook_jewel(object_type *o_ptr)
{
	int sv = o_ptr->sval;
	if(o_ptr->tval != TV_MATERIAL) return FALSE;

	if(sv == SV_MATERIAL_GARNET || sv == SV_MATERIAL_AMETHYST || sv == SV_MATERIAL_AQUAMARINE ||
	   sv == SV_MATERIAL_DIAMOND || sv == SV_MATERIAL_EMERALD || sv == SV_MATERIAL_MOONSTONE ||
	   sv == SV_MATERIAL_RUBY  || sv == SV_MATERIAL_PERIDOT || sv == SV_MATERIAL_SAPPHIRE ||
	   sv == SV_MATERIAL_OPAL  || sv == SV_MATERIAL_TOPAZ || sv == SV_MATERIAL_LAPISLAZULI) return TRUE;

	return FALSE;
}


/*:::宝飾師の魔力食い*/
class_power_type class_power_jeweler[] =
{
	{25,1,60,FALSE,TRUE,A_INT,0,0,_("魔力食い(宝石)", "Eat Magic (Gemstones)"),
		_("宝石から魔力を吸い取りMPを回復する。吸い取れる魔力の量は宝石の種類で変わる。",
        "Drains mana from a gemstone, recovering MP. Amount of mana drained depends on the kind of gemstone.")},

	{99,0,0,FALSE,FALSE,0,0,0,"dummy",	""},
};
cptr do_cmd_class_power_aux_jeweler(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;

	switch(num)
	{

	case 0:
		{
			cptr q,s;
			int item;
			object_type *o_ptr;
			int mag;

			if(only_info) return format("");
			item_tester_hook = item_tester_hook_jewel;

			q = _("どの宝石から魔力を吸収しますか? ", "Drain mana from which gemstone?");
			s = _("あなたは魔力を吸収する宝石を持っていない。", "You don't have gemstones to drain mana from.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;

			if (item >= 0) o_ptr = &inventory[item];
			else o_ptr = &o_list[0 - item];

			if(o_ptr->tval != TV_MATERIAL)
			{
				msg_print(_("ERROR:魔力食いで素材以外が選択された",
                            "ERROR: Item other than material chosen for magic eating"));
				return NULL;
			}
			switch(o_ptr->sval)
			{
				case SV_MATERIAL_GARNET:
				case SV_MATERIAL_AMETHYST:
				case SV_MATERIAL_AQUAMARINE:
				case SV_MATERIAL_MOONSTONE:
				case SV_MATERIAL_PERIDOT:
				case SV_MATERIAL_OPAL:
				case SV_MATERIAL_TOPAZ:
				case SV_MATERIAL_LAPISLAZULI:
					mag = 100 + randint1(100);
					break;
				case SV_MATERIAL_SAPPHIRE:
				case SV_MATERIAL_EMERALD:
					mag = 300 + randint1(300);
					break;
				case SV_MATERIAL_RUBY:
				case SV_MATERIAL_DIAMOND:
					mag = 1000;
					break;
				default:
				msg_print(_("ERROR:魔力食いで登録されていないアイテムが選択された",
                            "ERROR: Unlisted item chosen for magic eating"));
				return NULL;
			}

			msg_print(_("あなたは宝石を手に取り、魔力を吸収した。",
                        "You put the gemstone in your hand and drain mana from it."));
			p_ptr->csp += mag;

			if(p_ptr->csp >= p_ptr->msp)
			{
				p_ptr->csp = p_ptr->msp;
				p_ptr->csp_frac = 0;
			}
			p_ptr->redraw |= (PR_MANA);
			p_ptr->window |= (PW_PLAYER);
			p_ptr->window |= (PW_SPELL);

			if (item >= 0)
			{
				inven_item_increase(item, -1);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -1);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}

		}
		break;

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}






