cptr do_cmd_class_power_aux_sh_dealer(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!put_item_into_inven2()) return NULL;
			break;

		}
	case 1:
		{
			if(only_info) return format("");
			display_list_inven2();
			return NULL; //見るだけなので行動順消費なし

		}
	case 2:
		{
			if(only_info) return format("");
			if(!takeout_inven2()) return NULL;
			break;
		}
	case 3:
		{
			if(only_info) return format("");
			if(plev < 30)
			{
				if (!ident_spell(FALSE)) return NULL;
			}
			else
			{
				if (!identify_fully(FALSE)) return NULL;
			}

		}
		break;

	case 4:
		{
			if(only_info) return format("");
			msg_print(_("あなたは周囲を丹念に調べ始めた・・",
                        "You start diligently observing your surroundings..."));
			search_specific_object(4);
		}
		break;

	case 5:
		{
			int i;
			int count_ego = 0;
			int count_art = 0;
			int count_valuable = 0;
			bool no_use = TRUE;
			int value_thre = 100000;

			int rad = 4;
			if(plev > 44) rad = 5;
			if(only_info) return format(_str_eff_area,rad);
			msg_print(_("あなたの眼がキラリと光った！", "Your eyes start to glitter!"));
			for (i = 1; i < o_max; i++)
			{
				object_type *o_ptr = &o_list[i];
				if (!o_ptr->k_idx) continue;
				if (o_ptr->held_m_idx) continue;
				if(object_is_known(o_ptr) && object_value_real(o_ptr) < value_thre) continue;
				if(distance(py,px,o_ptr->iy,o_ptr->ix) > rad || !los(py,px,o_ptr->iy,o_ptr->ix)) continue;
				identify_item(o_ptr);
				no_use = FALSE;
				if(object_is_ego(o_ptr)) count_ego++;
				else if(object_is_artifact(o_ptr)) count_art++;
				else if(object_value_real(o_ptr) >= value_thre) count_valuable++;
			}

			if(no_use)	msg_print(_("・・しかし目新しいものはなかった。",
                                    "However, you didn't find anything of note."));

			if(count_art)
			{
				msg_format(_("これはまさしく伝説の品だ！", "No doubt, this is a legendary item!"));
				if(count_art > 1) msg_format(_("しかも%dつもある！", "And there's %d of them!"),count_art);
			}
			if(count_ego)
			{
				if(count_art) msg_format(_("さらに、", "And not only that!"));
				if(count_ego > 1) msg_format(_("高級な品が%dつほどあるようだ。", "There are about %d ego-items."),count_ego);
				else msg_format(_("高級な品があるようだ。", "There's an ego-item."));
			}
			if(count_valuable)
			{
				if(count_art || count_ego) msg_format(_("他にも、", "That's not all!"));
				if(count_valuable > 1) msg_format(_("極めて貴重な品が%dつほどあるようだ。", "There are about %d extremely valuable items."),count_valuable);
				else msg_format(_("極めて貴重な品があるようだ。", "There's an extremely valuable item."));
			}

			fix_floor_item_list(py,px);

		}
		break;


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



