/*:::騎兵用特技*/
class_power_type class_power_cavalry[] =
{
	{10,0,25,FALSE,FALSE,A_STR,0,0,_("荒馬馴らし", "Tame Unruly Mount"),
		_("敵モンスターに飛び乗って無理矢理手懐け、配下にする。乗馬不可能なモンスターには無効。敵HPが減っているほど効きやすい。",
        "Jump on an enemy monster and forcibly tame them, turning them into your followers. Works on rideable monsters only. More effective against enemies low on health.")},

	{99,0,0,FALSE,FALSE,0,0,0,"dummy",""},
};

cptr do_cmd_class_power_aux_cavalry(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch(num)
	{
	case 0:
		{
			char m_name[80];
			monster_type *m_ptr;
			monster_race *r_ptr;
			int rlev;

			if(only_info) return format("");
			if (p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}

			if (p_ptr->riding)
			{
#ifdef JP
				msg_print("今は乗馬中だ。");
#else
				msg_print("You are already riding right now.");
#endif
				return NULL;
			}
			if (!do_riding(TRUE)) return "";
			m_ptr = &m_list[p_ptr->riding];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);
#ifdef JP
			msg_format("%sに乗った。",m_name);
#else
			msg_format("You ride on %s.",m_name);
#endif
			if (is_pet(m_ptr)) break;
			rlev = r_ptr->level;
			if (r_ptr->flags1 & RF1_UNIQUE) rlev = rlev * 3 / 2;
			if (rlev > 60) rlev = 60+(rlev-60)/2;
			if ((randint1(ref_skill_exp(SKILL_RIDING) / 120 + p_ptr->lev * 2 / 3) > rlev)
				&& one_in_(2) && !p_ptr->inside_arena && !p_ptr->inside_battle
			    && !(r_ptr->flags7 & (RF7_GUARDIAN)) && !(r_ptr->flags1 & (RF1_QUESTOR))
//			    && (rlev < p_ptr->lev * 3 / 2 + randint0(p_ptr->lev / 5)))
			    && (m_ptr->hp < (ref_skill_exp(SKILL_RIDING) / 100 * p_ptr->lev) ))
			{
#ifdef JP
				msg_format("%sを手なずけた。",m_name);
#else
				msg_format("You tame %s.",m_name);
#endif
				if(rlev > randint1(plev)) set_deity_bias(DBIAS_WARLIKE, -2);
				set_pet(m_ptr);
			}
			else
			{
#ifdef JP
				msg_format("%sに振り落とされた！",m_name);
#else
				msg_format("You have been thrown off by %s!",m_name);
#endif
				rakuba(1,TRUE);

				/* Paranoia */
				/* 落馬処理に失敗してもとにかく乗馬解除 */
				p_ptr->riding = 0;
			}
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



/*:::剣術家用特技*/
class_power_type class_power_samurai[] =
{
	{1,0,0,FALSE,FALSE,A_WIS,0,0,_("気合い溜め", "Focus"),
		_("一時的にMPを大幅に増加させる。",
        "Temporarily greatly increases MP.")},
	{25,0,0,FALSE,FALSE,A_DEX,0,0,_("型を構える", "Assume Posture"),
		_("特殊な構えをとって戦う。耐性やステータスなどが変化する。",
        "Assume a special combat posture. Changes resistances/stats/etc.")},

	{99,0,0,FALSE,FALSE,0,0,0,"dummy",""},
};

cptr do_cmd_class_power_aux_samurai(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch(num)
	{
	case 0:
		{
			int max_csp = MAX(p_ptr->msp*4, p_ptr->lev*5+5);
			if (max_csp > 255) max_csp = 255;
			if(only_info) return format(_("最大:%d", "up to %d"),max_csp);

			if (total_friends)
			{
				msg_print(_("今は配下のことに集中していないと。",
                            "You have to stay focused on your followers right now."));
				return NULL;
			}
			if (p_ptr->special_defense & KATA_MASK)
			{
				msg_print(_("今は構えに集中している。",
                            "You're concentrating on your posture right now."));
				return NULL;
			}
			msg_print(_("精神を集中して気合いを溜めた。",
                        "You concentrate your mind and focus."));

			p_ptr->csp += p_ptr->msp / 2;
			if (p_ptr->csp >= max_csp)
			{
				p_ptr->csp = max_csp;
				p_ptr->csp_frac = 0;
			}
			p_ptr->redraw |= (PR_MANA);

			break;
		}
	case 1:
		{
			if(only_info) return format("");
			if (!choose_samurai_kata()) return NULL;
			p_ptr->update |= (PU_BONUS);
			break;
		}

	default:
		msg_format("ERROR:実装していない特技が呼ばれた num:%d",num);
		return NULL;
	}
	return "";
}


