/*:::メディスン猛毒精製用*/
bool item_tester_hook_make_venom(object_type *o_ptr)
{
	if(o_ptr->tval == TV_MATERIAL && (o_ptr->sval == SV_MATERIAL_ARSENIC || o_ptr->sval == SV_MATERIAL_MERCURY || o_ptr->sval == SV_MATERIAL_GELSEMIUM)) return TRUE;
	if(o_ptr->tval == TV_KNIFE && o_ptr->sval == SV_WEAPON_DOKUBARI && !object_is_artifact(o_ptr)) return TRUE;
	if(object_is_melee_weapon(o_ptr) && o_ptr->name2 == EGO_WEAPON_BRANDPOIS) return TRUE;
	return FALSE;
}


cptr do_cmd_class_power_aux_medi(int num, bool only_info)
{
	int dir, i, dam;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			object_type forge, *q_ptr = &forge;
			if(only_info) return format("");
			if(cave[py][px].feat != 93 && !cave_have_flag_bold(py,px,FF_POISON_PUDDLE)) //花地形 v1.1.85 毒沼も追加
			{
				msg_format(_("ここには毒になりそうなものが見当たらない。",
                            "You don't see anything here you can turn into poison."));
				break;
			}
			msg_format(_("「コンパロコンパロ、毒よ集まれ〜」", "'Konparo, konparo, poison gathers~'"));
			object_prep(q_ptr, lookup_kind(TV_POTION, SV_POTION_POISON ));
			drop_near(q_ptr, -1, py, px);

		}
		break;
	case 1:
	case 3:
	case 4:
		{

			int y, x;
			int dist;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int power;
			if(num == 4)
				power = plev * 3 + 30;
			else
				power = plev * 4 + 50;

			if(only_info) return  format(_str_eff_power,power);

			if (num == 4 && p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}


			if (!get_rep_dir2(&dir)) return NULL;
			if(dir==5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);
				if(num == 1)
				{
					msg_format(_("%sに向かって神経毒を振りまいた！", "You release neurotoxin at %s!"),m_name);
					if((r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2)) power /= 3;
					if((r_ptr->flags2 & RF2_POWERFUL)) power /= 2;

					if((r_ptr->flagsr & RFR_IM_POIS) || (r_ptr->flagsr & RFR_RES_ALL))
					{
						msg_format(_("しかし効果がなかった。", "There was no effect."));
						if (r_ptr->flagsr & RFR_IM_POIS) r_ptr->r_flagsr |= (RFR_IM_POIS);
					}
					else if(!monster_living(r_ptr)) msg_format(_("%sは眠らないようだ。", "Doesn't look like %s can sleep."),m_name);
					else if(randint1(power) < r_ptr->level) msg_format(_("%sは毒に抵抗した！", "%s resists the poison!"),m_name);
					else
					{
						if (r_ptr->flags3 & RF3_NO_SLEEP)
						{
							r_ptr->r_flags3 |= (RF3_NO_SLEEP);
							msg_format(_("%sは眠らされなかった。", "%s did not fall asleep."), m_name);
						}
						else
						{
							msg_format(_("%sは眠り込んだ。", "%s falls asleep."), m_name);
							(void)set_monster_csleep(cave[y][x].m_idx, power);
						}

						if (set_monster_timed_status_add(MTIMED2_DEC_ATK, cave[y][x].m_idx, MON_DEC_ATK(m_ptr) + 8 + randint1(8)))
							msg_format(_("%^sは攻撃力が下がったようだ。", "The attack power of %s is lowered."), m_name);

					}
				}
				else if(num == 3)
				{
					msg_format(_("%sに向かって憂鬱の毒を振りまいた！", "You release melancholy poison at %s!"),m_name);
					if((r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2)) power /= 3;
					if((r_ptr->flags2 & RF2_POWERFUL)) power /= 2;

					if((r_ptr->flagsr & RFR_IM_POIS) || (r_ptr->flags2 & RF2_EMPTY_MIND) || (r_ptr->flags2 & RF2_WEIRD_MIND) || (r_ptr->flagsr & RFR_RES_ALL))
					{
						msg_format(_("しかし効果がなかった。", "There was no effect."));
						if (r_ptr->flagsr & RFR_IM_POIS) r_ptr->r_flagsr |= (RFR_IM_POIS);
					}
					else if(!monster_living(r_ptr)) msg_format(_("%sは動じないようだ。", "Doesn't look like it works on %s."),m_name);
					else if(randint1(power) < r_ptr->level) msg_format(_("%sは毒に抵抗した！", "%s resists the poison!"),m_name);
					else
					{
						if (set_monster_slow(cave[y][x].m_idx, MON_SLOW(m_ptr) + 8 + randint1(8))) msg_format(_("%sの動きが遅くなった。",
                                                                                                                "%s starts moving slower."),m_name);

						if (set_monster_timed_status_add(MTIMED2_DEC_MAG, cave[y][x].m_idx, MON_DEC_MAG(m_ptr) + 8 + randint1(8)))
							msg_format(_("%^sは魔法力が下がったようだ。", "The magic power of %s is lowered."), m_name);

					}
					anger_monster(m_ptr);
				}
				else
				{
					if(is_pet(m_ptr))
					{
						msg_format(_("%sはすでに配下だ。", "%s is already your follower."),m_name);
						break;
					}
					msg_format(_("%sに向かって譫妄の毒を振りまいた！", "You release delirium poison at %s!"),m_name);
					if((r_ptr->flags2 & RF2_POWERFUL)) power = power * 2 / 3;

					if((r_ptr->flagsr & RFR_IM_POIS) || (r_ptr->flags2 & RF2_EMPTY_MIND) || (r_ptr->flags2 & RF2_WEIRD_MIND) || (r_ptr->flagsr & RFR_RES_ALL))
					{
						msg_format(_("しかし効果がなかった。", "There was no effect."));
						if (r_ptr->flags3 & RF3_NO_SLEEP) r_ptr->r_flags3 |= (RF3_NO_SLEEP);
						if (r_ptr->flagsr & RFR_IM_POIS) r_ptr->r_flagsr |= (RFR_IM_POIS);
					}
					else if(!monster_living(r_ptr) || (r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2) )
						msg_format(_("%sは動じないようだ。", "Doesn't look like it works on %s."),m_name);
					else if(randint1(power) < r_ptr->level) msg_format(_("%sは毒に抵抗した！", "%s resists the poison!"),m_name);
					else
					{
						msg_format(_("%sはあなたに従った。", "%s now obeys you."),m_name);
						set_pet(m_ptr);
					}
					(void)set_monster_csleep(cave[y][x].m_idx, 0);
					anger_monster(m_ptr);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
		}
		break;
	case 2:
		{
			dam = p_ptr->chp / 3;
			if(dam<1) dam = 1;
			if(dam > 1600) dam=1600;

			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;

			if(use_itemcard)
				msg_print(_("カードから毒ガスが噴き出した。", "Poison gas is released from the card."));
			else
				msg_print(_("あなたは毒のブレスを吐いた。", "You breathe poison."));

			fire_ball(GF_POIS, dir, dam, -2);
			if(!use_itemcard) (void)set_food(p_ptr->food -1500);
			break;
		}


	case 5:
		{
			int dam = 10 + plev * 3 + chr_adj * 8;
			if(dam < 100) dam = 100;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("部屋に毒ガスが充満した！", "The room gets filled with poisonous gas!"));
			project_hack2(GF_POIS, 0,0,dam);
		}
		break;
	case 6:
		{
			int         item;
			object_type forge, *q_ptr = &forge;
			cptr        q, s;
			if(only_info) return format("");

			item_tester_hook = item_tester_hook_make_venom;
			q = _("どれから毒を抽出しますか? ", "Extract poison from which item?");
			s = _("毒を抽出できそうなものが見当たらない。",
                "You don't seem to have anything you can extract poison from.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;
			msg_format(_("「コンパロコンパロ、毒よ集まれ〜」", "'Konparo, konparo, poison gathers~'"));
			object_prep(q_ptr, lookup_kind(TV_POTION, SV_POTION_POISON2 ));
			drop_near(q_ptr, -1, py, px);

			if (item >= 0)
			{
				inven_item_increase(item, -1);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -1);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}
		}
		break;
	case 7:
		{
			int dam = plev * 4 + chr_adj * 15;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("狂乱の毒が充満した！", "Maddening poison spreads around!"));
			project_hack2(GF_REDEYE, 0,0,dam);
		}
		break;


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


