/*:::星専用特技*/
cptr do_cmd_class_power_aux_shou(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int wis_adj = adj_general[p_ptr->stat_ind[A_WIS]];

	switch(num)
	{
	case 0: //へにょりレーザー
		{
			int num = 3 + p_ptr->lev / 15;
			dice = 4 + p_ptr->lev / 20;
			sides = 5 + chr_adj / 10;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides, num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("宝塔から光線が放たれた！", "Rays of light shoot out from the Jeweled Pagoda!"));
			fire_blast(GF_LITE, dir, dice, sides, num, 3,(PROJECT_BEAM | PROJECT_KILL));
			break;
		}
	case 1: //ハングリータイガー
		{
			int len = 2 + p_ptr->lev / 8;
			damage = plev + adj_general[p_ptr->stat_ind[A_STR]] *5;
			if(damage < 50) damage = 50;

			if(only_info) return format(_str_eff_range_dam,len,damage);
			if (!rush_attack3(len,_("あなたは激しく突進した！", "You ferociously rush forward!"),damage)) return NULL;
			break;
		}
	case 2: //レイディアントトレジャー
		{
			damage = p_ptr->lev * 2 + chr_adj * 3 + wis_adj * 2;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("宝塔が眩く光った！", "The Jeweled Pagoda emits dazzling light!"));
			fire_beam(GF_HOUTOU,dir,damage);

			break;
		}
	case 3: //v1.1.30追加　黄金の震眩
		{
			int power = p_ptr->lev * 4;
			if (only_info) return format(_str_eff_power,power);
			msg_format(_("宝塔が黄金の輝きを放った...", "The Jeweled Pagoda emits a golden glow..."));
			lite_room(py, px);
			stun_monsters(power);
			confuse_monsters(power);
			if(plev > 39)
				charm_monsters(power);
		}
		break;

	case 4://アブソリュートジャスティス
		{
			dice = p_ptr->lev / 3;
			sides = 5 + wis_adj / 5 + chr_adj / 5 ;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("宝塔から浄化の光が放たれた！", "Purifying light shoots out from the Jeweled Pagoda!"));
			fire_spark(GF_HOLY_FIRE,dir, damroll(dice,sides),1);

			break;
		}

	case 5: //v1.1.30追加　正義の威光
		{
			int t;
			base = 15;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			t = base + randint1(base);
			msg_format(_("毘沙門天の加護を感じる！", "You feel the divine protection of Bishamonten!"));
			set_tim_sh_holy(t,FALSE);
			set_protevil(t,FALSE);
			set_blessed(t,FALSE);
			break;
		}


	case 6: //レイディアントトレジャーガン
		{
			damage = p_ptr->lev * 4 + chr_adj * 5 + wis_adj * 5;
			if(damage < 300) damage = 300;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("至宝の輝きが溢れ出した！", "Brilliant treasures pour forth!"));
			fire_spark(GF_HOUTOU,dir,damage,2);

			break;
		}
	case 7: //浄化の魔
		{
			dice = 7 + p_ptr->lev / 2;
			sides = p_ptr->lev / 5 + chr_adj / 2 + wis_adj / 2;
			if(sides < 1) sides = 1;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("浄化の光が周囲を焼き尽くした！",
                        "Purifying light scorches area around you!"));
			project_hack2(GF_HOLY_FIRE,dice,sides,0);
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


