/*:::ミスティア特技 p_ptr->magic_num1[0],[1],p_ptr->magic_num2[0]が歌に使われる*/
cptr do_cmd_class_power_aux_mystia(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//歌を止める　行動順消費しない
	case 0:
		{
			if(only_info) return "";
			stop_singing();
			return NULL;

		}
		break;
	case 1: //梟の夜鳴声
		{
			if(only_info) return "";
			msg_format(_("あなたが一声鳴くと、部屋の明かりが消えた。",
                        "You sing, and the light in your vicinity disappears."));
			unlite_room(py,px);
			break;
		}
	case 2://夜雀の歌
		{
			if(only_info) return "";
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_MYSTIA_YAZYAKU, SPELL_CAST);

			break;
		}
	case 3://毒蛾の鱗粉
		{
			int power = 50 + plev * 4;
			int rad = 2 + plev / 10;

			if(only_info) return format(_("効果:%d 範囲:%d", "pow: %d rad: %d"),power/2,rad);
			msg_format(_("刺激性の粉を振り撒いた！", "You sprinkle irritating scale powder!"));
			project(0, rad, py, px, power, GF_OLD_CONF, PROJECT_KILL , -1);

		}
		break;
	case 4://リトルバタリオン
		{
			int i;
			int num = 2 + randint0(plev / 10);
			bool flag = FALSE;
			if(only_info) return format(_("召喚レベル:%d", "sum level: %d"),plev);
			for(i=0;i<num;i++)
			{
				if(summon_specific(-1, py, px, plev, SUMMON_BIRD, (PM_FORCE_PET))) flag = TRUE;
			}
			if(use_itemcard)
			{
				if(flag) msg_format(_("鳥たちが現れた！", "Birds appear!"));
				else msg_format(_("何も現れなかった・・", "Nobody arrives..."));
			}
			else
			{
				if(flag) msg_format(_("配下の鳥を呼んだ！", "You call for your bird followers!"));
				else msg_format(_("何も現れなかった・・", "Nobody arrives..."));
			}

		}
		break;
	case 5:
		{
			int len = p_ptr->lev / 7;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}
	case 6://ミステリアスソング
		{
			int power = plev * 2 + chr_adj * 2;
			if(only_info) return  format(_str_eff_power,power);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_MYSTIA_MYSTERIOUS, SPELL_CAST);

			break;
		}
	case 7://ヒューマンケージ
	{
		int range = plev / 3;
		if (only_info) return  format(_str_eff_area,range);
		do_spell(TV_BOOK_MUSIC, MUSIC_NEW_MYSTIA_HUMAN_CAGE, SPELL_CAST);

		break;
	}

	case 8://真夜中のコーラスマスター
		{
			int power = chr_adj * 5;
			if(only_info) return  format(_("効果:1d%d", "pow: 1d%d"),power);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_MYSTIA_CHORUSMASTER, SPELL_CAST);

			break;
		}


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





