cptr do_cmd_class_power_aux_banki(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0://フライングヘッド
	case 4://マルチプリケイティブヘッド
		{
			int summon_num;
			int max_num = MIN(9,(p_ptr->max_plv / 5));
			int i,cnt=0;
			int summon_idx;
			bool flg_success = FALSE;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(max_num<1)max_num=1;

			if(only_info) return format(_("最大召喚可能数:%d", "max summons: %d"),max_num);
			if(num == 0)
			{
				summon_idx = MON_BANKI_HEAD_1;
				summon_num = 1;
			}
			else
			{
				summon_idx = MON_BANKI_HEAD_2;
				summon_num = 1 + randint1(plev / 10);
			}

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_BANKI_HEAD_1) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
				if((m_ptr->r_idx == MON_BANKI_HEAD_2) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_num)
			{
				msg_format(_("これ以上分身を出せない。", "You cannot create any more clones."));
				return NULL;
			}
			if(cnt + summon_num > max_num) summon_num = max_num - cnt;
			for(i=0;i<summon_num;i++)
			{
				if(summon_named_creature(0, py, px, summon_idx, PM_EPHEMERA)) flg_success = TRUE;
			}

			if(flg_success)	msg_print(_("頭の分身を出した！", "A clone of your head appears!"));
			else msg_print(_("頭の分身に失敗した。", "You failed to clone your head."));

		break;
		}

	case 1: //散開
	case 2: //探査
	case 3: //集合
		{
			int i;
			monster_type *m_ptr;

			if (only_info) return "";

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (!is_pet(m_ptr)) continue;
				if (!(m_ptr->r_idx == MON_BANKI_HEAD_1 || (m_ptr->r_idx == MON_BANKI_HEAD_2)))continue;
				if (teleport_barrier(i)) continue;

				if (num == 1)
				{
					teleport_away(i, 10+randint1(5), 0L);
				}
				else if (num == 2)
				{
					teleport_away(i, 30+randint1(30), 0L);
				}
				else
				{
					teleport_monster_to(i, py, px, 100, TELEPORT_FORCE_NEXT);
				}
			}
		}
		break;

	case 5: //ツインロクロヘッド
		{
			int dir;
			int i;
			int m_idx1 = 0;
			int m_idx2 = 0;
			int damage = plev * 3 + chr_adj * 3;
			if (only_info) return format(_str_eff_dam, damage);

			for (i = 1; i < m_max; i++)
			{
				monster_type *m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (!is_pet(m_ptr)) continue;
				if (!(m_ptr->r_idx == MON_BANKI_HEAD_1 || (m_ptr->r_idx == MON_BANKI_HEAD_2)))continue;
				if (m_ptr->cdis > 1) continue;
				if(m_idx1) m_idx2 = i;
				else m_idx1 = i;
			}

			if (!m_idx1 || !m_idx2)
			{
				msg_print(_("近くに頭が二つ以上ないとこの技は使えない。",
                            "You cannot use this ability unless you're close enough to two of your heads."));
				return NULL;
			}

			if (!get_aim_dir(&dir)) return NULL;

			msg_print(_("あなたの頭は螺旋を描いて飛んだ！", "Your heads fly forth in a spiral pattern!"));
			delete_monster_idx(m_idx1);
			delete_monster_idx(m_idx2);
			fire_spark(GF_BANKI_BEAM2, dir, damage, 1);

		}
		break;

	case 6:
	{
		int i;
		int dam = plev * 200;
		int cnt=0;
		if (dam > 9999) dam = 9999;
		if (only_info) return format(_str_eff_dam, dam);

		for (i = 1; i < m_max; i++)
		{
			monster_type *m_ptr = &m_list[i];
			if (!m_ptr->r_idx) continue;
			if (!m_ptr->cdis) continue;
			if (!projectable(m_ptr->fy, m_ptr->fx, py, px)) continue;
			if ((m_ptr->r_idx == MON_BANKI_HEAD_1) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			if ((m_ptr->r_idx == MON_BANKI_HEAD_2) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
		}


		msg_print(_("破滅の宣告がダンジョンに響き渡った。",
                    "Your proclamation of doom echoes through the dungeon."));
		for (i = 1 + cnt / 3; i > 0; i--)
		{
			deathray_monsters();
		}

	}
	break;



	case 7://ヘルズレイ
		{
			int damage =  10 + plev/5 + chr_adj;
			int num = 2;
			int dir;
			if(plev > 47) num++;
			if(only_info) return format(_("損傷:%d*%d", "dam: %d*%d"),damage,num);
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			msg_print(_("恐ろしい眼光が放たれた！",
                        "You fire terrifying beams from your eyes!"));
			for(;num>0;num--)
			{
				int j;
				fire_beam(GF_BANKI_BEAM2,dir,damage);
				for (j = 1; j < m_max; j++)
				{
					int tx,ty;
					monster_type *m_ptr;
					m_ptr = &m_list[j];
					if(!target_okay()) break;
					if((m_ptr->r_idx != MON_BANKI_HEAD_1) && (m_ptr->r_idx != MON_BANKI_HEAD_2)) continue;
					if(!projectable(m_ptr->fy,m_ptr->fx,target_row,target_col)) continue;
					do
					{
						tx = target_col + randint0(3) - 1;
						ty = target_row + randint0(3) - 1;
					}while(!in_bounds2(ty,tx));

					project(j,0,ty,tx,damage,GF_BANKI_BEAM2,(PROJECT_BEAM | PROJECT_KILL | PROJECT_THRU),-1);
				}
				if(!target_okay()) break;


			}

		}
		break;

	default:
		if (only_info) return "";
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



