cptr do_cmd_class_power_aux_aya(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int dam = 50 + plev * 2 + chr_adj * 5;
			int rad = 1;
			if(plev > 29) rad = 2;

			if(only_info) return format(_("損傷:%d 範囲:%d", "dam: %d rad: %d"),dam/2,rad);
			msg_print(_("鎌鼬を起こした！", "You summon blades of wind!"));
			project(0, rad, py, px, dam, GF_WINDCUTTER, PROJECT_KILL|PROJECT_GRID , -1);

		}
		break;
	case 1:
		{
			int rad = 2 + plev / 20;
			int damage = plev + adj_general[p_ptr->stat_ind[A_CHR]] * 3;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_print(_("上空から裂風を放った！", "Intense winds blow down from above!"));
			cast_meteor(damage, rad, GF_TORNADO);

			break;
		}
	case 2:
		{
			int damage = 50 + p_ptr->lev * 2 + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("竜巻が前方を薙ぎ倒した！", "A tornado sweeps forth in front of you!"));
			fire_spark(GF_TORNADO,dir,damage,2);
			break;
		}
	case 3://レイシャルの「突進」のルーチンを少し改変
		{
			int dir;
			int tx, ty;
			u16b path_g[32];
			int path_n, i;
			bool moved = FALSE;
			int damage = plev * 3;
			project_length = MAX(5,plev / 3);
			if(damage < 100) damage=100;
			if(only_info) return format(_("損傷:%d 射程:%d", "dam: %d rng: %d"),damage/2,project_length);

			if (!get_aim_dir(&dir)) return NULL;
			tx = px + project_length * ddx[dir];
			ty = py + project_length * ddy[dir];
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			path_n = project_path(path_g, project_length, py, px, ty, tx, PROJECT_THRU | PROJECT_KILL);
			project_length = 0;
			if (!path_n) return NULL;
			if (in_bounds(ty, tx)) msg_format(_("あなたは風を纏って突進した！", "You envelop yourself in wind and rush forward!"));
			ty = py;
			tx = px;

			/* Project along the path */
			for (i = 0; i < path_n; i++)
			{
				monster_type *m_ptr;
				monster_race *r_ptr;
				char m_name[80];
				bool stop = FALSE;

				int ny = GRID_Y(path_g[i]);
				int nx = GRID_X(path_g[i]);

				if (cave[ny][nx].m_idx)
				{
					m_ptr = &m_list[cave[ny][nx].m_idx];
					r_ptr = &r_info[m_ptr->r_idx];
					monster_desc(m_name, m_ptr, 0);

					if(r_ptr->flags2 & RF2_GIGANTIC)
					{
						msg_format(_("%sに突進を止められた！", "You were stopped by %s!"),m_name);
						(void)project(0, 1, ny, nx, damage, GF_TORNADO, PROJECT_JUMP|PROJECT_KILL|PROJECT_GRID, -1);
						break;
					}
					else
					{
						(void)project(0, 1, ny, nx, damage, GF_TORNADO, PROJECT_JUMP|PROJECT_KILL|PROJECT_GRID, -1);
					}

				}
				if (cave_empty_bold(ny, nx) && player_can_enter(cave[ny][nx].feat, 0))
				{
					ty = ny;
					tx = nx;
					if (!player_bold(ty, tx)) teleport_player_to(ty, tx, TELEPORT_NONMAGICAL);
				}
				else
				{
					msg_print(_("ここには入れない。", "You can't go here."));
					break;
				}
			}
		}
		break;
	case 4:
		{
			int dam = 100 + plev * 6 + chr_adj * 20;
			int rad = 4 + plev / 15;

			if(only_info) return format(_("損傷:%d 範囲:%d", "dam: %d rad: %d"),dam/2,rad);
			msg_print(_("巨大な竜巻を起こした！", "You cause a massive tornado!"));
			project(0, rad, py, px, dam, GF_TORNADO, PROJECT_KILL , -1);
		}
		break;
	case 5:
		{
			if(only_info) return format(_("期間:10ターン", "dur: 10 turns"));
			set_lightspeed(10,FALSE);
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




