cptr do_cmd_class_power_aux_patchouli(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0: //ドヨースピア
		{
			int dist = 3 + plev / 15;
			dice = 8 + plev / 3;
			sides = 5 + chr_adj / 5;
			if(only_info) return format(_str_eff_dam_dice_sides_range,dice,sides,dist);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_MISSILE, dir, damroll(dice, sides),0,_("地面から石錐が飛び出した！", "Stone pillars rise from the ground!"))) return NULL;
			break;
		}
	case 1: //プリンセスウンディネ
		{
			int num = 3 + plev / 20;
			dice = 3 + plev / 6;
			sides = 5 + chr_adj / 5;
			if(only_info) return format(_str_eff_dam_dice_sides_mult,dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("水の矢を放った！", "You fire arrows of water!"));
			for (i = 0; i < num; i++) fire_bolt(GF_WATER, dir, damroll(dice, sides));
			break;

		}
	case 2://エレメンタルハーベスター
		{
			if(only_info) return format(_("期間:20+1d20", "dur: 20+1d20"));
			msg_format(_("回転する刃が現れた。", "Rotating blades surround you."));
			set_dustrobe(20+randint1(20),FALSE);
			break;
		}
	case 3://セントエルモピラー
		{
			int rad = 3;
			if(p_ptr->lev > 39) rad = 4;
			dice = 8 + p_ptr->lev / 4;
			sides = 10 + chr_adj / 3 ;
			base = p_ptr->lev*4;
			if(base < 100) base = 100;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_PLASMA, dir, base + damroll(dice,sides), rad,
                      _("火花を散らす光の柱が立ち昇った！", "A pillar of light rises and starts shooting sparks!"))) return NULL;
			break;
		}

	case 4: //メタルファティーグ
		{
			int power = 150 + plev * 10 + chr_adj * 5;
			if (only_info) return format(_("効力:〜%d", "pow: ~%d"), power / 2);

			msg_print(_("周囲の物質を急速に腐蝕させた！", "You rapidly corrode materials in your vicinity!"));

			project(0, 8, py, px, power, GF_DEC_ATK, PROJECT_JUMP | PROJECT_KILL, -1);
			project(0, 8, py, px, power, GF_DEC_DEF, PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL, -1);

			break;
		}



	case 5: //ラーヴァクロムレク
		{
			int rad = 3;
			base = p_ptr->lev*5 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,base);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_LAVA_FLOW, dir, rad, rad,_("溶岩が噴出した！", "Lava gushes forth!"))) return NULL;
			fire_ball_jump(GF_FIRE, dir, base, rad,NULL);
			break;

		}
		break;

	case 6: //スティッキーバブル
		{
			int power = plev / 4;

			if (only_info) return format(_str_eff_power, power);

			project_length = 5;

			if (!get_aim_dir(&dir)) return NULL;

			msg_print(_("粘つく泡を吹き付けた。", "You blow a sticky bubble."));
			fire_ball(GF_NO_MOVE, dir, power, -2);
			break;
		}

	case 7: //サイレントセレナ
		{
			int rad = 1;
			base = p_ptr->lev * 10 + chr_adj * 20;
			if(base < 600) base = 600;
			if(only_info) return format(_str_eff_dam,base / 2);
			msg_format(_("月の光が降り注いだ。", "Moonlight pours down around you."));
			project(0, rad, py, px, base, GF_DARK, PROJECT_KILL , -1);
			break;
		}

	case 8: //v1.1.75 同時詠唱
		{
			if (only_info) return format("");

			if (!can_do_cmd_cast()) return NULL;
			do_cmd_cast();
			handle_stuff();
			if (can_do_cmd_cast())
				do_cmd_cast();

			break;
		}


	case 9://ロイヤルフレア MAX500+1000 魅力47で570+1350辺り
		{
			base = p_ptr->lev * 14 + chr_adj * 10;
			dice = p_ptr->lev ;
			sides = 10 + chr_adj ;

			if(only_info) return format(_("損傷:〜%d+%dd%d ", "dam: ~%d+%dd%d"),base/2 ,dice,sides);
			msg_format(_("強烈な閃光と衝撃が周囲を蹂躙した！",
                        "Intense light and shockwaves spread around you!"));
			project(0, 7, py, px, base, GF_SOUND, PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM , -1);
			project_hack2(GF_NUKE,dice,sides,0);

			break;
		}
	case 10://賢者の石
		{
			int max_num = 5;
			int i,cnt=0;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(only_info) return format(_("最大召喚可能数:%d", "max summon: %d"),max_num);

			for (i = 1; i < m_max; i++)
			{
				int dist_tmp;
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_PHILOSOPHER_STONE) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_num)
			{
				msg_format(_("これ以上配置できない。", "You can't place any more."),num);
				return NULL;
			}

			if(summon_named_creature(0, py, px, MON_PHILOSOPHER_STONE, PM_EPHEMERA)) msg_print(_("賢者の石を呼び出した！",
                                                                                                "You call forth a Philosopher Stone!"));
			else msg_print(_("魔法に失敗した。", "Your spell failed."));

		break;
		}
	default:


		if (only_info) return format(_str_unimp);

		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




