cptr do_cmd_class_power_aux_yoshika(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			int i;
			bool flag = FALSE;
			int num = p_ptr->lev / 14;
			if(only_info) return "";
			for(i=0;i<num;i++)
			{
				if (summon_specific(-1, py, px, p_ptr->lev, SUMMON_DESIRE, PM_ALLOW_GROUP | PM_FORCE_FRIENDLY)) flag = TRUE;
			}
			if(flag)
				msg_print(_("小神霊が現れた！", "Small divine spirits appear!"));
			else
				msg_print(_("小神霊は現れなかった。", "No small divine spirits have appeared."));

		}
		break;
	case 1:
		{
			int heal = 100 + plev * 3;
			if(only_info) return format(_str_eff_heal,heal);

			msg_print(_("主が現れ、あなたの体の修復を始めた・・",
                        "Your mistress appear and starts fixing your body..."));
			hp_player(heal);
			set_stun(0);
			set_cut(0);
			set_poisoned(0);
			set_image(0);
			restore_level();
			set_alcohol(0);
			set_asthma(0);
			msg_print(_("主は再びどこかへ行った。",
                        "Your mistress once again goes away somewhere."));

		}
		break;
	case 2:
		{
			int x,y;
			cave_type       *c_ptr;
			monster_type    *m_ptr;
			monster_race	*r_ptr;
			if(only_info) return format("");

			if(!p_ptr->do_martialarts)
			{
				msg_print(_("素手でないとその技は使えない。", "You cannot use this ability if you're holding something."));
				return NULL;
			}

			for (dir = 0; dir < 8; dir++)
			{
				y = py + ddy_ddd[dir];
				x = px + ddx_ddd[dir];
				c_ptr = &cave[y][x];

				m_ptr = &m_list[c_ptr->m_idx];
				/*:::不可視のモンスターも攻撃するが、壁の中にいる不可視のモンスターは攻撃しない*/
				if (c_ptr->m_idx && (m_ptr->ml || cave_have_flag_bold(y, x, FF_PROJECT)))
				{
					py_attack(y, x, HISSATSU_YOSHIKA);

				}
			}
		}
		break;
	case 3:
		{
			int base = 10;
			int time;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_print(_("主が現れ、あなたの体に何やら剣呑な術を施した！",
                        "Your mistress appears and performs some technique on your body!"));
			set_fast(time,FALSE);
			set_shero(time,FALSE);
			set_tim_addstat(A_STR,105,time,FALSE);
			set_tim_addstat(A_CON,105,time,FALSE);
			msg_print(_("主は愉しげに帰っていった。",
                        "Your mistress happily returns."));

			break;
		}


	default:
			if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

