cptr do_cmd_class_power_aux_letty(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];


	switch(num)
	{

	case 0:
		{
			int dice = 3 + plev / 4;
			int sides = 6 + chr_adj / 5;
			if (WINTER_LETTY1) sides = sides * 3 / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("あなたは冷気の塊を放った！", "You fire a lump of frost!"));
			fire_bolt_or_beam(plev * 2, GF_COLD, dir, damroll(dice, sides));
		}
		break;
	case 1: //リンガリングコールド　視界内の寝ているモンスターをより深く眠らせる
		{
			int i;
			if(only_info) return format("");
			msg_print(_("部屋に寒気が染み渡った・・", "Chilling air permeates the room..."));
			for (i = 1; i < m_max; i++)
			{
				char m_name[80];

				int pow = plev * 3  + 50;
				int sleep;
				monster_type *m_ptr = &m_list[i];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				if (WINTER_LETTY1) pow += 100;

				if (!m_ptr->r_idx) continue;
				if(!projectable(py, px, m_ptr->fy, m_ptr->fx)) continue;
				sleep = MON_CSLEEP(m_ptr);
				if(!sleep) continue;

				monster_desc(m_name, m_ptr, 0);
				if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) pow /= 2;
				if(!monster_living(r_ptr)) pow /= 2;
				if(!(WINTER_LETTY2) && r_ptr->flagsr & RFR_IM_COLD ) pow /= 3; //v1.1.85 吹雪内で耐性貫通
				if(r_ptr->flags2 & RF2_POWERFUL) pow /= 2;
				(void)set_monster_csleep(i, sleep + pow);
				msg_format(_("%^sの眠りが深くなったようだ。", "%^s looks to be in a deeper sleep."), m_name);
			}


		}
		break;
	case 2://フラワーウィザラウェイ
		{
			int dam = plev * 2 + chr_adj * 5;
			if(dam < 50) dam = 50;

			if (WINTER_LETTY1) dam += plev * 2;

			if(only_info) return format(_str_eff_dam,dam);
			if(use_itemcard)
				msg_print(_("吹雪が巻き起こった！", "A blizzard forms!"));
			else
				msg_print(_("あなたは吹雪を巻き起こした！", "You cause a blizzard!"));

			project_hack2(GF_COLD, 0,0,dam);
		}

		break;

	case 3://アンデュレイションレイ
		{
			int power = plev * 3 + chr_adj * 5;
			int typ;
			if(power < 80) power = 80;

			if(only_info) return format(_str_eff_power,power);

			if (!get_aim_dir(&dir)) return NULL;
			if(plev > 34 || use_itemcard)
			{
				typ = GF_STASIS;
				msg_format(_("青白く輝く光線が放たれた！",
                            "You fire a pale blue beam of light!"));
			}
			else
			{
				typ = GF_OLD_SLEEP;
				msg_format(_("白く輝く光線が放たれた！",
                            "You fire a white beam of light!"));
			}
			fire_beam(typ, dir, power);
			break;
		}
	case 4://ノーザンウィナー
		{
			int base = chr_adj * 5;
			int dice = plev / 2;
			int sides = 10;
			int y, x;
			monster_type *m_ptr;
			if (WINTER_LETTY1) base *= 2;
			if(only_info) return format(_str_eff_dam_dice_sides_base_,dice,sides,base);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];
			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				int flg = PROJECT_STOP | PROJECT_KILL | PROJECT_JUMP;
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("あなたは%sに触れ、ゆっくりと寒気を吹き込んだ・・",
                            "You touch %s, slowly breathing chilling air..."),m_name);
				project(0, 0, y, x, damroll(dice,sides) + base, GF_NORTHERN, flg, -1);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;
		}
	case 5://コールドスナップ
		{
			if (WINTER_LETTY1)
			{
				int base = 50 + plev * 2 + chr_adj * 5;
				if (only_info) return format(_str_eff_dam, base);
				msg_print(_("吹雪が猛烈に激しさを増した！", "The blizzard drastically intensifies!"));
				project_hack2(GF_ICE, 0, 0, base);
				break;

			}
			else
			{
				int base = 100 + plev * 3 + chr_adj * 5;
				int rad = 2 + plev / 10;
				if (only_info) return format(_("損傷:最大%d", "dam: up to %d"), base / 2);
				msg_print(_("周囲の温度が急激に低下した！",
                            "Temperature around you drops sharply!"));
				project(0, rad, py, px, base, GF_ICE, PROJECT_KILL | PROJECT_ITEM, -1);
				break;

			}
		}

	case 6://テーブルターニング
		{
			int base = plev*3 + chr_adj * 10;
			int y, x;
			monster_type *m_ptr;
			if (WINTER_LETTY1) base = base * 3 / 2;
			if(only_info) return format(_str_eff_dam,base);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];
			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				int flg = PROJECT_STOP | PROJECT_KILL | PROJECT_JUMP;
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("謎の重圧が%sを押し潰した！",
                            "%s is crushed by some mysterious pressure!"),m_name);
				project(0, 0, y, x, base, GF_INACT, flg, -1);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

