cptr do_cmd_class_power_aux_nazrin(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;

	switch(num)
	{

	case 0:
		{
			if(only_info) return "";
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			detect_treasure(DETECT_RAD_DEFAULT);
			detect_objects_gold(DETECT_RAD_DEFAULT);
			break;
		}
	case 1:
		{
			if(only_info) return "";
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			detect_objects_normal(DETECT_RAD_DEFAULT);
			break;
		}

	case 2:
		{
			if(only_info) return "";
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			detect_traps(DETECT_RAD_DEFAULT, TRUE);
			break;
		}
	case 3:
		{
			if(only_info) return "";
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			(void)detect_monsters_normal(DETECT_RAD_DEFAULT);
			(void)detect_monsters_invis(DETECT_RAD_DEFAULT);
			break;
		}
	case 4:
		{
			int max = 2 + p_ptr->lev / 6;
			if(only_info) return format(_("最大:%d体", "up to %d"),max);

			if(use_itemcard)
				msg_print(_("どこからともなく妖怪鼠たちが現れた。", "Youkai rats appear out of nowhere."));
			else
				msg_print(_("あなたは子ネズミを呼び出した。", "You summon small rats."));

			for(i=0;i<max;i++) (void)summon_named_creature(0, py, px, MON_YOUKAI_MOUSE, PM_FORCE_PET);
			break;
		}
	//高級品感知
	case 5:
		{
			if(only_info) return format("");
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			search_specific_object(1);
			break;
		}
	case 6:
		{
			if(only_info) return "";
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			map_area(DETECT_RAD_MAP);
			break;
		}
	case 7:
		{
			if(only_info) return format("");
			if(!hit_and_away()) return NULL;
			break;
		}
	//素材感知
	case 8:
		{
			if(only_info) return format("");
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			search_specific_object(3);
			break;
		}
	case 9: //鑑識
		{
			if(only_info) return format("");
			if (!ident_spell(FALSE)) return NULL;
			break;
		}
	case 10://全感知
		{
			if(only_info) return  format("");
			msg_print(_("あなたはダウジングを始めた・・", "You start dowsing..."));
			detect_all(DETECT_RAD_DEFAULT);
			break;
		}
	case 11: //v1.1.59 ペンデュラムガード
		{
			int base = 20;
			if (only_info) return format(_str_eff_dur, base, base);

			msg_print(_("あなたはペンデュラムを回し始めた・・",
                        "You start rotating your pendulum."));
			update_dungeon_feeling(TRUE);
			//do_cmd_feeling_text[]参照
			if (p_ptr->feeling >= 2 && p_ptr->feeling <= 4)
			{
				msg_print(_("ペンデュラムが妖気に反応して防壁になった！",
                            "The pendulum formed a barrier in response to the aura of malice!"));
				set_magicdef((base + randint1(base)), FALSE);
			}

		}
		break;
	//伝説の品感知
	case 12:
		{
			if(only_info) return format("");
			msg_print(_("あなたは伝説的な存在を頭に思い浮かべてダウジングを始めた・・",
                        "You start dowsing while thinking of legends..."));
			search_specific_object(2);
			break;
		}
	//全感知・改
	case 13:
		{
			int base = p_ptr->lev/2;
			if(only_info) return format(_str_eff_dur,base,base);

			msg_format(_("あなたは大量の子鼠を周囲に放った・・",
                        "You spread multitude of rats around..."));
			set_radar_sense(randint1(base) + base, FALSE);
			break;
		}
	case 14: //フロア探知
		{
			if(only_info) return format("");
			msg_format(_("あなたは地中に住むネズミ達から情報を集めた・・",
                        "You gather information from rats living below the ground..."));
			wiz_lite(FALSE);
			break;
		}
	case 15:
		{
			damage = p_ptr->lev * 4 + adj_general[p_ptr->stat_ind[A_WIS]] * 5 + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("毘沙門天の宝塔から眩い光が放たれた！",
                        "Dazzling light shoots out from Bishamonten's Pagoda!"));
			fire_spark(GF_HOLY_FIRE,dir,damage,1);

			break;
		}
	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


