cptr do_cmd_class_power_aux_orin(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!put_item_into_inven2()) return NULL;
			break;

		}
	case 1:
		{
			if(only_info) return format("");
			display_list_inven2();
			return NULL; //見るだけなので行動順消費なし

		}
	case 2:
		{
			if(only_info) return format("");
			if(!takeout_inven2()) return NULL;
			break;
		}
	case 3:
		{
			if(only_info) return format(_("期間:最長1000ターン", "dur: up to 1000 turns"));
			if(!set_mimic(1000, MIMIC_CAT, FALSE)) return NULL;

			break;
		}

	case 4:
		{
			bool flag = FALSE;
			int i;
			int dice = 2 + plev / 5;
			int sides = 4 + adj_general[p_ptr->stat_ind[A_CHR]] / 10;
			int num = 3 + plev / 4;
			if(only_info) return format(_("損傷:(%dd%d) * %d", "dam: (%dd%d) * %d"),dice,sides,num);
			msg_print(_("地獄の針を放った！", "You fire needles of hell!"));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_NETHER, damroll(dice,sides),1, 0,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_str_mess_cant_find_enemies);
					break;
				}
			}
		}
		break;

	case 5:
		{
			int slev = p_ptr->lev;
			int k;
			if(slev < 20) slev=20;
			if(only_info) return format("");
			if (summon_specific(-1, py, px, slev, SUMMON_ZOMBIE_FAIRY, (PM_ALLOW_GROUP | PM_FORCE_PET)))
				msg_print(_("ゾンビ姿の妖精たちを呼び出した！", "You call fairies dressed up as zombies!"));

			break;
		}

	case 6:
		{
			int rad = 0;
			bool flag = FALSE;
			//int num;
			int i;
			int power = p_ptr->lev * 2 + adj_general[p_ptr->stat_ind[A_CHR]] * 2;
			//num = p_ptr->lev / 10 + adj_general[p_ptr->stat_ind[A_CHR]] / 5;

			if(power < 50) power = 50;
			//if(num < 4) num = 4;

			if(only_info) return format(_str_eff_power,power);

			if(use_itemcard)
				msg_format(_("カードから大量の怨霊が湧きだしてきた！", "Many vengeful spirits pour out of the card!"));
			else
				msg_format(_("あなたは大量の怨霊を放った！", "You release many vengeful spirits!"));

			project_hack2(GF_POSSESSION, 0, 0, power);
			/*
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_POSSESSION, power,3, rad,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format("しかし敵が見当たらなかった。");
					break;
				}
			}
			*/
			break;
		}
	case 7:
		{
			dice = 16;
			sides = 20 + adj_general[p_ptr->stat_ind[A_CHR]] ;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("地獄の火炎が放たれた！", "You unleash the fires of hell!"));
			project_hack2(GF_HELL_FIRE,dice,sides,0);
			break;
		}


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



