cptr do_cmd_class_power_aux_cirno(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	//v1.1.32 日焼けしたチルノの特技パワーアップ
	if(is_special_seikaku(SEIKAKU_SPECIAL_CIRNO))
	{
		plev += 10;
		chr_adj += 10;
		if(chr_adj > 50) chr_adj = 50;
	}

	switch(num)
	{

	//アイシクルシュート
	case 0:
		{
			int i;
			int dice = 2 + plev / 7;
			int dir;
			int sides = 3 + chr_adj / 15;
			int num = MIN(10,(1 + plev / 4));
			if(only_info) return format(_("損傷:(%dd%d) * %d", "dam: (%dd%d) * %d"),dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("氷の弾を放った！", "You fire ice bullets!"));
			for (i = 0; i < num; i++) fire_bolt(GF_COLD, dir, damroll(dice, sides));

		}
		break;

	//フローズン冷凍法
	case 1:
		{

			int y, x;
			int dist;
			monster_type *m_ptr;
			monster_race *r_ptr;
			int dir, damage;

			damage=plev * 2 + chr_adj * 3 ;
			if(damage < 15) damage=15;

			if(only_info) return  format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if(dir==5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);
				if(use_itemcard)
					msg_format(_("%sに触って氷漬けにした！", "You touch and freeze %s!"),m_name);
				else
					msg_format(_("%sは突然氷漬けになった！", "%s is suddenly covered in ice!"),m_name);
				project(0,0,y,x,damage,GF_ICE,PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID,0);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
		}
		break;
	//冷凍光線
	case 2:
		{
			int dir;
			int dice = 2 + p_ptr->lev / 5;
			int sides = 10 + chr_adj / 5;
			if(only_info) return format(_("損傷:(%dd%d) * 3", "dam: (%dd%d) * 3"),dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("冷気のビームを放った！", "You fire beams of cold!"));
			fire_blast(GF_COLD, dir, dice, sides, 3, 4,(PROJECT_BEAM | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM));
			break;

		}
		break;
	//フェアリースピン
	case 3:
		{
			int base = 25 + plev + chr_adj * 2;
			int num = 2 + plev / 30 + chr_adj / 20;
			int rad = 2 + plev / 30;
			int i;
			if(only_info) return format(_("損傷:最大%d * %d", "dam: up to %d * %d"),base/2,num);
			msg_print(_("氷の弾を放ちながら回転した。", "You spin around while scattering ice bullets."));
			for (i = 0; i < num; i++) project(0, rad, py, px, base, GF_COLD, PROJECT_KILL | PROJECT_ITEM, -1);
			break;

		}
		break;

	//フロストピラーズ
	case 4:
		{
			if(only_info) return format(_("損傷:%d + 7d7", "dam: %d + 7d7"),plev);
			msg_print(_("足元に氷柱を仕込んだ・・", "You set up an ice pillar beneath your feet..."));
			explosive_rune();
		}
		break;

	//ソードフリーザー
	case 5:
		{
			int base = p_ptr->lev/2;

			if(base < 10) base = 10;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			if (!buki_motteruka(INVEN_RARM) && !buki_motteruka(INVEN_LARM))
			{
				msg_print(_("武器を持っていないとそれを使えない。", "You cannot use this ability without wielding a weapon."));
				return NULL;
			}

			msg_format(_("手にしている武器が氷に覆われた。", "The weapon in your hand becomes covered in frost."));
			set_ele_attack(ATTACK_COLD, base + randint1(base));
			break;
		}
		break;
	//アイシクルボム
	case 6:
		{
			int dir;
			int rad = 3;
			int dice = p_ptr->lev / 4 + chr_adj / 3;
			int sides = 10 + p_ptr->lev / 5;
			int base = p_ptr->lev * 2;
			if(p_ptr->lev > 39) rad = 4;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("巨大な氷塊を放った！", "You launch a huge lump of ice!"));
			fire_rocket(GF_ICE, dir, base + damroll(dice,sides), rad);
			break;
		}
		//v1.1.68 氷塊生成
	case 7:
		{
		int y, x,dir;

		if (only_info) return format("");
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];

		if (!cave_naked_bold(y, x))
		{
			msg_print(_("この場所には特技を使えない。", "You cannot use this ability here."));
			return NULL;
		}
		msg_print(_("あなたは強固な氷を作り出した。", "You create a solid block of ice."));
		cave_set_feat(y, x, f_tag_to_index_in_init("ICE_WALL"));

		break;
		}

	//瞬間冷凍ビーム
	case 8:
		{
			int dir;
			int base = p_ptr->chp / 3;
			if(base<1) base = 1;
			if(base > 1600) base=1600;
			if(only_info) return format(_str_eff_dam,base);
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから強烈な冷気が放たれた！", "Intense frost shoots out from the card!"));
			else
				msg_print(_("全力の冷凍ビームを放った！", "You shoot a freezing beam with all of your might!"));
			fire_spark(GF_ICE,dir,base,1);
			break;

		}
	//アイシクルマシンガン
	case 9:
		{
			int i;
			int dice = 2 + plev / 8;
			int dir;
			int sides = 3  + chr_adj / 20;
			int num = MIN(30,(10 + plev / 5 + chr_adj / 3));
			if(only_info) return format(_("損傷:(%dd%d) * %d", "dam: (%dd%d) * %d"),dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから大量の氷塊が放たれた！", "Many shards of ice are launched out of the card!"));
			else
				msg_print(_("大量の氷弾幕を放った！", "You shoot many ice bullets!"));
			fire_blast(GF_COLD, dir, dice, sides, num, 4, 0);
			break;
		}

	//グレートクラッシャー
	case 10:
		{
			int y, x;
			int dist;
			int dir;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage=plev * 4 + adj_general[p_ptr->stat_ind[A_STR]] * 10 / 3 + chr_adj * 10 / 3  ;

			if(only_info) return  format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if(dir==5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				if(!(r_ptr->flagsr & RFR_IM_COLD)) damage = damage * 3 / 2;
				if(r_ptr->flagsr & RFR_HURT_COLD) damage *= 2;
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("巨大な氷のハンマーを%sに叩きつけた！", "You slam %s with a huge ice hammer!"),m_name);
				project(0,0,y,x,damage,GF_MISSILE,PROJECT_KILL,0);
				if(cave[y][x].m_idx && !(r_ptr->flags3 & RF3_NO_STUN))
				{
					msg_format(_("%sはフラフラになった。", "%s looks dazed."),m_name);
					(void)set_monster_stunned(cave[y][x].m_idx,MON_STUNNED(m_ptr)+ 4 + randint0(4) );
				}
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;

		}
	//フロストキング
	case 11:
		{
			int max_ice = 2;
			int i,cnt=0;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(plev > 44) max_ice += (plev - 40) / 5;
			if(max_ice > 5) max_ice = 6;
			if(only_info) return format(_("最大:%d", "up to %d"),max_ice);

			for (i = 1; i < m_max; i++)
			{
				int dist_tmp;
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_CIRNO_ICE) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_ice)
			{
				msg_format(_("これ以上配置できない。", "You cannot place any more crystals."),num);
				return NULL;
			}

			(void)summon_named_creature(0, py, px, MON_CIRNO_ICE, PM_EPHEMERA);

		}
		break;
	//パーフェクトフリーズ
	case 12:
		{
			int dam = plev * 4 + chr_adj * 10;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("一瞬、周囲の全ての物質が活動を停止した・・",
                        "For a moment, everything around you stops moving..."));
			project_hack2(GF_ICE, 0,0,dam);
		}

		break;


	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

/*:::巫女・神官専用技*/
class_power_type class_power_priest[] =
{

	{1,5,0,FALSE,FALSE,A_WIS,0,0,_("奉納", "Offering"),
		_("神にアイテムを捧げて祈る。アイテムの価値とプレイヤーのレベルに応じて経験値を得られる。酒を捧げると価格の割に得られる経験値が多い。あまり変なものを捧げると罰を受けることがある。",
        "Pray and offer an item to your god. Grants experience depending on item value and player level. Offering alcohol will give more experience for their value. You might receive punishment if you offer something weird.")},
	{30,60,90,FALSE,TRUE,A_WIS,0,0,_("祝福", "Blessing"),
		_("武器一つに対し祝福を与える。",
        "Makes one weapon blessed.")},
	{99,0,0,FALSE,FALSE,0,0,0,"dummy",""},
};

cptr do_cmd_class_power_aux_priest(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;

	switch(num)
	{
		//奉納
		case 0:
		{
			int item;
			int amt;
			s32b price, value;
			bool goodrealm = is_good_realm(p_ptr->realm1);
			int exp;
			u32b flgs[TR_FLAG_SIZE];
			object_type forge;
			object_type *q_ptr;
			object_type *o_ptr;
			bool baditem = FALSE;
			cptr q, s;
			char o_name[MAX_NLEN];

			if(only_info) return format("");

			q = _("どのアイテムを神に捧げますか? ", "Offer which item");
			s = _("神に捧げられそうなアイテムを持っていない。", "You don't have any items to offer.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;

			if (item >= 0)	o_ptr = &inventory[item];
			else	o_ptr = &o_list[0 - item];

			amt = 1;
			if (o_ptr->number > 1)
			{
				amt = get_quantity(NULL, o_ptr->number);
				if (amt <= 0) return NULL;
			}

			q_ptr = &forge;
			object_copy(q_ptr, o_ptr);
			q_ptr->number = amt;
			if ((o_ptr->tval == TV_ROD) || (o_ptr->tval == TV_WAND)) q_ptr->pval = o_ptr->pval * amt / o_ptr->number;
			object_desc(o_name, q_ptr, 0);

			if(!get_check(format(_("%sを捧げますか？", "Offer %s?"),o_name))) return NULL;

			if(goodrealm)
				msg_format(_("%sは天に昇って消えた。", "%s rises to the sky and vanishes."),o_name);
			else
				msg_format(_("%sは溶け崩れて地面へ消えた。", "%s melts down into the ground."),o_name);
			if (item >= 0)
			{
				inven_item_increase(item, -amt);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -amt);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}
			object_flags(q_ptr,flgs);
			value = object_value_real(q_ptr) * q_ptr->number;
			exp = value * 10 / (100 - p_ptr->lev);
			/*:::神様は酒が好き*/
			if(q_ptr->tval == TV_ALCOHOL) exp *= 5;
			//アーティファクトは経験値が高い
			if(object_is_artifact(q_ptr)) exp *= 3;
			/*:::変なアイテムを捧げると怒る*/
			if( !goodrealm && have_flag(flgs, TR_BLESSED) ) baditem = TRUE;
			else if((q_ptr->curse_flags & TRC_HEAVY_CURSE) || (q_ptr->curse_flags & TRC_PERMA_CURSE)) baditem = TRUE;
			else if(q_ptr->tval == TV_ALCOHOL && q_ptr->sval == SV_ALCOHOL_MARISA) baditem = TRUE;
			else if(q_ptr->tval == TV_CHEST && !q_ptr->pval) baditem = TRUE;

			else if( !value && one_in_(13)) baditem = TRUE;

			if( !exp  ||  baditem)
			{
				if(goodrealm)
					msg_format(_("・・と思ったら天から落ちてきた。", "...but it falls back out of the sky."));
				else
					msg_format(_("・・と思ったら地面から吐き出された。", "...but it gets expelled out of the ground."));
				(void)drop_near(q_ptr, -1, py, px);
			}

			if( baditem )
			{
				bool stop_ty = FALSE;
				int count = 0;
				msg_format(_("あなたは神の怒りに触れてしまったようだ！", "Your god's wrath is upon you!"));
				do
				{
					stop_ty = activate_ty_curse(stop_ty, &count);
				}
				while (one_in_(6));
			}
			else if(exp)
			{
				msg_print(_("神の加護を感じる・・・", "You feel divine protection..."));

				if(p_ptr->prace == RACE_ANDROID)
				{
					if(randint0(30000) < exp) acquirement(py,px,3,TRUE,FALSE);
					else if(randint0(10000) < exp) acquirement(py,px,1,TRUE,FALSE);
				}
				else
				{
					gain_exp(exp);
				}
			}

		}
		break;
		case 1:
		{
			if(only_info) return format("");
			if (!bless_weapon()) return NULL;
		}
		break;

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


