cptr do_cmd_class_power_aux_seiga(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;

	switch(num)
	{

	case 0:
		{

			int x, y;
			cave_type *c_ptr;
			feature_type *f_ptr, *mimic_f_ptr;
			if(only_info) return format("");

			if (!get_rep_dir2(&dir)) return NULL;
			y = py + ddy[dir];
			x = px + ddx[dir];
			c_ptr = &cave[y][x];
			f_ptr = &f_info[c_ptr->feat];
			if (have_flag(f_ptr->flags, FF_HURT_ROCK))
			{
				msg_format(_("%sに穴を開けた。", "You open a hole in %s."), f_name + f_info[get_feat_mimic(c_ptr)].name);
				cave_alter_feat(y, x, FF_HURT_ROCK);
				p_ptr->update |= (PU_FLOW);
			}
			else
			{
				msg_print(_("そこには穴を開けられない。", "You can't open a hole there."));
				return NULL;
			}

			break;

		}
	case 1:
		{
			if(only_info) return format("");

			if(!dun_level)
			{
				msg_print(_("ダンジョンの中でないとこの技は使えない。", "You can't use this ability outside dungeon."));
				return NULL;

			}
			else if(quest_number(dun_level))
			{
				msg_print(_("・・？なぜか床に穴が開かない。", "...? For some reason, you can't dig a hole in the floor here."));
				return NULL;
			}
			else if(dun_level == d_info[dungeon_type].maxdepth)
			{
				msg_print(_("ここは既にダンジョンの最深層のようだ。", "You already are on the lowest level in this dungeon."));
				return NULL;
			}
			else
			{
				msg_print(_("あなたは地面に穴を開け、下のフロアへと降りて行った・・",
                            "You open a hole in the floor and descend to the level below..."));
				p_ptr->leaving = TRUE;
				prepare_change_floor_mode(CFM_SAVE_FLOORS | CFM_DOWN | CFM_RAND_PLACE | CFM_RAND_CONNECT);
			}
			break;
		}
	case 2:
		{
			if(only_info) return format("");
			if (!word_of_recall()) return NULL;
		}
		break;
	case 3:
		{
			int i,cnt=0;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(only_info) return "";
			for (i = 1; i < m_max; i++)
			{
				int dist_tmp;
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_YOSHIKA) && (m_ptr->mflag & MFLAG_EPHEMERA) ) cnt++;
			}
			if(cnt)
			{
				msg_format(_("すでに芳香はこのフロアにいる。", "Yoshika is already on this level."),num);
				return NULL;
			}

			(void)summon_named_creature(0, py, px, MON_YOSHIKA, PM_EPHEMERA);


			break;
		}
	case 4:
		{
			monster_type *m_ptr;
			monster_race *r_ptr;

			bool flag_fire = FALSE;
			int i,j;
			int fire_num = p_ptr->lev / 5;
			sides = p_ptr->lev;
			base = adj_general[p_ptr->stat_ind[A_CHR]]+10;
			if(only_info) return format(_("損傷:(%d+1d%d)* %d", "dam: (%d+1d%d) * %d"),base,sides,fire_num);

			for(j=0;j<fire_num;j++)
			{
				int m_idx = 0;
				int cnt = 0;
				for(i=1;i<m_max;i++)
				{
					m_ptr = &m_list[i];
					if (!m_ptr->r_idx) continue;
					if (is_pet(m_ptr)) continue;
					if (!projectable(m_ptr->fy, m_ptr->fx, py, px)) continue;
					r_ptr = &r_info[m_ptr->r_idx];
					if(!monster_living(r_ptr)) continue;

					cnt++;
					if(one_in_(cnt)) m_idx = i;

				}
				if(!m_idx) break;
				else
				{
					flag_fire = TRUE;
					target_who = m_idx;
					m_ptr = &m_list[m_idx];
					target_row = m_ptr->fy;
					target_col = m_ptr->fx;

					fire_ball(GF_NETHER,5,base + randint1(sides),0);
				}

			}
			if(!flag_fire) msg_print(_("近くには標的になる者がいないようだ。",
                                        "You don't see any targets nearby."));
			break;
		}
	case 5:
		{
			if (only_info) return "";
			if (!get_aim_dir(&dir)) return NULL;
			fire_spark(GF_STONE_WALL,dir,0,1);

			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

