cptr do_cmd_class_power_aux_momizi(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			if(only_info) return  format(_str_eff_area,DETECT_RAD_MAP);
			map_area(DETECT_RAD_MAP);
			break;
		}
	case 1:
		{
			if(only_info) return  format(_str_eff_area,DETECT_RAD_DEFAULT);
			detect_all(DETECT_RAD_DEFAULT);
			break;
		}
	case 2:
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage = 50 + plev * 2 + adj_general[p_ptr->stat_ind[A_STR]] * 5;

			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sに牙を突き立てた！", "You thrust your fangs into %s!"),m_name);
				project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_MISSILE,PROJECT_KILL,-1);
				if(!m_ptr->r_idx) break;

				if(!(r_ptr->flagsr & (RFR_IM_POIS | RFR_RES_ALL)))
				{
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_OLD_CONF,PROJECT_KILL,-1);
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_STUN,PROJECT_KILL,-1);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}

	case 3:
		{
			if(only_info) return  format("");
			wiz_lite(FALSE);
			break;
		}
	case 4:
		{
			int rad = 3 + plev / 20;
			int power = plev * 8;
			if( power < 200) power = 200;

			if(only_info) return  format(_("範囲:%d 効力:最大%d", "rad: %d pow: up to %d"),rad,power/2);

			msg_print(_("あなたは強烈な威圧を放った！", "You release an intense aura of intimidation!"));
			project(0,rad,py,px,power,GF_GENOCIDE,(PROJECT_KILL|PROJECT_HIDE),-1);

		}
		break;
	default:
			if(only_info) return  format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




