//p_ptr->magic_num1[0]を「ご先祖様が見ているぞ」用に使う
cptr do_cmd_class_power_aux_koishi(int num, bool only_info)
{
	int dir,sides,base,damage,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
		//キャッチアンドローズ　不可視の電車属性攻撃
	case 0:
		{
			damage = plev * 2;
			if(damage < 20) damage=20;
			if (only_info) return format(_str_eff_dam,damage);

			project_length = 2;
			if (!get_aim_dir(&dir)) return NULL;
			fire_ball_hide(GF_TRAIN, dir, damage, 0);

			break;
		}
		//スティンギングマインド
	case 1:
		{
			int rad = 2 + plev / 30;
			damage = plev + adj_general[p_ptr->stat_ind[A_CHR]] * 3;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			cast_meteor(damage, rad, GF_REDEYE);

			break;
		}

		//ご先祖様が見ているぞ
	case 2:
		{
			base = 30 + plev + adj_general[p_ptr->stat_ind[A_CHR]] * 2;
			if(only_info) return format(_("損傷:%d*2(一回あたり)", "dam: %d*2 (per hit)"),base);
			msg_print(_("周囲に透き通った人影が何体も出現した・・", "Several transparent figures appear around you..."));
			p_ptr->magic_num1[0] = 3;
			break;
		}

		//コンディションドテレポート
	case 3:
		{
			monster_type *m_ptr;
			monster_race *r_ptr;

			int dist = 99;
			int range = 25 + plev / 2;
			int tx,ty;
			if(only_info) return  format(_("距離:%d", "dist: %d"),range);
			for (i = 1; i < m_max; i++)
			{
				int dist_tmp;
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				r_ptr = &r_info[m_ptr->r_idx];
				if (is_pet(m_ptr)) continue;
				if (is_friendly(m_ptr)) continue;
				if(r_ptr->flags2 & RF2_EMPTY_MIND) continue;
				dist_tmp = distance(py,px,m_ptr->fy,m_ptr->fx);
				if(dist_tmp > range) continue;
				if(dist_tmp < dist)
				{
					dist = dist_tmp;
					tx = m_ptr->fx;
					ty = m_ptr->fy;
				}
			}
			if(dist == 99)
			{
				msg_print(_("誰もあなたの近くにいない・・", "There's nobody close enough..."));
			}
			else
			{
				teleport_player_to(ty,tx,0L);
				if(distance(py,px,ty,tx)==1)
				{
					py_attack(ty,tx,HISSATSU_ATTACK_ONCE);
				}
				else
				{
					msg_print(_("失敗！", "Failed!"));
				}
			}

			break;
		}

		//イドの開放
	case 4:
		{
			base = plev + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
			if(only_info) return format(_str_eff_dam_base_1d,base,base);

			msg_format(_("強烈な衝動が開放された！", "You release a powerful shockwave!"));
			project_hack2(GF_REDEYE,1, base,base);
			//v1.1.95
			project_hack2(GF_BERSERK, 1, base, base);

			break;
		}
		//スーパーエゴ
	case 5:
	{
		base = plev + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
		if (only_info) return format(_("効力:%d+1d%d", "pow: %d+1d%d"), base, base);
		msg_format(_("強烈な抑圧が発動された！", "You cause intense supression!"));

		//v1.1.95
		project_hack2(GF_SUPER_EGO, 1, base, base);

		break;
	}


	case 6: //胎児の夢
		{
			int x,y;
			base = chr_adj * 20 + plev * 4 ;
			if(base < 300) base=300;
			if(only_info) return format(_("最大%d", "up to %d"),base);

			if (!get_rep_dir2(&dir)) return NULL;
			y = py + ddy[dir];
			x = px + ddx[dir];
			if(!in_bounds(y,x)) return NULL;

			if (cave[y][x].m_idx)
			{
				int flg = PROJECT_STOP | PROJECT_KILL | PROJECT_JUMP;
				monster_type *m_ptr = &m_list[cave[y][x].m_idx];
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				if(!use_itemcard)
					msg_format(_("あなたは%sに触れ、じっと見つめた・・", "You touch %s, staring intently..."),m_name);

				project(0, 0, y, x, base / 2 , GF_TIME, flg, -1);
				project(0, 0, y, x, base / 2 , GF_COSMIC_HORROR, flg, -1);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;

		}
	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




