cptr do_cmd_class_power_aux_marisa(int num, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];


	switch(num)
	{

	case 0:
		{
			if (only_info) return format("");
			check_marisa_recipe();
			return NULL;//確認のみなので行動順消費しない
			break;
		}
	case 1:
		{
			int spell_num;
			if (only_info) return format("");
			spell_num = choose_marisa_magic(CMM_MODE_CAST);
			if(spell_num < 0) return NULL;



			if(!use_marisa_magic(spell_num,FALSE)) return NULL;
			p_ptr->magic_num1[spell_num + MARISA_HOME_CARRY_SHIFT] -= 1;

			break;
		}

	case 2: //マジックミサイル
		{
			int dice = 3 + (plev / 5);
			int sides = 4 + chr_adj / 12;
			if (only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;
			fire_bolt(GF_MISSILE, dir, damroll(dice, sides));


		}
		break;

	case 3: //キノコ鑑定
		{
			if (only_info) return format("");
			if (!ident_spell_2(1)) return NULL;
		}
		break;
	case 4:
		{
			if(only_info) return "";
			msg_print(_("あなたはコレクターの勘を働かせた・・",
                        "You use your collector's intuition..."));
			detect_objects_normal(DETECT_RAD_DEFAULT);
			break;
		}
	case 5:
		{
			int dam = 20 + plev * 2 + chr_adj * 2;
			if (only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			fire_beam(GF_LITE, dir, dam);
			break;
		}
	case 6:
		{
			int rate = plev + 30;
			if (only_info) return format(_("効率:%d%%", "rate: %d%%"),rate);
			if(!marisa_extract_material(FALSE)) return NULL;

			break;
		}
	case 7:
		{
			if(only_info) return format("");
			if(plev < 40)
			{
				if (!ident_spell(FALSE)) return NULL;
			}
			else
			{
				if (!identify_fully(FALSE)) return NULL;
			}
		}
		break;

	case 8: //マジックナパーム 200-400
		{
			int sides = 100 + chr_adj * 5;
			int base = 50+plev * 4;
			if(only_info) return format(_str_eff_dam_base_1d,base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("特製の薬瓶を投げつけた。", "You throw a special potion bottle."));
			fire_rocket(GF_MANA,dir, base + randint1(sides),3);

			break;
		}

	case 9: //マスパ400-800
		{
			int dice = 15 + p_ptr->lev / 2;
			int sides = 20 + chr_adj / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("マスタースパークを放った！", "You fire a Master Spark!"));
			fire_spark(GF_NUKE,dir, damroll(dice,sides),1);

			break;
		}
	case 10: //着せ替え魔法
		{
			if(only_info) return format("");

			msg_print(_("あなたは煙に包まれた・・", "You are enveloped in smoke..."));
			hack_flag_access_home = TRUE;
			do_cmd_store();
			hack_flag_access_home = FALSE;
			msg_print(_("煙が晴れるとあなたの装いは様変わりしていた！",
                        "The smoke clears, and your outfit looks different!"));

			break;
		}


	default:
		if(only_info) return format("");
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



