cptr do_cmd_class_power_aux_suika(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;

	switch(num)
	{

	case 0:
		{
			int weight = p_ptr->lev * 30;
			if (only_info) return format(_("重量:%d", "wgt: %d"),weight);
			if (!get_aim_dir(&dir)) return NULL;
			fetch(dir, weight, FALSE);
			break;
		}
	case 1:
		{
			int dam;
			dam = p_ptr->chp / 3;
			if(dam<1) dam = 1;
			if(dam > 1600) dam=1600;

			if(only_info) return format(_str_eff_dam,dam);

			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから炎が吐き出された！", "Fire blows out from the card!"));
			else
				msg_print(_("あなたは炎を吐いた！", "You breathe fire!"));

			fire_ball(GF_FIRE, dir, dam, (p_ptr->lev > 35 ? -3 : -2));
			break;
		}
	case 2:
		{
			int time;
			int percentage;
			base = p_ptr->lev/2;
			sides = p_ptr->lev/2;

			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			time = base + randint1(sides);
			percentage = p_ptr->chp * 100 / p_ptr->mhp;
			msg_print(_("体が大きくなった！", "Your body becomes bigger!"));
			set_tim_addstat(A_STR,5,time,FALSE);
			set_tim_addstat(A_CON,5,time,FALSE);
			p_ptr->chp = p_ptr->mhp * percentage / 100;
			p_ptr->redraw |= PR_HP;
			redraw_stuff();
			break;
		}
	case 3:
		{
			int rad = p_ptr->stat_ind[A_STR] / 10;
			dice = p_ptr->lev / 5;
			sides = p_ptr->stat_ind[A_STR]+3;
			base = p_ptr->lev;

			if(rad < 1) rad = 1;
			if(base < 30) base = 30;
			if(dice < 5) dice = 5;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			damage = damroll(dice,sides) + base;
			msg_print(_("あなたは大岩を投げつけた！", "You throw a boulder!"));
			fire_ball(GF_ARROW,dir,damage,rad);

			break;
		}

	case 4:
		{
			int time;
			base = p_ptr->lev/2;
			sides = p_ptr->lev/2;

			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			if(!set_mimic(base+randint1(base), MIMIC_MIST, FALSE)) return NULL;
			break;
		}

	case 5:
        {
            int idx;
            monster_type *m_ptr;
            char m_name[80];
            int power = p_ptr->lev * 8;

            if (only_info) return format(_str_eff_power,power);

            if (!teleport_back(&idx, TELEPORT_FORCE_NEXT)) return NULL;
            m_ptr = &m_list[idx];
            monster_desc(m_name, m_ptr, 0);
            if (distance(py, px, m_ptr->fy, m_ptr->fx) > 1) msg_format(_("%sを引き寄せるのに失敗した。", "You fail to pull %s in."), m_name);
            else
            {
                msg_format(_("%sに鬼縛りの鎖を巻きつけて魔力を散らした！",
                            "%s is wrapped in oni-binding chains that drain magical power!"), m_name);
                project(0, 0, m_ptr->fy, m_ptr->fx, power, GF_DEC_MAG, PROJECT_KILL | PROJECT_JUMP | PROJECT_HIDE, -1);
                project(0, 0, m_ptr->fy, m_ptr->fx, 10, GF_NO_MOVE, PROJECT_KILL | PROJECT_JUMP | PROJECT_HIDE, -1);
            }
        }
        break;


	case 6:
		{
			bool flag = FALSE;
			int i,num;
			if(only_info) return format("");
			num = 5 + randint0(5);
			msg_print(_("あなたは髪を数本抜いて吹いた・・", "You pull out several of your hairs and blow..."));
			for(i=0;i<num;i++) if(summon_named_creature(0, py, px, MON_MINI_SUIKA, PM_EPHEMERA)) flag = TRUE;
			if(!flag) msg_print(_("しかし何も出てこなかった。", "But nobody appeared."));
			break;
		}

	case 7:
		{
			int time;
			int percentage;
			base = p_ptr->lev/2+10;
			sides = 25;

			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			percentage = p_ptr->chp * 100 / p_ptr->mhp;
			set_mimic(base + randint1(sides), MIMIC_GIGANTIC, FALSE);
			p_ptr->chp = p_ptr->mhp * percentage / 100;
			p_ptr->redraw |= PR_HP;
			redraw_stuff();
			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


