cptr do_cmd_class_power_aux_udonge(int num, bool only_info)
{
	int dir,dice,sides,base,damage;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int plev = p_ptr->lev;

	switch(num)
	{
	case 0:
		{
			dice = p_ptr->lev / 5;
			sides = 7 + p_ptr->lev / 10;
			base = p_ptr->lev ;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			damage = damroll(dice,sides) + base;
			if(p_ptr->lev < 30) fire_ball_hide(GF_MIND_BLAST, dir, damage, 0);
			else				fire_ball_hide(GF_BRAIN_SMASH, dir, damage, 0);

			break;
		}
	case 1:
		{
			int rad = p_ptr->lev / 2 + 10;
			if(only_info) return format(_str_eff_area,rad);

			if(use_itemcard)
				msg_format(_("周囲の色々なことが分かった気がする...",
                            "You feel you know more about nearby area..."));
			else
				msg_format(_("周囲の波長を分析した・・", "You analyze ambient wavelength..."));
			detect_doors(rad);
			if(p_ptr->lev > 4) detect_monsters_normal(rad);
			if(p_ptr->lev > 9) detect_traps(rad, TRUE);
			if(p_ptr->lev < 15)detect_stairs(rad);
			else map_area(rad);
			if(p_ptr->lev > 19) detect_objects_normal(rad);
			break;
		}
	case 2:
		{
			dice = p_ptr->lev / 5;
			sides = 5 + p_ptr->lev / 10;
			base = p_ptr->lev + 10;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("あなたは目からビームを発射した！", "You fire a beam from your eyes!"));
			fire_beam(GF_REDEYE, dir, base + damroll(dice,sides));
			break;
		}
	case 3:
		{
			if(only_info) return "";

			msg_format(_("周囲の存在から放たれる波長を読み取った。",
                        "You read wavelengthes emitted by nearby beings."));
			probing();
			break;
		}
	case 4:
		{
			int power = p_ptr->lev * 4;
			if(power < 70) power = 70;
			if(only_info) return format(_str_eff_power,power);

			if(use_itemcard)
				msg_format(_("カードが赤く光った！", "The card glows red!"));
			else
				msg_format(_("あなたの目が赤く光った！", "Your eyes glow red!"));
			stasis_monsters(power);
			break;
		}

	case 5:
		{
			base = p_ptr->lev * 5 + chr_adj * 5;
			if (only_info) return format(_str_eff_power, base);

			if (!get_aim_dir(&dir)) return NULL;

			msg_print(_("呪文を阻害する波動を送った。", "You release a wave interfering with spellcasting."));
			fire_ball_hide(GF_DEC_MAG, dir, base, 0);

			break;
		}
	case 6:
		{
			int rad = 2;
			if(p_ptr->lev > 39) rad = 3;
			dice = p_ptr->lev / 4;
			sides = 10 + p_ptr->lev / 10;
			base = p_ptr->lev * 2;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("巨大な幻影の弾丸を発射した！",
                        "You fire a huge illusionary bullet!"));
			fire_rocket(GF_REDEYE, dir, base + damroll(dice,sides), rad);
			break;
		}
	case 7:
		{
			dice = base = 25;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);

			if(!p_ptr->hero)
			{
                msg_format(_("薬を服用した。", "You take the medicine."));
				set_afraid(0);
				set_hero(randint1(dice) + base, FALSE);
			}
			else if (!p_ptr->shield)
			{
				msg_format(_("薬を服用した。", "You take the medicine."));
				set_shield(randint1(dice) + base, FALSE);
			}
			//else if(!p_ptr->fast) set_fast(randint1(dice) + base, FALSE);
			else if(!p_ptr->tim_addstat_count[A_STR] || !p_ptr->tim_addstat_count[A_DEX] || !p_ptr->tim_addstat_count[A_CON])
			{
				int percentage = p_ptr->chp * 100 / p_ptr->mhp;
				int v = randint1(dice) + base;

				msg_format(_("薬を服用した。", "You take the medicine."));
				msg_format(_("強大な力が湧き出してきた！", "Power flows through you!"));
				set_tim_addstat(A_STR,105,v,FALSE);
				set_tim_addstat(A_DEX,105,v,FALSE);
				set_tim_addstat(A_CON,105,v,FALSE);
				p_ptr->chp = p_ptr->mhp * percentage / 100;
				p_ptr->redraw |= (PR_HP );
				redraw_stuff();
			}
			else
			{
				//int dam = p_ptr->chp * 2 / 3;
				int dam = p_ptr->mhp / 2;
				if(dam<1) dam=1;

				if(p_ptr->pclass == CLASS_EIRIN)
				{
					msg_print(_("...やはりやめておいた。これ以上の服用は危険だ。",
                                "...Better not do it. Taking any more is dangerous."));
					return NULL;
				}

				if (p_ptr->warning && !get_check_strict(_("...嫌な予感がする。本当に飲みますか？",
                                                        "...You have a bad feeling about this. Are you sure you want to drink it? "), CHECK_OKAY_CANCEL))
				{
					return NULL;
				}
				msg_format(_("飲んではいけない量の薬を飲み干した！", "You've drunk too much of the elixir!"));
				msg_format(_("あなたは大爆発した！", "You explode!"));
				project(0, 7, py, px, dam * 5 + randint1(dam*5), GF_MANA, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
				take_hit(DAMAGE_LOSELIFE, dam, _("師匠の薬", "master's elixir"), -1);
				set_hero(0,TRUE);
				set_shield(0,TRUE);
				set_tim_addstat(A_STR,0,0,TRUE);
				set_tim_addstat(A_DEX,0,0,TRUE);
				set_tim_addstat(A_CON,0,0,TRUE);
			}

			break;
		}



	case 8:
		{
			base = p_ptr->lev * 3 + chr_adj * 5;
			if (only_info) return format(_str_eff_power, base);

			if (!get_aim_dir(&dir)) return NULL;

			msg_print(_("狂気をもたらす波動を送った！", "You release a berserk rage inducing wave!"));
			fire_ball_hide(GF_BERSERK, dir, base, 0);

			break;
		}

	case 9:
		{
			dice = base = 15;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);
			msg_format(_("周囲の波長を狂わせた！", "You distort ambient wavelength!"));
			set_tim_superstealth(base + randint1(dice),FALSE, SUPERSTEALTH_TYPE_NORMAL);
			break;
		}
	case 10:
		{
			damage = p_ptr->lev * 5 + adj_general[p_ptr->stat_ind[A_WIS]] * 5 + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("あなたの視界は赤く染まった！", "Everything in your sight gets dyed red!"));
			fire_spark(GF_REDEYE,dir,damage,3);

			break;
		}

	case 11:
		{
			dice = base = 6;
			if(only_info) return format(_str_eff_dur_plus_1d,base,dice);
			if(use_itemcard)
				msg_format(_("自分の存在する位相がずれた気がする...",
                            "You feel yourself out of phase..."));
			else
				msg_format(_("あなたは自分の位相をずらした。",
                            "You move yourself out of phase."));
			set_multishadow(base + randint1(dice),FALSE);
			break;
		}
	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


/*:::てゐ専用技*/
class_power_type class_power_tewi[] =
{

	{10,7,25,FALSE,FALSE,A_DEX,0,0,_("落とし穴", "Trap Hole"),
		_("足元に落とし穴を掘る。誰も見ていない場所でないと実行できない。浮遊していない敵は落とし穴に落ちて気絶、朦朧、行動遅延することがある。",
        "Digs a trap hole in your location. Has to be done in a place where nobody can see you. When a non-flying enemy falls into a trap hole, they might get knocked out, stunned or slowed.")},
	{20,12,25,FALSE,FALSE,A_INT,0,0,_("爆発トラップ", "Explosive Rune"),
		_("敵が通ったら爆発するルーンを床に仕掛ける。",
        "Sets up a rune on the floor that explodes when an enemy crosses it.")},
	{27,20,30,TRUE,FALSE,A_DEX,30,0,_("フラスターエスケープ", "Fluster Escape"),
		_("通常攻撃を行い、その後一瞬で離脱する。離脱に失敗することもある。",
        "Attacks, and then immediately escapes. Escape might fail.") },
	{32,30,65,FALSE,FALSE,A_WIS,0,0,_("大穴牟遅様の薬", "Vulnerary of Ohnamuji-sama"),
		_("神代の止血薬により、HPを回復させて切り傷を治す。",
        "Uses hemostatic medicine to recover HP and cure wounds.")},
	{38,45,70,FALSE,FALSE,A_INT,0,0,_("レーザートラップ", "Laser Trap"),
		_("このフロア限定でレーザーを放つ魔法陣を設置する。",
        "Places a magical circle that fires laser. It cannot leave this floor.")},
	{44,64,80,FALSE,TRUE,A_CHR,0,0,_("エンシェントデューパー", "Ancient Duper"),
		_("視界内のモンスター全てを友好的にする。ただしこの特技の発動に失敗したときフロアのモンスター全てが激怒する。クエストの討伐対象モンスターには効果がない。",
        "Turns all monsters in sight friendly. However, if you fail to activate this ability, all monsters on this floor will be angered. Does not affect quest target monsters.")},
	{99,0,0,FALSE,FALSE,0,0,0,"dummy",	""},
};
cptr do_cmd_class_power_aux_tewi(int num, bool only_info)
{
	int dir,dice,sides,base;
	switch(num)
	{
	case 0:
		{
			int i;
			if(only_info) return "";
			if(!cave_clean_bold(py,px))
			{
				msg_print(_("ここは掘れない。", "You can't dig here."));
				return NULL;
			}

			if (check_player_is_seen())
			{
				msg_print(_("今は掘れない。誰かに見られている。", "You can't dig right now. Someone might notice."));
				return NULL;
			}

			cave_set_feat(py, px, f_tag_to_index_in_init("TEWI_PIT"));
			msg_print(_("落とし穴を掘った！", "You dig up a trap hole!"));
		}
		break;
	case 1:
		{
			if(only_info) return format(_("トラップ威力:%d+7d7", "trap dam: %d+7d7"),p_ptr->lev);
			msg_format(_("足元に罠を仕掛けた。", "You set up a trap."));
			explosive_rune();
			break;
		}
	case 2:
		{
			if(only_info) return "";
			if(!hit_and_away()) return NULL;
			break;
		}

	case 3:
		{
			int heal = (adj_general[p_ptr->stat_ind[A_WIS]]/2 + 15) * (5+p_ptr->lev / 10);
			if(heal < 50) heal = 50;
			if(only_info) return format(_str_eff_heal,heal);
			msg_print(_("ダイコク様の優しさに癒やされる...", "You are healed by the kindness of Daikoku-sama..."));
			hp_player(heal);
			set_cut(0);
			break;
		}
	case 4:
		{
			if(only_info) return format("");

			if(summon_named_creature(0,py,px,MON_SIGN_L,PM_EPHEMERA))
				msg_print(_("トラップを設置した。", "You set up a trap."));
			else
				msg_print(_("設置に失敗した。", "Setting up failed."));

		}
		break;

	case 5:
		{
			int i, count=0;
			monster_type *m_ptr;
			if(only_info) return "";

#ifdef JP
			if(one_in_(4))
				msg_format("あなたは一瀉千里の名口上を披露した！");
			else if(one_in_(3))
				msg_format("あなたは口から出任せをまくし立てた！");
			else if(one_in_(2))
				msg_format("あなたは壮大なハッタリをぶち上げた！");
			else
				msg_format("あなたは一世一代の啖呵を切った！");
#else
            if (one_in_(4))
                msg_format("You fast-talked an enthralling speech!");
            else if (one_in_(3))
                msg_format("You made up a random speech on the fly!");
            else
                msg_format("You made a magnificent bluff!");
#endif

			for (i = m_max - 1; i >= 1; i--)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (r_info[m_ptr->r_idx].flags1 & RF1_QUESTOR) continue;
				if (!projectable(m_ptr->fy,m_ptr->fx,py,px)) continue;
				if (!is_hostile(m_ptr)) continue;
				set_friendly(m_ptr);
				count++;
			}
			if(count)
				msg_format(_("モンスターたちは友好的になった！", "The monsters became friendly!"));
			else
				msg_format(_("...しかし相手がいなかった。", "However, there were no enemies nearby."));

			break;
		}

	default:
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



